INSERT INTO `qandas` (`id`, `type_question`, `knowledge`, `question`, `image`, `answer`, `right_answer`, `created_by`, `updated_by`, `deleted_by`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'text', 'ATNB', 'Giải pháp nào sau đây sẽ làm giảm bức xúc của người bệnh khi tai biến điều trị xảy ra (chọn câu sai):', '', 'A. Tuân thủ nghiêm các quy chế bệnh viện;;;s_answer;;;B. Tuân thủ nghiêm các quy định “những việc cần tránh” trong quá trình điều trị người bệnh;;;s_answer;;;C. Tuân thủ nghiêm quy trình “Những việc cần làm ngay” khi xảy ra tai biến;;;s_answer;;;D. Chủ động hỗ trợ tiền cho người bệnh khi có bức xúc', 'D. Chủ động hỗ trợ tiền cho người bệnh khi có bức xúc', 1, 1, NULL, 1596183132, 1596183132, NULL),
(2, 'text', 'ATNB', 'Thuật ngữ “hồi sức thất bại- failure to rescure” đề cập đến những trường hợp nào:', '', 'A. Những người đáng lẽ được cứu sống nếu nhân viên y tế nhận biết sớm hơn các triệu chứng nặng và cấp cứu kịp thời;;;s_answer;;;B. Người bệnh nặng giai doạn cuối không còn khả năng cứu sống;;;s_answer;;;C. Người bệnh tử vong do sai sót điều trị;;;s_answer;;;D. Người mắc bệnh nan y', 'A. Những người đáng lẽ được cứu sống nếu nhân viên y tế nhận biết sớm hơn các triệu chứng nặng và cấp cứu kịp thời', 1, 1, NULL, 1596183132, 1596183132, NULL),
(3, 'text', 'ATNB', 'Thành phần hệ thống phản ứng nhanh gồm:', '', 'A. Bộ phận báo động;;;s_answer;;;B. Đội code blue;;;s_answer;;;C. Đội cấp cứu nội viên (Rapid response team);;;s_answer;;;D. B và C đúng', 'D. B và C đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(4, 'text', 'ATNB', 'Qui trình báo động đỏ:', '', 'A. Là quy trình cấp cứu người bệnh nguy kịch có sự phối hợp nhiều chuyên khoa;;;s_answer;;;B. Mục tiêu là có được sự phối hợp các chuyên khoa nhằm vừa hồi sức vừa nhanh chóng đưa bệnh nhân vào phòng mổ tiến hành phẫu thuật ngay;;;s_answer;;;C. Dù rất khẩn trương nhưng vẫn tuân thủ các bước chuẩn bị trước khi phẫu thuật như hội chẩn tại giường, đăng ký máu với các xét nghiệm hòa hợp miễn dịch được thực hiện đầy đủ;;;s_answer;;;D. A và B đúng', 'D. A và B đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(5, 'text', 'ATNB', 'Điều nào sau đây đúng khi tổ chức hoạt động phản ứng nhanh tại bệnh viện:', '', 'A. Trong hệ thống này, việc lập đội phản ứng nhanh là bắt buộc và có vai trò quyết định sự thành công;;;s_answer;;;B. Kiến thức và kỹ năng hồi sức cấp cứu của đội phản ứng nhanh cũng như tất cả nhân viên trong bệnh viện có vai trò quan trọng nhất;;;s_answer;;;C. Việc tổ chức hệ thống này phụ thuộc vào nguồn lực và điều kiện cụ thể từng bệnh viện, không nên rập khuôn theo bất kỳ mô hình nào;;;s_answer;;;D. Tất cả đúng', 'D. Tất cả đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(6, 'text', 'ATNB', 'Tại Việt Nam, khảo sát thực trang tiêm an toàn vào năm 2002, 2005, 2008 cho thấy tỉ lệ nào sau đây đúng nhất :', '', 'A. 55% nhân viên y tế chưa cập nhận thông tin về tiêm an toàn ;;;s_answer;;;B. Tỷ lệ người bệnh được kê đơn sử dụng thuốc tiêm đến 55%;;;s_answer;;;C. 55% nhân viên y tế chưa tuân thủ quy trình kỹ thuật ;;;s_answer;;;D. Tất cả đều sai', 'A. 55% nhân viên y tế chưa cập nhận thông tin về tiêm an toàn', 1, 1, NULL, 1596183132, 1596183132, NULL),
(7, 'text', 'ATNB', 'Tổn thương do kim tiêm hay vật sắc nhọn đâm, anh chị xử lý như thế nào?', '', 'A. Rửa tay ngay bằng xà phòng và nước dưới vòi nước chảy;;;s_answer;;;B. Để máu tự chảy không nặn vết thương;;;s_answer;;;C. A và B đúng;;;s_answer;;;D. Xử lý khác', 'C. A và B đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(8, 'text', 'ATNB', 'Các giải pháp chính để tăng cường thực hành TAT, bao gồm:', '', 'A. Giảm thiểu mũi tiêm, thực hành an toàn;;;s_answer;;;B. Trang bị phương tiện thuốc, tiêm;;;s_answer;;;C. Phòng ngừa, xử trí rủi ro do vật săc nhọn , quản lý chất thải y tế ;;;s_answer;;;D. A, B, C đúng', 'D. A, B, C đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(9, 'text', 'ATNB', 'Yếu tố nào sau đây không cần kiểm tra để tránh nguy cơ lầm lẫn về thủ tục hành chính trong quy trình truyền máu lâm sàng:', '', 'A.  Kiểm tra nhóm máu của bệnh nhân, của túi máu;;;s_answer;;;B. Kiểm tra phiếu truyền máu: tên, tuổi, giường bệnh;;;s_answer;;;C. Kiểm tra chất lượng huyết thanh mẫu ;;;s_answer;;;D. Kiểm tra phản ứng chéo tại giường', 'C. Kiểm tra chất lượng huyết thanh mẫu', 1, 1, NULL, 1596183132, 1596183132, NULL),
(10, 'text', 'ATNB', 'Mục đích định nhóm máu tại giường của túi máu và của bệnh nhân nhằm tránh:', '', 'A. Tránh truyền khác nhóm máu cho bệnh nhân;;;s_answer;;;B. Kiểm tra lại nhóm máu của túi máu;;;s_answer;;;C. Kiểm tra lại nhóm máu của người bệnh;;;s_answer;;;D. Để tránh truyền nhầm đơn vị máu cho người bệnh', 'D. Để tránh truyền nhầm đơn vị máu cho người bệnh', 1, 1, NULL, 1596183132, 1596183132, NULL),
(11, 'text', 'ATNB', 'Đảm bảo an toàn truyền máu nhằm mục đích:', '', 'A. Đảm bảo an toàn người cho máu, người nhận máu và nhân viên y tế;;;s_answer;;;B. Chỉ cần đảm bảo an toàn cho người nhận máu;;;s_answer;;;C. Đảm bảo chống phơi nhiễm cho nhân viên y tế là đủ;;;s_answer;;;D. Đảm bảo đúng chỉ định truyền máu', 'A. Đảm bảo an toàn người cho máu, người nhận máu và nhân viên y tế', 1, 1, NULL, 1596183132, 1596183132, NULL),
(12, 'text', 'ATNB', 'Giám sát các vấn đề liên quan đến tính an toàn của thuốc gồm các nhiệm vụ:', '', 'A. Giám sát các phản ứng có hại liên quan đến thuốc hoặc chất lượng thuốc;;;s_answer;;;B. Giám sát các sai sót liên quan đến thuốc ;;;s_answer;;;C. Bảo đảm chất lượng thuốc thông qua việc thực hiện tốt các quy định và mua sắm, bảo quản và cấp phát, đồng thời giám sát và giải quyết các vấn đề về chất lượng thuốc;;;s_answer;;;D. A, B, C đúng', 'D. A, B, C đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(13, 'text', 'ATNB', 'Một số biện pháp ngăn chặn sai sót liên quan đến thuốc, ngoại trừ:', '', 'A. Sai sót liên quan đến thuốc là bất kỳ biến cố có thể phòng tránh nào có khả năng gây ra hoặc dẫn đến việc sử dụng thuốc không hợp lý, hoặc gây hại cho người bệnh trong khi thuốc được kiểm soát bởi nhân viên y tế, người bệnh, hoặc người sử dụng;;;s_answer;;;B. Có thể liên quan đến thực hành chuyên môn, các sản phẩm chăm sóc sức khoẻ, quy trình và hệ thống bao gồm: kê đơn, pha chế, cấp phát, phân phối, giám sát và sử dụng.;;;s_answer;;;C. Sai sót liên quan đến thuốc đã có thể gây hại cho người bệnh hoặc chưa( chưa tính đến việc gây hậu quả bất lợi trên người bệnh). Ví dụ bác sĩ kê đơn liều thuốc chưa phù hợp với hướng dẫn nhưng ngay sau đó Dược sĩ đã thong báo và có sửa lại liều đúng', 'B. Có thể liên quan đến thực hành chuyên môn, các sản phẩm chăm sóc sức khoẻ, quy trình và hệ thống bao gồm: kê đơn, pha chế, cấp phát, phân phối, giám sát và sử dụng.', 1, 1, NULL, 1596183132, 1596183132, NULL),
(14, 'text', 'ATNB', 'Loại nào sau đây là vật tư trang thiết bị y tế ?', '', 'A. Cây đè lưỡi;;;s_answer;;;B. Máy giúp thở;;;s_answer;;;C. Tim nhân tạo;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(15, 'text', 'ATNB', 'Điều dưỡng cài đặt mức độ bơm tiêm là 4ml/h. Điều dưỡng có ấn phím “4” có một lần nhưng máy nhân tín hiệu 2 lần, vì vậy tốc độ máy thực hiện là 44ml/h. Sự cố này được phân loại theo:', '', 'A. Sự cố liên quan đến nhà sản xuất;;;s_answer;;;B. Sự cố liên quan đến quy trình sử dụng;;;s_answer;;;C. Sự cố liên quan đến người sử dụng;;;s_answer;;;D. Sự cố liên quan đến bảo trì trang thiết bị', 'D. Sự cố liên quan đến bảo trì trang thiết bị', 1, 1, NULL, 1596183132, 1596183132, NULL),
(16, 'text', 'ATNB', 'Quy định bệnh viện phải triển khai hoạt động “An toàn người bệnh trong sử dụng trang thiết bị y tế” được ban hành trong:', '', 'A. Luật khám bệnh, chữa bệnh, Luật số 40/2009/QH12;;;s_answer;;;B. Thông tư số 19/2013/TT-BYT về hướng dẫn thực hiện quản lý chất lượng dịch vụ khám bệnh, chữa bệnh tại bệnh viện;;;s_answer;;;C. Khuyến cáo triển khai các hoạt động an toàn người bệnh tại các bệnh viện;;;s_answer;;;D. Chỉ có B và C đúng', 'D. Chỉ có B và C đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(17, 'text', 'ATNB', 'Để tránh rò rỉ hoặc đổ vỡ khi vận chuyển, nghiện phẩm/ bệnh phẩm phải được đóng gói trong vật chứa có bao nhiêu lớp?', '', 'A. 1;;;s_answer;;;B. 2;;;s_answer;;;C. 3;;;s_answer;;;D. 4', 'C. 3', 1, 1, NULL, 1596183132, 1596183132, NULL),
(18, 'text', 'ATNB', 'Nguyên nhân gốc rễ làm mất an toàn và gây ra tai nạn trong xét nghiệm:', '', 'A. Mẫu bệnh phẩm chứa nhiều tác nhân gây bệnh;;;s_answer;;;B. Hành vi mất an toàn;;;s_answer;;;C. Thiếu phương tiện bảo hộ;;;s_answer;;;D. Hóa chất xét nghiệm độc hại', 'B. Hành vi mất an toàn', 1, 1, NULL, 1596183132, 1596183132, NULL),
(19, 'text', 'ATNB', 'Những biện pháp nào là quan trọng nhất cần thực hiện để đảm bảo an toàn xét nghiệm?', '', 'A. Chống nhầm lẫn, đảm bảo an toàn sinh học ;;;s_answer;;;B. Thực hiện an toàn sử dụng thiết bị, an toàn vệ sinh phòng xét nghiệm;;;s_answer;;;C. An toàn trong xử lý nghiện phẩm, an toàn xử lý sự cố ;;;s_answer;;;D. Tất cả biện pháp trên', 'D. Tất cả biện pháp trên', 1, 1, NULL, 1596183132, 1596183132, NULL),
(20, 'text', 'ATNB', 'Trong an toàn sinh học, mục đích chính đánh giá nhóm nguy cơ là gì?', '', 'A. Đưa ra biện pháp kiểm soát phù hợp;;;s_answer;;;B. Đưa ra kế hoạch phòng ngừa sự cố;;;s_answer;;;C. Xác định cấp độ an toàn sinh học phù hợp;;;s_answer;;;D. Thực hiện công tác đánh giá định kỳ', 'B. Đưa ra kế hoạch phòng ngừa sự cố', 1, 1, NULL, 1596183132, 1596183132, NULL),
(21, 'text', 'ATNB', 'Tính cần thiết của việc xây dựng hệ thống quản lý sự cố và rủi ro trong bệnh viện, chọn câu đúng nhất:', '', 'A. Rất cần thiết ;;;s_answer;;;B. Không thể thiếu trong các hoạt động bệnh viện;;;s_answer;;;C. Giúp ngăn chặn các sự cố tiếp tục tái diễn trong tương lai;;;s_answer;;;D. Cả 3 đều đúng', 'D. Cả 3 đều đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(22, 'text', 'ATNB', 'Quản lý sự cố là tìm ra người chịu trách nhiệm sự cố đó để xử phạt kịp thời', '', 'A. Đúng  ;;;s_answer;;;B. Sai', 'B. Sai', 1, 1, NULL, 1596183132, 1596183132, NULL),
(23, 'text', 'ATNB', 'Sự cố là các sai sót chuyên môn trong bệnh viện:', '', 'A. Đúng;;;s_answer;;;B. Sai', 'B. Sai', 1, 1, NULL, 1596183132, 1596183132, NULL),
(24, 'text', 'ATNB', 'Sự cố suýt xảy ra là sự cố chưa xảy ra, nên không cần báo cáo', '', 'A. Đúng;;;s_answer;;;B. Sai', 'B. Sai', 1, 1, NULL, 1596183132, 1596183132, NULL),
(25, 'text', 'ATNB', 'Cần có một chuyên viên chuyên trách quản lý tất cả sự cố xảy ra trong bệnh viện', '', 'A. Đúng;;;s_answer;;;B. Sai', 'A. Đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(26, 'text', 'ATNB', 'Thông thường mỗi vấn đề/sự cố sẽ có một nguyên nhân gốc rễ, hiếm có trường hợp nào có hơn một nguyên nhân gốc rễ', '', 'A. Đúng;;;s_answer;;;B. Sai', 'A. Đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(27, 'text', 'ATNB', 'Khi thành lập nhóm thực hiện phân tích nguyên nhân gốc của sự cố chỉ chọn đối tượng có liên quan đến sự cố đó', '', 'A. Đúng;;;s_answer;;;B. Sai', 'B. Sai', 1, 1, NULL, 1596183132, 1596183132, NULL),
(28, 'text', 'ATNB', 'Bước thông báo kết quả điều tra sự cố, kết quả triển khai giải pháp đến nhân viên bệnh viện là hết sức cần thiết để đảm bảo hoạt động quản lý sự cố và rủi ro tại bệnh viện được hiệu quả', '', 'A. Đúng;;;s_answer;;;B. Sai', 'A. Đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(29, 'text', 'ATNB', 'Nguyên tắc xác định chính xác nguời bệnh nào đúng, NGOẠI TRỪ:', '', 'A. Vòng nhận diện là công cụ phổ biến để nhận diện người bệnh. ;;;s_answer;;;B. Khi dán nhãn lên tuýp bệnh phẩm cần có sự hiện diện của bệnh nhân. Tên và thông tin về nguời bệnh trên các nhãn bệnh phẩm phải bảo đảm dán chặt lên lọ hoặc ống dựng bệnh phẩm truớc, trong và sau khi làm xét nghiệm.;;;s_answer;;;C. Sử dụng ít nhất hai công cụ để nhận dạng bệnh nhân, có thể sử dụng số phòng và số giường để dễ ghi nhớ vị trí bệnh nhân đang nằm điều trị giúp bác sĩ và điều dưỡng thuận lợi trong chăm sóc.;;;s_answer;;;D. Cần có quy định rõ ràng việc tiếp nhận bệnh nhân hôn mê khó xác định nhân thân và buộc mọi nguời phải tuân thủ một cách nhất quán.', 'C. Sử dụng ít nhất hai công cụ để nhận dạng bệnh nhân, có thể sử dụng số phòng và số giường để dễ ghi nhớ vị trí bệnh nhân đang nằm điều trị giúp bác sĩ và điều dưỡng thuận lợi trong chăm sóc.', 1, 1, NULL, 1596183132, 1596183132, NULL),
(30, 'text', 'ATNB', 'Những biện pháp cần thiết để hạn chế nguy cơ té ngã đối với bé sơ sinh, ngoại trừ?', '', 'A. Khi tắm bé, cần chuẩn bị đầy đủ vật dụng cần thiết trước khi tắm trẻ, giữ sàn phòng luôn khô.;;;s_answer;;;B. Quấn bé bằng nhiều khăn để hạn chế rơi bé.;;;s_answer;;;C. Đặt bé trên vị trí an toàn chắc chắn khi tắm, thay tã, luôn dùng 1 tay cố định bé trong khi tay còn lại thao tác: tắm, thay tã cho bé.;;;s_answer;;;D. Hướng dẫn sản phụ và người nhà tư thế cho bú đúng.', 'B. Quấn bé bằng nhiều khăn để hạn chế rơi bé.', 1, 1, NULL, 1596183132, 1596183132, NULL),
(31, 'text', 'ATNB', 'Sản phụ chỉ nên rời khỏi giường sau sinh xong >24 giờ, nếu có người trợ giúp.', '', 'A. Đúng;;;s_answer;;;B. Sai', 'A. Đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(32, 'text', 'ATNB', 'Xác định nguy cơ tương ứng với tình trạng bệnh, NGOẠI TRỪ:', '', 'A. Gắn vòng nguy cơ màu tím đối với bệnh nhân có nguy cơ té ngã;;;s_answer;;;B. Gắn vòng nguy cơ màu đỏ đối với bệnh nhân có tiền sử dị ứng thuốc;;;s_answer;;;C. Gắn vòng nguy cơ màu đỏ đối với mẹ có bệnh lý tiểu đường, mẹ viêm gan đang điều trị, bé dị tật chẻ vòm hầu/ sứt môi;;;s_answer;;;D. Gắn vòng nguy cơ màu tím đối với bệnh nhân có tiền sử dị ứng thuốc', 'B. Gắn vòng nguy cơ màu đỏ đối với bệnh nhân có tiền sử dị ứng thuốc', 1, 1, NULL, 1596183132, 1596183132, NULL),
(33, 'text', 'ATNB', 'Biện pháp nào sau đây giúp tránh lặp lại sự cố:', '', 'A. Mở nhiều kênh tiếp nhận báo cáo sự cố;;;s_answer;;;B. Phản hồi thông tin cho người trực tiếp báo cáo sự cố ;;;s_answer;;;C. Phân tích nguyên nhân gốc rễ của sự cố, đề xuất và thực hiện các giải pháp can thiệp khắc phục sự cố, phản hồi cho các khoa, phòng.;;;s_answer;;;D. Khen thưởng, động viên nhân viên', 'C. Phân tích nguyên nhân gốc rễ của sự cố, đề xuất và thực hiện các giải pháp can thiệp khắc phục sự cố, phản hồi cho các khoa, phòng.', 1, 1, NULL, 1596183132, 1596183132, NULL),
(34, 'text', 'ATNB', 'Xây dựng hệ thống nhắc tránh nhầm lẫn trong sử dụng thuốc, NGOẠI TRỪ:', '', 'A. Thuốc có hình thức đóng gói giống nhau;;;s_answer;;;B. Thuốc được sử dụng thường xuyên;;;s_answer;;;C. Bệnh nhân có tiền sử dị ứng thuốc;;;s_answer;;;D. Thuốc có tên gọi giống nhau', 'B. Thuốc được sử dụng thường xuyên', 1, 1, NULL, 1596183132, 1596183132, NULL),
(35, 'text', 'ATNB', 'Nguyên tắc nào SAI trong tiếp cận lỗi hệ thống:', '', 'A. Không buộc tội, không xử phạt;;;s_answer;;;B. Khuyến khích học từ sai sót của người khác;;;s_answer;;;C. Xác định nguyên nhân gốc rễ của sự cố và giải quyết lỗi hệ thống;;;s_answer;;;D. Tiếp cận ban đầu dựa vào “ai có lỗi”', 'D. Tiếp cận ban đầu dựa vào “ai có lỗi”', 1, 1, NULL, 1596183132, 1596183132, NULL),
(36, 'text', 'ATNB', 'Các hoạt động nào sau đây giúp xây dựng văn hóa an toàn người bệnh của bệnh viện?', '', 'A. Xây dựng và triển khai báo cáo sự cố tự nguyện trong bệnh viện;;;s_answer;;;B. Tập huấn về quản lý chất lượng lâm sàng và an toàn người bệnh;;;s_answer;;;C. Triển khai giám sát và phản hồi;;;s_answer;;;D. Xây dựng kiện toàn hệ thống phác đồ, quy trình, quy định của bệnh viện;;;s_answer;;;E. A, B, C, D đều đúng', 'E. A, B, C, D đều đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(37, 'text', 'ATNB', 'Các yếu tố nào sau đây không phải nguy cơ có thể gây té ngã:', '', 'A. Lớn tuổi >70 tuổi;;;s_answer;;;B. Bệnh nhân tai biến;;;s_answer;;;C. Bệnh nhân bị đái tháo đường;;;s_answer;;;D. Bệnh nhân đi lại khó khăn, dùng gậy, khung chậu lệch', 'C. Bệnh nhân bị đái tháo đường', 1, 1, NULL, 1596183132, 1596183132, NULL),
(38, 'text', 'ATNB', 'Nguy cơ bệnh nhân trốn viện, bỏ con, cho con cần đánh dấu nguy cơ màu gì?', '', 'A. Màu đỏ;;;s_answer;;;B. Màu xanh lá cây;;;s_answer;;;C. Màu tím;;;s_answer;;;D. Màu trắng', 'B. Màu xanh lá cây', 1, 1, NULL, 1596183132, 1596183132, NULL),
(39, 'text', 'ATNB', 'Mục tiêu của việc đeo vòng nhận diện lên tay người bệnh:', '', 'A. Nhằm xác định chính xác người bệnh từ lúc tiếp nhận bệnh, trong suốt quá trình điều trị, chăm sóc;;;s_answer;;;B. Nhằm hạn chế rủi ro cung cấp nhầm dịch vụ cho người bệnh;;;s_answer;;;C. Nhằm hạn chế nguy cơ khi bàn giao, chuyển bệnh;;;s_answer;;;D. A, B, C đúng', 'D. A, B, C đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(40, 'text', 'ATNB', 'Nội dung nào sau đây không góp phần cải thiện an toàn người bệnh?', '', 'A. Chuẩn hóa quy trình bàn giao chuyển bệnh giữa các khoa;;;s_answer;;;B. Trả kết quả cận lâm sàng đúng hẹn, tránh để bệnh nhân than phiền;;;s_answer;;;C. Đánh giá, sàng lọc bệnh nhân ngay từ thời điểm nhập viện điều trị;;;s_answer;;;D. Hạn chế ra y lệnh miệng.', 'B. Trả kết quả cận lâm sàng đúng hẹn, tránh để bệnh nhân than phiền', 1, 1, NULL, 1596183132, 1596183132, NULL),
(41, 'text', 'ATNB', 'Nguyên tắc lựa chọn thuốc thiết yếu, chọn câu sai:', '', 'A. Đảm bảo có hiệu quả, hợp lý, an toàn, ;;;s_answer;;;B. Phải sẵn có với số lượng đầy đủ, có dạng bào chế phù hợp với điều kiện bảo quản, cung ứng, sử dụng, ;;;s_answer;;;C. Phù hợp với mô hình bệnh tật, phương tiện kỹ thuật, trình độ cán bộ chuyên môn của tuyến sử dụng,;;;s_answer;;;D. Đa số là đa chất. Nếu có hai hay nhiều thuốc tương tự nhau phải lựa chọn trên cơ sở đánh giá về hiệu lực, độ an toàn, chất lượng, giá cả, khả năng cung ứng.', 'D. Đa số là đa chất. Nếu có hai hay nhiều thuốc tương tự nhau phải lựa chọn trên cơ sở đánh giá về hiệu lực, độ an toàn, chất lượng, giá cả, khả năng cung ứng.', 1, 1, NULL, 1596183132, 1596183132, NULL),
(42, 'text', 'ATNB', 'Biện pháp giám sát quy trình an toàn chuyển viện sản phụ và sơ sinh nào có thể ứng dụng trong thực tế:', '', 'A. Nhân viên chuyển viện dùng bảng kiểm để tự đánh giá;;;s_answer;;;B. Gửi bảng kiểm cho nơi tiếp nhận đánh giá sau mỗi lần chuyển viện;;;s_answer;;;C. Gởi công văn yêu cầu phản hồi kết quả chuyển viện;;;s_answer;;;D. A, B, C đúng', 'D. A, B, C đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(43, 'text', 'ATNB', 'Quy định màu sắc của vòng nhận diện người bệnh:', '', 'A. Vòng màu hồng dành cho sản phụ; vòng màu xanh dương dành cho người bệnh phụ khoa; vòng màu trắng dành cho sản phụ đeo trước khi chuyển mổ, người bệnh trước khi thực hiện các thủ thuật xâm lấn.;;;s_answer;;;B. Vòng màu hồng dành cho bé gái, vòng màu xanh dương dành cho bé trai.;;;s_answer;;;C. Trên các vòng nhận diện người bệnh có thể được đánh dấu các yếu tố nguy cơ giúp xác định tình trạng người bệnh;;;s_answer;;;D. A, B, C đúng', 'D. A, B, C đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(44, 'text', 'ATNB', 'Cách giám sát tuân thủ của Hộ sinh với quy trình xử trí băng huyết sau sanh nào là chính xác nhất?', '', 'A. Giám sát gián tiếp, kiểm tra số thuốc Ergometrin đã được sử dụng;;;s_answer;;;B. Giám sát số liệu bao đong vạch máu đã được sử dụng;;;s_answer;;;C. Kiểm tra tất cả hồ sơ bệnh án có băng huyết sau sanh;;;s_answer;;;D. Quan sát trực tiếp, theo bảng kiểm', 'D. Quan sát trực tiếp, theo bảng kiểm', 1, 1, NULL, 1596183132, 1596183132, NULL),
(45, 'text', 'ATNB', 'Điều phát biểu nào sau đây SAI khi nói đến vấn đề đảm bảo an toàn người bệnh trong phẫu thuật?', '', 'A. Thực hiện 1 phút dừng trước khi rạch da để kiểm tra, đối chiếu đảm bảo đúng người bệnh, đúng vị trí phẫu thuật và đúng phương pháp phẫu thuật giữa tất cả thành viên tham gia phẫu thuật;;;s_answer;;;B. Phẫu thuật viên phải có trách nhiệm thăm khám lại người bệnh, xác định lại chẩn đoán, phương pháp phẫu thuật và đánh dấu vị trí phẫu thuật; không để người khác thực hiện thay phẫu thuật khi đã được phân công.;;;s_answer;;;C. Nhân viên PT-GMHS không được từ chối đưa người bệnh vào phòng mổ nếu phẫu thuật viên chưa đánh dấu vị trí phẫu thuật;;;s_answer;;;D. Chủ động báo cáo sự cố bắt buộc, tự nguyện, giám sát chủ động trong hoạt động phẫu thuật; rút kinh nghiệm kịp thời các sự cố đã xảy ra.', 'C. Nhân viên PT-GMHS không được từ chối đưa người bệnh vào phòng mổ nếu phẫu thuật viên chưa đánh dấu vị trí phẫu thuật', 1, 1, NULL, 1596183132, 1596183132, NULL),
(46, 'text', 'ATNB', 'Các giải pháp góp phần xây dựng văn hóa an toàn người bệnh tại các bệnh viện, phát biểu nào SAI:', '', 'A. Tạo không khí cở mở cho nhân viên trao đổi về sai sót, lãnh đạo bệnh viện và lãnh đạo khoa, phòng cần tuân thủ nguyên tắc phân tích hệ thống để xác định lỗi hệ thống hay lỗi cá nhân khi có sai sót xảy ra;;;s_answer;;;B. “Hành xử không buộc tội” nghĩa là không đưa ra biện pháp chế tài đối với các lỗi cá nhân.;;;s_answer;;;C. Giám sát chuyên đề an toàn người bệnh nhằm phát hiện lỗi tiềm ẩn, tai biến nhẹ chưa được báo cáo giúp bệnh viện chủ động triển khai các biện pháp cải tiến thiết thực và hiệu quả.;;;s_answer;;;D. Định kỳ rà soát các bộ phận trong một khoa thường xuyên quá tải người bệnh để có kế hoạch hợp lý phân bố nguồn nhân lực hoặc điều phối công việc giữa các khoa là trách nhiệm của Phòng Tổ chức cán bộ và các phòng liên quan.', 'B. “Hành xử không buộc tội” nghĩa là không đưa ra biện pháp chế tài đối với các lỗi cá nhân.', 1, 1, NULL, 1596183132, 1596183132, NULL),
(47, 'text', 'ATNB', 'Sản phụ nhập viện vì thai 39 tuần, tiền sản giật, bị khiếm thị 2 mắt: vậy sản phụ được đeo vòng nhận diện như thế nào?', '', 'A. Vòng màu trắng, gắn vòng nguy cơ màu đỏ ;;;s_answer;;;B. Vòng màu vàng, gắn 2 vòng nguy cơ: đỏ và tím ;;;s_answer;;;C. Vòng màu hồng, gắn 2 vòng nguy cơ: đỏ và tím ;;;s_answer;;;D. Vòng màu hồng, gắn 2 vòng nguy cơ: đỏ và xanh', 'C. Vòng màu hồng, gắn 2 vòng nguy cơ: đỏ và tím', 1, 1, NULL, 1596183132, 1596183132, NULL),
(48, 'text', 'ATNB', 'Bé trai sinh ra có trọng lượng 1000g, lúc thai 32 tuần, trẻ được đeo vòng nhận diện như thế nào:', '', 'A. Vòng màu hồng, có đính vòng nguy cơ màu đỏ;;;s_answer;;;B. Vòng màu vàng, có đính vòng nguy cơ màu đỏ ;;;s_answer;;;C. Vòng màu xanh dương, có đính vòng nguy cơ màu đỏ;;;s_answer;;;D. Tất cả đều sai', 'C. Vòng màu xanh dương, có đính vòng nguy cơ màu đỏ', 1, 1, NULL, 1596183132, 1596183132, NULL),
(49, 'text', 'ATNB', 'Mẹ có bệnh lý tiểu đường thì gắn vòng nguy cơ màu gì?', '', 'A. Vòng màu hồng;;;s_answer;;;B. Vòng màu vàng ;;;s_answer;;;C. Vòng màu đỏ;;;s_answer;;;D. Vòng màu xanh', 'C. Vòng màu đỏ', 1, 1, NULL, 1596183132, 1596183132, NULL),
(50, 'text', 'ATNB', 'Trường hợp người bệnh nằm cách ly, không có thân nhân bên cạnh thì việc xác định đúng người bệnh dựa vào đâu?', '', 'A. Dựa vào vòng đeo tay hoặc Barcode (có chứa ít nhất 2 thông tin nhận dạng người bệnh) trên người bệnh;;;s_answer;;;B. Dựa vào hỏi tên người bệnh;;;s_answer;;;C. Dựa vào hồ sơ bệnh án;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183132, 1596183132, NULL),
(51, 'text', 'ATPT', 'Theo tiêu chí ATPT 2018 để xác định danh tính người bệnh bằng các đặc tính:', '', 'A. Họ và tên, giới tính, năm sinh, mã người bệnh;;;s_answer;;;B. Họ và tên, giới tính, ngày tháng năm sinh, mã người bệnh;;;s_answer;;;C. Họ và tên, giới tính, ngày tháng năm sinh, ngày nhập viện, mã người bệnh;;;s_answer;;;D. Họ và tên, giới tính, ngày tháng năm sinh (hoặc ngày nhập viện nếu không rõ ngày tháng năm sinh), mã người bệnh', 'D. Họ và tên, giới tính, ngày tháng năm sinh (hoặc ngày nhập viện nếu không rõ ngày tháng năm sinh), mã người bệnh', 1, 1, NULL, 1596183144, 1596183144, NULL),
(52, 'text', 'ATPT', 'Nhãn thông tin người bệnh cố định trên người bệnh nhân:', '', 'A. Lắc xanh đối với sản khoa;;;s_answer;;;B. Lắc hồng đối với  phụ khoa;;;s_answer;;;C. Lắc trắng và hồng đới với sản khoa;;;s_answer;;;D. Lắc hồng đối với sản khoa', 'D. Lắc hồng đối với sản khoa', 1, 1, NULL, 1596183144, 1596183144, NULL),
(53, 'text', 'ATPT', 'Bảng thông tin nhận diện người bệnh được ghi trên bảng trong phòng phẫu thuật gồm:', '', 'A. Họ và tên, tuổi, giới tính;;;s_answer;;;B. Họ và tên, tuổi, gạc, chỉ;;;s_answer;;;C. Họ và tên, tuổi, gạc, chỉ, kim, DC vòng trong, DC vòng ngoài;;;s_answer;;;D. Họ và tên, giới tính, ngày tháng năm sinh (hoặc ngày nhập viện nếu không rõ ngày tháng năm sinh), mã người bệnh', 'D. Họ và tên, giới tính, ngày tháng năm sinh (hoặc ngày nhập viện nếu không rõ ngày tháng năm sinh), mã người bệnh', 1, 1, NULL, 1596183144, 1596183144, NULL),
(54, 'text', 'ATPT', 'Nội dung bảng cam kết phẫu thuật theo mẫu của BYT ban hành gồm:', '', 'A. Phẫu thuật viên và người giải thích;;;s_answer;;;B. Phẫu thuật viên và người bệnh trên 18 tuổi;;;s_answer;;;C. Phẫu thuật viên và người bệnh dưới 18 tuổi;;;s_answer;;;D. Phẫu thuật viên và người bệnh trên 18 tuổi có đủ nội dung liên quan đến phẫu thuật, phương pháp phẫu thuật, nguy cơ phẫu thuật và gây mê.', 'D. Phẫu thuật viên và người bệnh trên 18 tuổi có đủ nội dung liên quan đến phẫu thuật, phương pháp phẫu thuật, nguy cơ phẫu thuật và gây mê.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(55, 'text', 'ATPT', 'Kiểm tra trước khi gây mê: người chịu trách nhiệm thực hiện bảng kiểm đọc to thông tin nhận diện người bệnh, với sự xác nhận tối thiểu của:', '', 'A. Bác sĩ gây mê, người bệnh (đối với người bệnh không tỉnh hoặc trẻ em: đối chiếu với thông tin nhận diện gắn trên người bệnh).;;;s_answer;;;B. Bác sĩ gây mê, kỹ thuật viên gây mê.;;;s_answer;;;C. Bác sĩ gây mê, phẫu thuật viên;;;s_answer;;;D. Bác sĩ gây mê, phẫu thuật viên, hộ sinh.', 'A. Bác sĩ gây mê, người bệnh (đối với người bệnh không tỉnh hoặc trẻ em: đối chiếu với thông tin nhận diện gắn trên người bệnh).', 1, 1, NULL, 1596183144, 1596183144, NULL),
(56, 'text', 'ATPT', 'Đánh dấu vị trí phẫu thuật là trách nhiệm của:', '', 'A. Phẫu thuật viên chính đánh dấu trước khi chuyển lên phòng phẫu thuật ;;;s_answer;;;B. Phẫu thuật viên chính đánh dấu tại phòng phẫu thuật ;;;s_answer;;;C. Phẫu thuật viên chính đánh dấu tại phòng tiên mê ;;;s_answer;;;D. Phẫu thuật viên chính đánh dấu tại phòng phẫu thuật', 'A. Phẫu thuật viên chính đánh dấu trước khi chuyển lên phòng phẫu thuật', 1, 1, NULL, 1596183144, 1596183144, NULL),
(57, 'text', 'ATPT', 'Cách đánh dấu vị trí phẫu thuật đúng:', '', 'A. Đánh dấu X vào vị trí phẫu thuật.;;;s_answer;;;B. Đánh dấu chấm vào vị trí phẫu thuật.;;;s_answer;;;C. Chỉ áp dụng đối với phẫu thuật vú.;;;s_answer;;;D. Đánh dấu mũi hướng về vị trí phẫu thuật.', 'D. Đánh dấu mũi hướng về vị trí phẫu thuật.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(58, 'text', 'ATPT', 'Vùng đánh dấu vị trí phẫu thuật được kiểm tra, xác định ít nhất 2 lần sau khi người bệnh đã vào phòng phẫu thuật:', '', 'A. Lần 1: Người phụ trách bảng kiểm ATPT và bác sĩ hoặc KTV gây mê, Lần 2: Cả e-kip xác nhận bằng lời nói trước khi đặt dao mổ.;;;s_answer;;;B. Lần 1: Người phụ trách bảng kiểm ATPT , Lần 2: Cả e-kip xác nhận bằng lời nói trước khi đặt dao mổ.;;;s_answer;;;C. Lần 1: Người phụ trách bảng kiểm ATPT, Lần 2: PTV xác nhận bằng lời nói trước khi đặt dao mổ.;;;s_answer;;;D. Lần 1: KTV gây mê, Lần 2: Cả e-kip xác nhận bằng lời nói trước khi đặt dao mổ.', 'A. Lần 1: Người phụ trách bảng kiểm ATPT và bác sĩ hoặc KTV gây mê, Lần 2: Cả e-kip xác nhận bằng lời nói trước khi đặt dao mổ.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(59, 'text', 'ATPT', 'Bảo đảm có các chức năng tối thiểu dành cho gây mê:', '', 'A. Bảo đảm tối thiểu có 5 thông số theo dõi, bao gồm: Vt, MV, f, Pmax, FiO2;;;;s_answer;;;B. Luôn duy trì chế độ báo động phù hợp; Có hệ thống acqui dự phòng hoạt động tốt;;;;s_answer;;;C. Máy được bật và kiểm tra khả năng hoạt động vào đầu giờ buổi sáng mỗi ngày và ghi vào sổ theo dõi hoạt động của máy;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(60, 'text', 'ATPT', 'Theo tiêu  chí ATPT máy Monitoring có đủ các thông số cơ bản gồm:', '', 'A. SpO2, ECG, huyết áp, nhiệt độ, EtCO2.;;;s_answer;;;B. SpO2, ECG, huyết áp, IPB, EtCO2.;;;s_answer;;;C. SpO2, ECG, huyết áp IPB, EtCO2.;;;s_answer;;;D. SpO2, ECG, huyết áp, IPB, EtCO2.', 'A. SpO2, ECG, huyết áp, nhiệt độ, EtCO2.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(61, 'text', 'ATPT', 'Theo tiêu chí ATPT máy Monitoring có các thông số; hoặc các thông số monitoring trên các máy mê kèm thở. Các thông số này phải được theo dõi liên tục trong suốt quá trình gây mê và ghi chép ít nhất:', '', 'A. 10 phút/1 lần vào phiếu gây mê.;;;s_answer;;;B. 5 phút/1 lần vào phiếu gây mê.;;;s_answer;;;C. Trường hợp đặc biệt cần ghi chép tối thiểu 5 phút/ 1 lần.;;;s_answer;;;D. 10 phút/1 lần vào phiếu gây mê, trường hợp đặc biệt cần ghi chép tối thiểu 5 phút/ 1 lần.', 'D. 10 phút/1 lần vào phiếu gây mê, trường hợp đặc biệt cần ghi chép tối thiểu 5 phút/ 1 lần.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(62, 'text', 'ATPT', 'Trang thiết bị phòng Phẫu thuật cần thiết:', '', 'A. Có sổ theo dõi lý lịch máy; ghi chép đầy đủ kết quả các kỳ bảo dưỡng;;;s_answer;;;B. Có danh mục thông số bảo trì, thay thế định kỳ;;;;s_answer;;;C. Có nhân viên chuyên ngành trang thiết bị của bệnh viện (đối với tuyến TW, tỉnh) trực tiếp phụ trách bảo dưỡng, bảo trì.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(63, 'text', 'ATPT', 'Bảo đảm phòng, ngừa nguy cơ mất nguồn cung cấp điện đột ngột:', '', 'A. Hệ thống điện bảo đảm hoạt động 24/24h;;;;s_answer;;;B. Có phương án ứng phó cụ thể khi có sự cố mất điện (nhân lực, phương tiện, chỉ huy điều hành...);;;;s_answer;;;C. Hệ thống điện dự phòng bảo đảm tự động kích hoạt thay thế không quá 15 giây;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(64, 'text', 'ATPT', 'Bảo đảm chuẩn bị đầy đủ phương tiện kiểm soát đường thở trước khi gây mê, gây tê:', '', 'A. Bộ đặt nội khí quản (NKQ); dụng cụ mở khí quản thông thường;;;s_answer;;;B. Bộ đặt nội khí quản khó có sẵn tại khu mổ; mask thanh quản;;;s_answer;;;C. Bộ đèn soi thanh quản;;;s_answer;;;D. A & B đúng', 'D. A & B đúng', 1, 1, NULL, 1596183144, 1596183144, NULL),
(65, 'text', 'ATPT', 'Đánh giá nguy cơ mất kiểm soát đường thở trước phẫu thuật được ghi trong phiếu khám chuyên khoa (khám tiền mê):', '', 'A. Nguy cơ trào ngược thức ăn;;;;s_answer;;;B. Nguy cơ đặt NKQ khó; tiên lượng nguy cơ đặt NKQ khó.;;;s_answer;;;C. A & B đúng;;;s_answer;;;D. Tiên lượng dị ứng thuốc kháng sinh.', 'C. A & B đúng', 1, 1, NULL, 1596183144, 1596183144, NULL),
(66, 'text', 'ATPT', 'Kiểm tra xác định đã đặt NKQ thành công và ghi vào phiếu theo dõi gây mê dựa theo các tiêu chí:', '', 'A. Hình thán đồ có ít nhất 3 sóng liên tục đều nhau (tiêu chuẩn vàng);;;;s_answer;;;B. Nghe phổi (tại ít nhất 4 vị trí) và dạ dày; ;;;s_answer;;;C. Theo dõi thể tích khí lưu thông (VT) của người bệnh trên máy thở; độ sâu nội khí quản, bơm và kiểm tra độ căng bóng ống NKQ.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(67, 'text', 'ATPT', 'Trong mổ lấy thai, nữ hộ sinh tham gia đọc bảng kiểm trong giai đoạn nào', '', 'A. Vào buồng mổ;;;s_answer;;;B. Trước khi rạch da;;;s_answer;;;C. Trước khi rời khỏi phòng mổ;;;s_answer;;;D. Cả 3 giai đoạn', 'D. Cả 3 giai đoạn', 1, 1, NULL, 1596183144, 1596183144, NULL),
(68, 'text', 'ATPT', 'Đánh giá tình trạng thiếu máu trước phẫu thuật:', '', 'A. Khai thác tiền sử bệnh và xét nghiệm máu (thành phần tế bào máu, Hct, Hb);;;;s_answer;;;B. Khai thác tiền sử bệnh, khám lâm sàng (da, niêm mạc, mạch, huyết áp) và xét nghiệm máu (thành phần tế bào máu, Hct, Hb);;;;s_answer;;;C. Khám lâm sàng (da, niêm mạc, mạch, huyết áp) và xét nghiệm máu (thành phần tế bào máu, Hct, Hb);;;;s_answer;;;D. Khai thác tiền sử bệnh, khám lâm sàng (da, niêm mạc, mạch, huyết áp)', 'B. Khai thác tiền sử bệnh, khám lâm sàng (da, niêm mạc, mạch, huyết áp) và xét nghiệm máu (thành phần tế bào máu, Hct, Hb);', 1, 1, NULL, 1596183144, 1596183144, NULL),
(69, 'text', 'ATPT', 'Theo dõi sát, đánh giá đúng tình trạng mất máu trong phẫu thuật nhằm đưa ra chỉ định truyền máu kịp thời, thích hợp với từng người bệnh:', '', 'A. Đánh giá tốc độ và thể tích máu mất (qua bông gạc, bình hút khăn trải vết mổ v.v..);;;;s_answer;;;B. Đánh giá mức độ cải thiện tình trạng thiếu máu về mặt lâm sàng và xét nghiệm sau khi đã truyền máu để có kế hoạch tiếp theo.;;;s_answer;;;C. Theo dõi các chỉ số sinh tồn cơ bản (mạch, huyết áp, điện tim, SpO2, nước tiểu);;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(70, 'text', 'ATPT', 'Các thuốc sau khi đã được hút sẵn vào ống tiêm phải được dán nhãn thuốc cần xác nhận:', '', 'A. Kiểm tra 5 đúng theo quy định và tốc độ tiêm trước khi sử dụng.;;;s_answer;;;B. Kiểm 3 tra 5 đúng theo quy định và tốc độ tiêm trước khi sử dụng.;;;s_answer;;;C. Kiểm tra 5 đối chiếu theo quy định và tốc độ tiêm trước khi sử dụng.;;;s_answer;;;D. Tất cả đều sai', 'A. Kiểm tra 5 đúng theo quy định và tốc độ tiêm trước khi sử dụng.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(71, 'text', 'ATPT', 'Cơ số thuốc để xử trí cấp cứu sốc phản vệ.', '', 'A. Methylprednisolon 40mg 2 lọ, Diphenhydramin 10mg 5 ống, Adrenalin 1mg/1ml 5 ống, nước cất 10ml 3 ống;;;s_answer;;;B. Methylprednisolon 40mg 2 lọ, Diphenhydramin 10mg 10 ống, Adrenalin 1mg/1ml 10 ống, nước cất 10ml 5 ống;;;s_answer;;;C. Methylprednisolon 40mg 2 lọ, Diphenhydramin 10mg 15 ống, Adrenalin 1mg/1ml 15 ống, nước cất 10ml 5 ống;;;s_answer;;;D. Methylprednisolon 40mg 2 lọ, Diphenhydramin 10mg 15 ống, Adrenalin 1mg/1ml 15 ống, nước cất 10ml 2 ống', 'A. Methylprednisolon 40mg 2 lọ, Diphenhydramin 10mg 5 ống, Adrenalin 1mg/1ml 5 ống, nước cất 10ml 3 ống', 1, 1, NULL, 1596183144, 1596183144, NULL),
(72, 'text', 'ATPT', 'Cơ số thiết bị cần thiết để xử trí cấp cứu sốc phản vệ', '', 'A. Phác đồ, sơ đồ xử trí cấp cứu phản vệ (Phụ lục III, Phụ lục X), bơm kim tiêm vô khuẩn các loại, kim 14 - 16G, bông tiệt trùng tẩm cồn, dây garro.;;;s_answer;;;B. Phác đồ, bơm kim tiêm vô khuẩn các loại, kim 14 – 16 G, bông tiệt trùng tẩm cồn, dây garro.;;;s_answer;;;C. Phác đồ, bơm kim tiêm vô khuẩn các loại, kim 14 – 16 G G, bông tiệt trùng tẩm cồn;;;s_answer;;;D. Câu A & B đúng', 'A. Phác đồ, sơ đồ xử trí cấp cứu phản vệ (Phụ lục III, Phụ lục X), bơm kim tiêm vô khuẩn các loại, kim 14 - 16G, bông tiệt trùng tẩm cồn, dây garro.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(73, 'text', 'ATPT', 'Trang thiết bị y tế và thuốc tối thiểu cấp cứu phản vệ tại cơ sở khám bệnh, chữa bệnh.', '', 'A. Bơm xịt salbutamol.;;;s_answer;;;B. Bộ đặt nội khí quản và/hoặc bộ mở khí quản và/hoặc mask thanh quản.;;;s_answer;;;C. Nhũ dịch Lipid 20% lọ 100ml (02 lọ) đặt trong tủ thuốc cấp cứu tại nơi sử dụng thuốc gây tê, gây   mê.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(74, 'text', 'ATPT', 'Bảng thông tin theo dõi phẫu thuật treo trên tường phòng mổ có nội dung:', '', 'A. Họ và tên BN, ngày tháng năm sinh;;;s_answer;;;B. Gạc, kim, dụng cụ;;;s_answer;;;C. Tên dụng cụ vòng trong, ngoài;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(75, 'text', 'ATPT', 'Nữ hộ sinh xác nhận nội dung gì trong giai đoạn tiền mê (vào buồng mổ)', '', 'A. Đã ghi nhận việc lấy máu cuống rốn chưa?;;;s_answer;;;B. Có cần lấy máu cuống rốn không?;;;s_answer;;;C. Có lo lắng gì về vị trí nhau bám hay không;;;s_answer;;;D. Có cần mời Bs nhi đón bé hay không?', 'D. Có cần mời Bs nhi đón bé hay không?', 1, 1, NULL, 1596183144, 1596183144, NULL),
(76, 'text', 'ATPT', 'Nhóm phẫu thuật phải xác định chính xác người bệnh:', '', 'A. Thông qua vòng nhận diện bệnh nhân;;;s_answer;;;B. Đặt câu hỏi mở cho người bệnh ngay từ khi nhận vào phòng mổ;;;s_answer;;;C. Xác định chính xác thông tin người bệnh phải được thực hiện tại phòng mổ ít nhất hai lần;;;s_answer;;;D. Tất cả câu trên đều đúng', 'D. Tất cả câu trên đều đúng', 1, 1, NULL, 1596183144, 1596183144, NULL),
(77, 'text', 'ATPT', 'Giám sát thực hiện đọc bảng kiểm ATPT', '', 'A. Được thực hiện bởi Điều dưỡng trưởng khoa ngoại hoặc chuyên khoa có bệnh nhân phẫu thuật;;;s_answer;;;B. Dùng bảng kiểm ATPT để giám sát;;;s_answer;;;C. Thống kê và báo cáo số bảng kiểm đã được đánh dấu để biết được số lượng thực hiện;;;s_answer;;;D. Dùng bảng kiểm độc lập nhằm đánh giá sự tuân thủ đọc bảng kiểm của từng đối tượng tham gia phẫu thuật', 'D. Dùng bảng kiểm độc lập nhằm đánh giá sự tuân thủ đọc bảng kiểm của từng đối tượng tham gia phẫu thuật', 1, 1, NULL, 1596183144, 1596183144, NULL),
(78, 'text', 'ATPT', 'Không đánh dấu vị trí phẫu thuật', '', 'A. Nhân viên Phòng mổ có thể từ chối không nhận bệnh;;;s_answer;;;B. Trình lại Phẫu thuật viên để xin ý kiến;;;s_answer;;;C. Trình lại Điều dưỡng trưởng khoa PTGMHS để quyết định có nên tiếp tục phẫu thuật hay không;;;s_answer;;;D. Đánh dấu ngay tại phòng mổ để đảm bảo luôn đánh dấu vị trí phẫu thuật trước mổ', 'A. Nhân viên Phòng mổ có thể từ chối không nhận bệnh', 1, 1, NULL, 1596183144, 1596183144, NULL),
(79, 'text', 'ATPT', 'Bảng kiểm an toàn phẫu thuật là gì?', '', 'A. Là công cụ nhằm giảm nguy cơ xảy ra các biến chứng có thể phòng tránh được quanh giai đoạn phẫu thuật;;;s_answer;;;B. Là danh sách các dụng cụ cần sử dụng trong phẫu thuật;;;s_answer;;;C. Là công cụ nhằm đảm bảo tất cả nhân viên sẵn sàng cho các tình huống khẩn cấp;;;s_answer;;;D. Một tài liệu liệt kê danh sách vai trò của các thành viên tham gia trong phẫu thuật', 'A. Là công cụ nhằm giảm nguy cơ xảy ra các biến chứng có thể phòng tránh được quanh giai đoạn phẫu thuật', 1, 1, NULL, 1596183144, 1596183144, NULL),
(80, 'text', 'ATPT', 'Trong giai đoạn nào NVYT xác nhận danh tính người bệnh?', '', 'A. Trước khi rời khỏi phòng phẫu thuật;;;s_answer;;;B. Tiền mê;;;s_answer;;;C. Trước khi rạch da;;;s_answer;;;D. Đăng kí chuyển mổ', 'B. Tiền mê', 1, 1, NULL, 1596183144, 1596183144, NULL),
(81, 'text', 'ATPT', 'Tại sao rất cần thiết để thảo luận các nguy cơ có thể xảy ra trong cuộc mổ?', '', 'A. Cho phép nhân viên thông báo cho thân nhân người bệnh về những nguy cơ này;;;s_answer;;;B. Cho phép nhân viên chuẩn bị và cảnh báo về những khả năng xảy ra sự cố;;;s_answer;;;C. Cho phép nhân viên chuẩn bị đội ngũ thay thế hoặc ở lại trễ;;;s_answer;;;D. Cho phép nhân viên chuẩn bị kế hoạch chăm sóc bệnh nhân đối phó những nguy cơ xảy ra sau mổ', 'B. Cho phép nhân viên chuẩn bị và cảnh báo về những khả năng xảy ra sự cố', 1, 1, NULL, 1596183144, 1596183144, NULL),
(82, 'text', 'ATPT', 'Nội dung nào được kiểm tra hai lần khi thực hiện bảng kiểm an toàn phẫu thuật?', '', 'A. Cam kết, đồng thuận phẫu thuật;;;s_answer;;;B. Dự trù nguy cơ máu mất;;;s_answer;;;C. Hình ảnh được trình chiếu;;;s_answer;;;D. Kiểm tra số lượng gạc, kim và dụng cụ', 'B. Dự trù nguy cơ máu mất', 1, 1, NULL, 1596183144, 1596183144, NULL),
(83, 'text', 'ATPT', 'Vì sao trước khi phẫu thuật cần phải thảo luận về sự cố nguy hiểm có thể xảy ra?', '', 'A. Giúp nhân viên y tế có thể giải thích nguy cơ cho người nhà bệnh nhân.;;;s_answer;;;B. Giúp nhân viên y tế chuẩn bị và cảnh giác cho những sự cố như vậy có thể xảy ra.;;;s_answer;;;C. Giúp nhân viên y tế chuẩn bị dụng cụ và yêu cầu nhân viên làm thêm giờ;;;s_answer;;;D. Giúp nhân viên y tế lên kế hoạch vệ sinh phòng phẫu thuật sau cuộc mổ', 'B. Giúp nhân viên y tế chuẩn bị và cảnh giác cho những sự cố như vậy có thể xảy ra.', 1, 1, NULL, 1596183144, 1596183144, NULL),
(84, 'text', 'ATPT', 'Theo khuyến cáo của SYT, quy trình báo động đỏ nội viện được kích hoạt khi nào?', '', 'A. Người bệnh được chuyển đến khoa Cấp cứu trong tình trạng nguy kịch;;;s_answer;;;B. Người bệnh được chuyển đến khoa Hồi sức tích cực trong tình trạng nguy kịch;;;s_answer;;;C. Người bệnh được chuyển đến khoa Ngoại trong tình trạng nguy kịch;;;s_answer;;;D. Người bệnh trong tình trạng nguy kịch cần can thiệp điều trị khẩn cấp', 'D. Người bệnh trong tình trạng nguy kịch cần can thiệp điều trị khẩn cấp', 1, 1, NULL, 1596183144, 1596183144, NULL),
(85, 'text', 'ATPT', 'Trong trường hợp đang phẫu thuật, bác sĩ có thể ra y lệnh miệng thì điều dưỡng/kỹ thuật viên gây mê ghi chép vào hồ sơ bệnh án và:', '', 'A. Phải thực hiện y lệnh ngay;;;s_answer;;;B. Phải xác nhận lại với bác sĩ các thông tin trước khi thực hiện;;;s_answer;;;C. Yêu cầu bác sĩ ký tên vào hồ sơ bệnh án trước khi thực hiện;;;s_answer;;;D. Thực hiện y lệnh và đề nghị bác sĩ ký vào hồ sơ ngay sau khi phẫu thuật xong.', 'B. Phải xác nhận lại với bác sĩ các thông tin trước khi thực hiện', 1, 1, NULL, 1596183144, 1596183144, NULL);
INSERT INTO `qandas` (`id`, `type_question`, `knowledge`, `question`, `image`, `answer`, `right_answer`, `created_by`, `updated_by`, `deleted_by`, `created_at`, `updated_at`, `deleted_at`) VALUES
(86, 'text', 'ATPT', 'Để đảm bảo tuân thủ các qui định về thủ tục hành chính mang tính pháp lý trong phẫu thuật, chỉ tiến hành phẫu thuật khi:', '', 'A. Đã xác nhận chính xác người bệnh;;;s_answer;;;B. Đã có giấy cam đoan chấp thuận phẫu thuật;;;s_answer;;;C. Đã đánh dấu vị trí phẫu thuật;;;s_answer;;;D. Đã thực hiện tư vấn đầy đủ cho người bênh/người nhà người bệnh', 'B. Đã có giấy cam đoan chấp thuận phẫu thuật', 1, 1, NULL, 1596183144, 1596183144, NULL),
(87, 'text', 'ATPT', 'Biến chứng nguy hiểm của hạ thân nhiệt', '', 'A. Lạnh run;;;s_answer;;;B. Rối loạn tuần hoàn;;;s_answer;;;C. Rối loạn hô hấp;;;s_answer;;;D. Rối loạn đông máu', 'B. Rối loạn tuần hoàn', 1, 1, NULL, 1596183144, 1596183144, NULL),
(88, 'text', 'ATPT', 'Cách xử lý khi kim tiêm hay vật sắc nhọn đâm', '', 'A. Rửa tay ngay bằng xà phòng và nước dưới vòi nước chảy;;;s_answer;;;B. Để máu tự chảy hoặc nặn vết thương;;;s_answer;;;C. A, B đúng;;;s_answer;;;D. Xử lý khác', 'A. Rửa tay ngay bằng xà phòng và nước dưới vòi nước chảy', 1, 1, NULL, 1596183144, 1596183144, NULL),
(89, 'text', 'ATPT', 'Cấp cứu ngưng hô hấp – tuần hoàn', '', 'A. Chỉ được thực hiện ở cơ sở điều trị;;;s_answer;;;B. Chỉ được thực hiện bởi nhân viên y tế;;;s_answer;;;C. Thực hiện càng sớm càng tốt;;;s_answer;;;D. Mục đích cung cấp oxy cho não', 'C. Thực hiện càng sớm càng tốt', 1, 1, NULL, 1596183144, 1596183144, NULL),
(90, 'text', 'ATPT', 'Trong hồi sinh tim phổi theo khuyến cáo của Hội Tim mạch Hoa Kỳ năm 2015, tỉ lệ nhấn tim – thông khí là', '', 'A. 30:1;;;s_answer;;;B. 30:2;;;s_answer;;;C. 15:1;;;s_answer;;;D. 15:2', 'B. 30:2', 1, 1, NULL, 1596183144, 1596183144, NULL),
(91, 'text', 'ATPT', 'Các thuốc phải có trong hộp chống sốc phản vệ', '', 'A. Adrenaline, Hydrocortisone;;;s_answer;;;B. Adrenaline, Ephedrin;;;s_answer;;;C. Hydrocortisone, Ephedrin;;;s_answer;;;D. Adrenaline, Lidocain', 'A. Adrenaline, Hydrocortisone', 1, 1, NULL, 1596183144, 1596183144, NULL),
(92, 'text', 'ATPT', 'Xử trí nào không đúng khi bệnh nhân có biểu hiện của sốc phản vệ', '', 'A. Ngừng ngay đường đường tiếp xúc với dị nguyên, thuốc đang dùng như tiêm truyền, uống, nhỏ mắt, mũi, bôi da…;;;s_answer;;;B. Để bệnh nhân nằm đầu cao, chân thấp, cổ ngửa;;;s_answer;;;C. Để bệnh nhân nằm đầu thấp chân cao;;;s_answer;;;D. Cởi bỏ bớt các trang phục như dây thắt, quần áo quá chặt.', 'B. Để bệnh nhân nằm đầu cao, chân thấp, cổ ngửa', 1, 1, NULL, 1596183144, 1596183144, NULL),
(93, 'text', 'ATPT', 'Trong hồi sinh tim phổi theo khuyến cáo của Hội Tim mạch Hoa Kỳ năm 2015, tốc độ nhấn tim là', '', 'A. 80-100 lần/phút;;;s_answer;;;B. Khoảng 100 lần/phút;;;s_answer;;;C. 100-120 lần/phút;;;s_answer;;;D. Càng nhanh càng tốt', 'C. 100-120 lần/phút', 1, 1, NULL, 1596183144, 1596183144, NULL),
(94, 'text', 'ATPT', 'Trong hồi sinh tim phổi theo khuyến cáo của Hội Tim mạch Hoa Kỳ năm 2015, biên độ nhấn ngực là', '', 'A. 3 cm;;;s_answer;;;B. 4 cm;;;s_answer;;;C. Ít nhất là 5 cm cho người lớn trung bình;;;s_answer;;;D. 6 cm', 'C. Ít nhất là 5 cm cho người lớn trung bình', 1, 1, NULL, 1596183144, 1596183144, NULL),
(95, 'text', 'ATPT', 'Thời gian ngưng cấp cứu hô hấp - tuần hoàn', '', 'A. 30 phút;;;s_answer;;;B. 45 phút;;;s_answer;;;C. Không xác định;;;s_answer;;;D. Tùy thuộc vào bệnh cảnh lâm sàng của nạn nhân', 'D. Tùy thuộc vào bệnh cảnh lâm sàng của nạn nhân', 1, 1, NULL, 1596183144, 1596183144, NULL),
(96, 'text', 'ATPT', 'Xây dựng hệ thống nhắc để tránh nhầm lẫn trong sử dụng thuốc, NGOẠI TRỪ:', '', 'A. Các thuốc có tên gọi gần giống nhau;;;s_answer;;;B. Các thuốc có hình thức đóng gói gần giống nhau;;;s_answer;;;C. Các thuốc có cùng hoạt chất;;;s_answer;;;D. Các thuốc có nhiều tác dụng phụ', 'D. Các thuốc có nhiều tác dụng phụ', 1, 1, NULL, 1596183144, 1596183144, NULL),
(97, 'text', 'ATPT', 'Biện pháp xử lý môi trường phòng phẫu thuật bị văng bắn, đổ máu, dịch tiết xuống sàn', '', 'A. Đeo găng vô trùng, dùng khăn hoặc giấy thấm máu dịch tiết bỏ vào túi nilon, sau đó đổ dung dịch khử khuẩn vào vết lau, lưu lại 5 đến 10 phút, dùng khăn lau lại theo quy trình làm sạch bề mặt.;;;s_answer;;;B. Để nguyên, sau khi phẫu thuật xong vệ sinh môi trường sẽ lau phòng;;;s_answer;;;C. Dùng tải lau vết máu và dịch tiết;;;s_answer;;;D. Đeo găng sạch, dùng khăn hoặc giấy thấm máu dịch tiết bỏ vào túi nilon màu vàng, sau đó đổ dung dịch khử khuẩn vào vết lau, lưu lại 5 đến 10 phút, dùng khăn lau lại theo quy trình làm sạch bề mặt', 'D. Đeo găng sạch, dùng khăn hoặc giấy thấm máu dịch tiết bỏ vào túi nilon màu vàng, sau đó đổ dung dịch khử khuẩn vào vết lau, lưu lại 5 đến 10 phút, dùng khăn lau lại theo quy trình làm sạch bề mặt', 1, 1, NULL, 1596183144, 1596183144, NULL),
(98, 'text', 'CĐHA', 'Trong phòng chụp X quang, CT, nhân viên bức xạ có được cho phép người nhà người bệnh vào phòng chụp không?', '', 'A. Cho phép, nhưng người nhà bệnh nhân phải tuân thủ các quy định tại phòng chụp.;;;s_answer;;;B. Cho phép, vì sẽ hỗ trợ KTV trong việc chụp chiếu cho NB.;;;s_answer;;;C. Không cho phép, vì sẽ ảnh hưởng đến chất lượng hình ảnh trong quá trình chụp chiếu.;;;s_answer;;;D. Không cho phép, vì ảnh hưởng gây hại do tia X gây ra.', 'D. Không cho phép, vì ảnh hưởng gây hại do tia X gây ra.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(99, 'text', 'CĐHA', 'Sau khi thực hiện siêu âm thăm khám cho người bệnh được cấp cứu có tràn dịch tiết. Để sử dụng máy cho người bệnh tiếp theo,  nhân viên y tế cần thực hiện vệ sinh khử khuẩn đầu dò siêu âm (Tranducer/ Probe) như thế nào:', '', 'A. Sử dụng hóa chất diệt khuẩn để vệ sinh;;;s_answer;;;B. Thực hiện theo hướng dẫn của hãng sản xuất;;;s_answer;;;C. Thực hiện theo hướng dẫn của khoa Kiểm soát nhiễm khuẩn.;;;s_answer;;;D. B, C đúng', 'D. B, C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(100, 'text', 'CĐHA', 'Cần có xem xét đặc biệt khi chỉ định chẩn đoán, điều trị bằng bức xạ ion hóa đối với trẻ em, phụ nữ độ tuổi sinh đẻ, phụ nữ có thai hoặc trong thời gian đang nuôi con dưới 12 tháng tuổi, cụ thể:', '', 'A. Hỏi người bệnh và kiểm tra để chắc chắn họ không đang có thai hoặc không trong thời gian nuôi con dưới 12 tháng tuổi (trong trường hợp chỉ định sử dụng thuốc phóng xạ) trước khi chỉ định chẩn đoán hoặc điều trị.;;;s_answer;;;B. Trường hợp chỉ định lâm sàng là bắt buộc phải thông báo cho nhân viên y tế, nhân viên vận hành thiết bị biết để có biện pháp bảo vệ thích hợp cho người bệnh.;;;s_answer;;;C. Tránh chỉ định dùng bức xạ ion hóa để khám, chữa bệnh cho phụ nữ có thai hoặc nghi có thai hoặc đang trong thời gian đang nuôi con dưới 12 tháng tuổi (trường hợp sử dụng thuốc phóng xạ) trừ khi có chỉ định lâm sàng bắt buộc.;;;s_answer;;;D. A, B, C đúng', 'D. A, B, C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(101, 'text', 'CĐHA', 'Chiếu xạ nghề nghiệp đối với mọi nhân viên bức xạ phải được kiểm soát sao cho:', '', 'A. Liều hiệu dụng toàn thân trong một năm được lấy trung bình trong 03 năm liên tục không được vượt quá 30mSv;;;s_answer;;;B. Liều hiệu dụng toàn thân trong một năm được lấy trung bình trong 04 năm liên tục không được vượt quá 10mSv;;;s_answer;;;C. Liều hiệu dụng toàn thân trong một năm được lấy trung bình trong 05 năm liên tục không được vượt quá 20mSv;;;s_answer;;;D. Liều hiệu dụng toàn thân trong một năm được lấy trung bình trong 05 năm liên tục không được vượt quá 50mSv', 'C. Liều hiệu dụng toàn thân trong một năm được lấy trung bình trong 05 năm liên tục không được vượt quá 20mSv', 1, 1, NULL, 1596183155, 1596183155, NULL),
(102, 'text', 'CĐHA', 'Nhân viên bức xạ y tế nữ khi có thai phải:', '', 'A. Thông báo bằng văn bản cho người phụ trách an toàn, người đứng đầu cơ sở để được bố trí công việc khác không liên quan đến bức xạ.;;;s_answer;;;B. Thông báo bằng văn bản cho người phụ trách an toàn.;;;s_answer;;;C. Thông báo bằng văn bản cho trưởng khoa chẩn đoán hình ảnh.;;;s_answer;;;D. Thông báo bằng văn bản cho hệ thống y tế cơ quan để có báo cáo lãnh đạo sắp xếp.', 'A. Thông báo bằng văn bản cho người phụ trách an toàn, người đứng đầu cơ sở để được bố trí công việc khác không liên quan đến bức xạ.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(103, 'text', 'CĐHA', 'Khi buộc phải có người hỗ trợ người bệnh vào khu vực chiếu, chụp X quang, nhân viên vận hành thiết bị X quang phải:', '', 'A. Có biện pháp kiểm soát để chỉ cho phép nhân viên bức xạ y tế cần thiết và những người được chỉ định giúp đỡ người bệnh được ở trong phòng khám, điều trị người bệnh trong thời gian chiếu xạ.;;;s_answer;;;B. Hướng dẫn người hỗ trợ người bệnh trang bị các bảo hộ như tạp dề cao su chì...;;;s_answer;;;C. A, B đúng;;;s_answer;;;D. A, B sai', 'C. A, B đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(104, 'text', 'CĐHA', 'Khi sử dụng xong thiết bị y tế chẩn đoán như : siêu âm, điện tim; kỹ thuật viên hoặc điều dưỡng thu hổi các dây dẫn tín hiệu bằng biện pháp:', '', 'A. Xếp gập lại;;;s_answer;;;B. Cuộn tròn và neo cố định hoặc treo, máng lên giá đỡ;;;s_answer;;;C. A, B đều sai;;;s_answer;;;D. A, B đều đúng', 'D. A, B đều đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(105, 'text', 'CĐHA', 'Nhân viên vận hành thiết bị bức xạ không được phép thực hiện công việc gì dưới đây khi thực hiện nhiệm vụ:', '', 'A. Kiểm tra an toàn thiết bị trước khi bắt đầu và sau khi kết thúc công việc.;;;s_answer;;;B. Đóng cửa ra vào trong suốt quá trình vận hành thiết bị.;;;s_answer;;;C. Cho phép nhiều người bệnh vào phòng chiếu, chụp cùng một lúc.;;;s_answer;;;D. Tuân thủ quy trình vận hành thiết bị.', 'C. Cho phép nhiều người bệnh vào phòng chiếu, chụp cùng một lúc.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(106, 'text', 'CĐHA', 'Các nhân viên y tế, nhân viên vận hành thiết bị bức xạ có trách nhiệm bảo đảm an toàn bức xạ cho người bệnh khi thực hiện công việc của mình như sau:', '', 'A. Bảo đảm mọi sự chiếu xạ y tế chỉ được thực hiện khi có chỉ định của bác sỹ điều trị và được thực hiện theo đúng chỉ định của bác sỹ điều trị;;;;s_answer;;;B. Bảo đảm thiết bị được dùng là thích hợp.;;;s_answer;;;C. A, B sai;;;s_answer;;;D. A, B đúng', 'D. A, B đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(107, 'text', 'CĐHA', 'Nhân viên vận hành thiết bị X - quang chẩn đoán y tế ngoài các yêu cầu trách nhiệm chung về bảo vệ người bệnh quy định, không được thực hiện biện pháp nào:', '', 'A. Sử dụng cơ cấu khu trú chùm tia một cách phù hợp để chỉ tạo ra liều chiếu tối thiểu trên người bệnh nhưng vẫn có chất lượng ảnh phù hợp với yêu cầu cho chẩn đoán trong chiếu chụp X – quang.;;;s_answer;;;B. Chiếu xạ vào các bộ phận cơ thể nhạy cảm phóng xạ của người bệnh như tuyến sinh dục, thủy tinh thể mắt, vú, tuyến giáp trạng và phải sử dụng các biện pháp che chắn thích hợp cho người bệnh khi không thể tránh được;;;s_answer;;;C. Tránh chiếu, chụp vùng bụng và vùng hố chậu của phụ nữ có thai hoặc nghi có thai trừ khi có lý do lâm sàng bắt buộc và cần áp dụng các biện pháp để hạn chế đến mức nhỏ nhất liều gây ra cho thai nhi khi phải làm như vậy.;;;s_answer;;;D. Không được cho người bệnh và người không có phận sự đứng chờ trong phòng chiếu, chụp X - quang khi đang thực hiện chiếu, chụp cho người bệnh khác.', 'B. Chiếu xạ vào các bộ phận cơ thể nhạy cảm phóng xạ của người bệnh như tuyến sinh dục, thủy tinh thể mắt, vú, tuyến giáp trạng và phải sử dụng các biện pháp che chắn thích hợp cho người bệnh khi không thể tránh được', 1, 1, NULL, 1596183155, 1596183155, NULL),
(108, 'text', 'CĐHA', 'Việc kiểm tra, kiểm định thiết bị bức xạ phải được thực hiện theo:', '', 'A. Quy trình do nhà sản xuất cung cấp.;;;s_answer;;;B. Tiêu chuẩn quốc gia tương ứng.;;;s_answer;;;C. Quy trình do Chủ cơ sở bức xạ xây dựng.;;;s_answer;;;D. A và B đúng', 'D. A và B đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(109, 'text', 'CĐHA', 'Việc kiểm định, hiệu chuẩn thiết bị bức xạ và thiết bị đo bức xạ phải được thực hiện bởi:', '', 'A. Kỹ sư bảo trì của cơ sở bức xạ.;;;s_answer;;;B. Kỹ sư bảo trì của tổ chức, cá nhân kinh doanh trang thiết bị y tế;;;s_answer;;;C. Tổ chức, cá nhân được Bộ Khoa học và Công nghệ cấp đăng ký hoạt động dịch vụ kiểm định, hiệu chuẩn.;;;s_answer;;;D. A, B, C đúng', 'C. Tổ chức, cá nhân được Bộ Khoa học và Công nghệ cấp đăng ký hoạt động dịch vụ kiểm định, hiệu chuẩn.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(110, 'text', 'CĐHA', 'Cơ sở y tế phải thực hiện việc kiểm định đối với thiết bị X - quang chẩn đoán trong y tế được sử dụng tại cơ sở mình như sau:', '', 'A. Định kỳ một năm một lần;;;s_answer;;;B. Định kỳ hai năm một lần ;;;s_answer;;;C. Định kỳ hai năm một lần kể từ ngày đưa vào sử dụng  ;;;s_answer;;;D. Định kỳ ba năm một lần', 'C. Định kỳ hai năm một lần kể từ ngày đưa vào sử dụng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(111, 'text', 'CĐHA', 'Nếu kết quả kiểm định cho thấy các thông số vận hành liên quan đến an toàn của thiết bị bức xạ lệch khỏi giá trị cho phép, biện pháp xử lý:', '', 'A. Tiếp tục sử dụng thiết bị để kịp thời phục vụ chẩn đoán cho người bệnh đồng thời hiệu chuẩn lại thiết bị.;;;s_answer;;;B. Liên hệ với kỹ sư bảo trì để hiệu chuẩn. ;;;s_answer;;;C. Thiết bị phải hiệu chuẩn mới được cho phép tiếp tục sử dụng.;;;s_answer;;;D. A, B đúng', 'C. Thiết bị phải hiệu chuẩn mới được cho phép tiếp tục sử dụng.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(112, 'text', 'CĐHA', 'Thời gian chà tay với hóa chất vệ sinh tay theo quy trình 6 bước phải đạt từ:', '', 'A. 15 giây - 20 giây;;;s_answer;;;B. 20 giây - 30 giây;;;s_answer;;;C. 30 giây - 40 giây;;;s_answer;;;D. 40 giây - 60 giây', 'B. 20 giây - 30 giây', 1, 1, NULL, 1596183155, 1596183155, NULL),
(113, 'text', 'CĐHA', 'Điền từ thích hợp vào vị trí trống sau đây “Quy trình rửa tay thường tay gồm ….  và thời gian tối thiểu của mỗi bước là …”', '', 'A. 5 bước; 10 giây;;;s_answer;;;B. 5 bước; 30 giây;;;s_answer;;;C. 6 bước; 10 giây;;;s_answer;;;D. 6 bước; 30 giây', 'D. 6 bước; 30 giây', 1, 1, NULL, 1596183155, 1596183155, NULL),
(114, 'text', 'CĐHA', 'Khi tiếp nhận hồ sơ siêu âm, điều dưỡng tiếp nhận hồ sơ cần phải làm gì để không nhầm hồ sơ của người khác:', '', 'A. Đọc rõ họ, tên, tuổi bệnh nhân.;;;s_answer;;;B. Đọc kỹ giới tính.;;;s_answer;;;C. Đọc kỹ chỉ định của bác sĩ.;;;s_answer;;;D. A, B và C đúng', 'D. A, B và C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(115, 'text', 'CĐHA', 'Điều dưỡng tiếp nhận hồ sơ trước khi đưa vào phòng cho bác sĩ siêu âm phải làm gì?', '', 'A. Xem kỹ họ, tên , tuổi, giới tính, địa chỉ.;;;s_answer;;;B. Xem kỹ chỉ định của bác sĩ yêu cầu siêu âm phần nào.;;;s_answer;;;C. Khi trả kết quả cho bệnh nhân phải đọc phần mô tả để tránh nhầm lẫn giới tính.;;;s_answer;;;D. A, B đúng', 'D. A, B đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(116, 'text', 'CĐHA', 'Để tránh trường hợp bác sĩ siêu âm có thể siêu âm nhầm bên phải hoặc bên trái, điều dưỡng phụ bác sĩ phải:', '', 'A. Đọc kỹ chỉ định cho bác sĩ trước khi siêu âm. ;;;s_answer;;;B. Hỏi cẩn thận tình hình bệnh tật của bệnh nhân. ;;;s_answer;;;C. Hợp tác với bác sĩ siêu âm để tránh trường hợp sai sót. ;;;s_answer;;;D. A, B và C đúng', 'D. A, B và C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(117, 'text', 'CĐHA', 'Trường hợp thực hiện siêu âm sai chỉ định của bác sĩ , hay siêu âm không đúng với phần yêu cầu siêu âm của bệnh nhân, điều dưỡng phòng siêu âm phải cố gắng:', '', 'A. Xin lỗi bệnh nhân sau khi đã khắc phục sự cố.;;;s_answer;;;B. Giúp đỡ bệnh nhân có kết quả một cách sớm nhất.;;;s_answer;;;C. Trả bệnh nhân về lại phòng khám;;;s_answer;;;D. A, B đúng', 'D. A, B đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(118, 'text', 'CĐHA', 'Với bệnh nhân sau khi được siêu âm xong, điều dưỡng phụ trách trong phòng siêu âm phải kiểm tra cẩn thận :', '', 'A. Tên, tuổi bệnh nhân.;;;s_answer;;;B. Địa chỉ, giới tính;;;s_answer;;;C. Kết quả phải phù hợp với yêu cầu của bác sĩ cho chỉ định;;;s_answer;;;D. Cả 3 câu trên', 'D. Cả 3 câu trên', 1, 1, NULL, 1596183155, 1596183155, NULL),
(119, 'text', 'CĐHA', 'Sau khi siêu âm xong, điều dưỡng phòng siêu âm phải giúp bác sĩ vệ sinh máy siêu âm:', '', 'A. Lau đầu dò siêu âm;;;s_answer;;;B. Dây nối đầu dò phải giữ cẩn thận tránh gẫy gập;;;s_answer;;;C. Phải vệ sinh máy siêu âm sau buổi làm việc;;;s_answer;;;D. A, B và C đúng', 'D. A, B và C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(120, 'text', 'CĐHA', 'Phòng siêu âm phải có kế hoạch vệ sinh cho máy siêu âm hằng ngày:', '', 'A. Trước khi bác sĩ ngồi vào máy;;;s_answer;;;B. Sau khi bác sĩ siêu âm xong;;;s_answer;;;C. Bọc đầu dò cẩn thận sau buổi khám;;;s_answer;;;D. A, B và C đúng', 'D. A, B và C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(121, 'text', 'CĐHA', 'Khi máy siêu âm có sự cố, điều dưỡng phòng siêu âm phải mời ngay:', '', 'A. Người phụ trách ban kỹ thuật;;;s_answer;;;B. Tắt máy siêu âm để tránh bị hư hỏng thêm.;;;s_answer;;;C. Giải thích và giúp bệnh nhân được thuận tiện cho việc khám sớm nhất (nếu có ). ;;;s_answer;;;D. A, B và C đúng', 'A. Người phụ trách ban kỹ thuật', 1, 1, NULL, 1596183155, 1596183155, NULL),
(122, 'text', 'CĐHA', 'Với những chất thải, dịch tiết ở phòng siêu âm, điều dưỡng phòng xử lý như thế nào:', '', 'A. Mang găng tay phân loại chất thải.;;;s_answer;;;B. Bỏ chất thải vào thùng rác đúng qui định.;;;s_answer;;;C. Bỏ kim tiêm vào thùng rác y tế.;;;s_answer;;;D. A, B và C đúng', 'B. Bỏ chất thải vào thùng rác đúng qui định.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(123, 'text', 'CĐHA', 'Giới hạn liều tương đương đối với nhân viên bức xạ theo khuyến cáo của (ICRP) ?', '', 'A. 5mSv/năm;;;s_answer;;;B. 10mSv/năm;;;s_answer;;;C. 15mSv/năm;;;s_answer;;;D. 20mSv/năm', 'D. 20mSv/năm', 1, 1, NULL, 1596183155, 1596183155, NULL),
(124, 'text', 'CĐHA', 'Giới hạn liều tương đương đối với nhân dân theo khuyến cáo của (ICRP) ?', '', 'A. 1mSv/năm;;;s_answer;;;B. 2mSv/năm;;;s_answer;;;C. 3mSv/năm;;;s_answer;;;D. 4mSv/năm', 'A. 1mSv/năm', 1, 1, NULL, 1596183155, 1596183155, NULL),
(125, 'text', 'CĐHA', 'Phải kiểm soát liều chiếu xạ nghề nghiệp đối với mọi nhân viên bức xạ như thế nào?', '', 'A. Liều hiệu dụng toàn thân trong một năm được lấy trung bình trong 5 năm liên tục không được vượt quá 20mSv.;;;s_answer;;;B. Liều hiệu dụng toàn thân trong một năm riêng lẻ bất kỳ không được vượt quá 50mSv.;;;s_answer;;;C. Liều tương đương trong một năm ;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(126, 'text', 'CĐHA', 'Giới hạn liều nghề nghiệp / năm đối với nhân viên bức xạ trên 18 tuổi (liều hiệu dụng):', '', 'A. >20mSv/năm;;;s_answer;;;B. 20mSv/năm;;;s_answer;;;C. 10mSv/năm;;;s_answer;;;D. không giới hạn', 'B. 20mSv/năm', 1, 1, NULL, 1596183155, 1596183155, NULL),
(127, 'text', 'CĐHA', 'Giới hạn liều công chúng / năm (liều hiệu dụng):', '', 'A. 1mSv/ năm;;;s_answer;;;B. 2mSv/năm;;;s_answer;;;C. 3mSv/năm;;;s_answer;;;D. 4mSv/năm', 'A. 1mSv/ năm', 1, 1, NULL, 1596183155, 1596183155, NULL),
(128, 'text', 'CĐHA', 'Liều kế cá nhân:', '', 'A. Chỉ đeo khi có kiểm tra;;;s_answer;;;B. Nhân viên bức xạ phải đeo thường xuyên trong lúc làm việc;;;s_answer;;;C. Dùng để đo liều hấp thụ cho từng cá nhân;;;s_answer;;;D. B và C đúng', 'D. B và C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(129, 'text', 'CĐHA', 'Liều cá nhân được trang bị định kỳ:', '', 'A. 1 tháng / lần;;;s_answer;;;B. 2 tháng / lần;;;s_answer;;;C. 3 tháng/ lần;;;s_answer;;;D. 4 tháng/ lần', 'C. 3 tháng/ lần', 1, 1, NULL, 1596183155, 1596183155, NULL),
(130, 'text', 'CĐHA', 'Dụng cụ bảo vệ và đảm bảo an toàn bức xạ gồm:', '', 'A. Áo chì;;;s_answer;;;B. Yếm chì (đeo cổ);;;s_answer;;;C. Kính bảo vệ mắt, găng tay cao su chì;;;s_answer;;;D. Tất cả đúng', 'D. Tất cả đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(131, 'text', 'CĐHA', 'Để hạn chế tình trạng phản ứng thuốc, trước khi tiêm thuốc cản quang nhân viên y tế cần :', '', 'A. Kiểm tra dấu sinh hiệu;;;s_answer;;;B. Hỏi tiền sử dị ứng, xét nghiệm chức năng gan – thận ;;;s_answer;;;C. Có hộp thuốc chống sốc;;;s_answer;;;D. Tất cả đúng', 'B. Hỏi tiền sử dị ứng, xét nghiệm chức năng gan – thận', 1, 1, NULL, 1596183155, 1596183155, NULL),
(132, 'text', 'CĐHA', 'Chống chỉ định của dùng thuốc cản quang trong khảo sát cắt lớp vi tính:', '', 'A. Tiền sử dị ứng thuốc cản quang;;;s_answer;;;B. Xơ gan;;;s_answer;;;C. Cường giáp;;;s_answer;;;D. A và C đúng', 'D. A và C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(133, 'text', 'CĐHA', 'Thiết bị X - quang di động phải có cáp nối đủ dài để bảo đảm khoảng cách giữa người vận hành thiết bị và bóng phát:', '', 'A. Tối thiểu 1,5 m. ;;;s_answer;;;B. Tối thiểu 2 m.;;;s_answer;;;C. Tối thiểu 2,5 m. ;;;s_answer;;;D. Tối thiểu 3 m.', 'B. Tối thiểu 2 m.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(134, 'text', 'CĐHA', 'Phòng đặt thiết bị chiếu, chụp X quang tổng hợp có bàn người bệnh yêu cầu diện tích lắp đặt tối thiểu là:', '', 'A. 18 m2, kích thước tối thiểu một chiều: 4 m;;;s_answer;;;B. 14 m2, kích thước tối thiểu một chiều: 4 m;;;s_answer;;;C. 20 m2, kích thước tối thiểu một chiều: 3,5 m ;;;s_answer;;;D. 14 m2 , kích thước tối thiểu một chiều: 3,5 m', 'D. 14 m2 , kích thước tối thiểu một chiều: 3,5 m', 1, 1, NULL, 1596183155, 1596183155, NULL),
(135, 'text', 'CĐHA', 'Cơ sở y tế phải thực hiện việc kiểm định đối với các thiết bị bức xạ được sử dụng tại cơ sở mình như sau:', '', 'A. Trước khi đưa vào sử dụng lần đầu tiên.;;;s_answer;;;B. Định kỳ một năm một lần đối với thiết bị xạ trị, thiết bị chụp cắt lớp vi tính CT scanner, thiết bị X - quang tăng sáng truyền hình và định kỳ hai năm một lần đối với các thiết bị X - quang chẩn đoán trong y tế khác kể từ ngày đưa vào sử dụng; ;;;s_answer;;;C. Sau khi lắp đặt lại hoặc sửa chữa thiết bị.;;;s_answer;;;D. A và B đúng', 'D. A và B đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(136, 'text', 'CĐHA', 'Mục đích của việc Cơ sở y tế phải thực hiện việc kiểm định đối với các thiết bị bức xạ được sử dụng tại cơ sở mình để:', '', 'A. Bảo đảm an toàn sức khỏe cho người sử dụng thiết bị bức xạ.;;;s_answer;;;B. Bảo đảm an toàn sức khỏe cho người bệnh;;;s_answer;;;C. Bảo đảm an toàn sức khỏe cho người sử dụng thiết bị bức xạ, người bệnh.;;;s_answer;;;D. Bảo đảm an toàn sức khỏe cho người sử dụng thiết bị bức xạ, người bệnh và môi trường xung quanh.', 'D. Bảo đảm an toàn sức khỏe cho người sử dụng thiết bị bức xạ, người bệnh và môi trường xung quanh.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(137, 'text', 'CĐHA', 'Các dấu hiệu, đèn cảnh báo tại phòng thiết bị bức xạ, gồm: Phía trên cửa ra vào khu vực kiểm soát đặt một biển cảnh báo bức xạ theo mẫu quy định. Đèn báo hiệu tại các cửa ra vào phòng đặt thiết bị bức xạ, phòng cấy nguồn xạ trị áp sát, phòng pha chế thuốc phóng xạ và phải bảo đảm đèn báo hiệu phát sáng trong suốt thời gian đang tiến hành công việc bức xạ. Nhằm để cảnh báo có nguy cơ nhiễm xạ cho:', '', 'A. Người bệnh.;;;s_answer;;;B. Người bệnh đang chờ đợi vào chiếu, chụp X quang;;;s_answer;;;C. Người bệnh và Người bệnh đang chờ đợi vào chiếu, chụp X quang;;;s_answer;;;D. Tất cả mọi người bên ngoài phòng đặt thiết bị bức xạ không bước vào khu vực đặt thiết bị bức xạ đang trong quá trình chụp/ chiếu', 'D. Tất cả mọi người bên ngoài phòng đặt thiết bị bức xạ không bước vào khu vực đặt thiết bị bức xạ đang trong quá trình chụp/ chiếu', 1, 1, NULL, 1596183155, 1596183155, NULL),
(138, 'text', 'CĐHA', 'Nhân viên vận hành thiết bị bức xạ phải thực hiện nghiêm các bước tiến hành công việc:', '', 'A. Kiểm tra an toàn thiết bị trước khi bắt đầu và sau khi kết thúc công việc; đóng cửa ra vào trong suốt quá trình vận hành thiết bị; tuân thủ quy trình vận hành thiết bị; chú ý những tín hiệu bất thường của thiết bị bức xạ và thiết bị kiểm tra để kịp thời phát hiện sự cố, ngăn ngừa tai nạn;;;s_answer;;;B. Kiểm tra an toàn thiết bị trước khi bắt đầu và sau khi kết thúc công việc, đóng cửa ra vào trong suốt quá trình vận hành thiết bị;;;;s_answer;;;C. Tuân thủ quy trình vận hành thiết bị; ;;;s_answer;;;D. B, C đúng', 'A. Kiểm tra an toàn thiết bị trước khi bắt đầu và sau khi kết thúc công việc; đóng cửa ra vào trong suốt quá trình vận hành thiết bị; tuân thủ quy trình vận hành thiết bị; chú ý những tín hiệu bất thường của thiết bị bức xạ và thiết bị kiểm tra để kịp thờ', 1, 1, NULL, 1596183155, 1596183155, NULL),
(139, 'text', 'CĐHA', 'Cơ sở y tế có người học nghề, học viên, sinh viên thực tập vận hành các thiết bị bức xạ, làm việc với các nguồn phóng xạ, chăm sóc người bệnh được điều trị bằng nguồn phóng xạ hoặc thuốc phóng xạ, làm việc trong khu vực tiềm ẩn nguy cơ bị chiếu xạ với mức liều lớn hơn 1 mSv/năm hoặc trong khu vực có nguy cơ bị nhiễm bẩn phóng xạ có trách nhiệm:', '', 'A. Tổ chức huấn luyện cho các đối tượng nêu trên các yêu cầu cơ bản về an toàn bức xạ, các nội quy, quy định an toàn bức xạ của cơ sở.;;;s_answer;;;B. Tổ chức huấn luyện cho các đối tượng nêu trên các yêu cầu cơ bản về an toàn bức xạ, các nội quy, quy định an toàn bức xạ của cơ sở; bố trí cán bộ chuyên môn hướng dẫn, theo dõi, giám sát trong quá trình làm việc.;;;s_answer;;;C. Áp dụng các biện pháp kiểm soát liều để bảo đảm liều chiếu xạ của các đối tượng này không vượt quá giới hạn liều theo quy định.;;;s_answer;;;D. B và C đúng', 'D. B và C đúng', 1, 1, NULL, 1596183155, 1596183155, NULL),
(140, 'text', 'CĐHA', 'Kim loại thường được dùng trong dụng cụ chuyên dụng đảm bảo an toàn bức xạ là?', '', 'A. Sắt.;;;s_answer;;;B. Đồng.;;;s_answer;;;C. Vàng.;;;s_answer;;;D. Chì', 'D. Chì', 1, 1, NULL, 1596183155, 1596183155, NULL),
(141, 'text', 'CĐHA', 'Ảnh hưởng của tia X trong thai kỳ có thể gây ra:', '', 'A. Thai chết lưu.;;;s_answer;;;B. Thai dị tật.;;;s_answer;;;C. Sanh non.;;;s_answer;;;D. Tất cả đều đúng', 'B. Thai dị tật.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(142, 'text', 'CĐHA', 'Cách xử trí khi bị tổn thương do kim tiêm hay vật sắc nhọn', '', 'A. Nặn, bóp vết thương cho chảy nhanh hết máu nghi bị nhiễm rồi rửa tay bằng nước sạch ;;;s_answer;;;B. Để máu chảy tự nhiên, rửa ngay dưới vòi nước chảy sạch ;;;s_answer;;;C. Để máu chảy tự nhiên 5 phút rồi rửa vết thương bằng dung dịch sát khuẩn ;;;s_answer;;;D. Lập biên bản báo cáo sự cố để lãnh thuốc uống dự phòng', 'B. Để máu chảy tự nhiên, rửa ngay dưới vòi nước chảy sạch', 1, 1, NULL, 1596183155, 1596183155, NULL),
(143, 'text', 'CĐHA', 'Theo anh/chị thời gian điều trị dự phòng phơi nhiễm HIV tốt nhất khi nào?', '', 'A. Ngay sau khi xác đinh rõ tình trạng HIV của nguồn phơi nhiễm dương tính;;;s_answer;;;B. Trong 2 – 3 giờ đầu tiên;;;s_answer;;;C. Sau 72 giờ;;;s_answer;;;D. Sau 7 ngày', 'A. Ngay sau khi xác đinh rõ tình trạng HIV của nguồn phơi nhiễm dương tính', 1, 1, NULL, 1596183155, 1596183155, NULL),
(144, 'text', 'CĐHA', 'Dụng cụ được đề nghị mới mang vào phòng mổ:', '', 'A. Máy gây mê;;;s_answer;;;B. Máy chụp X Quang;;;s_answer;;;C. Máy hút;;;s_answer;;;D. Tủ đựng thuốc', 'B. Máy chụp X Quang', 1, 1, NULL, 1596183155, 1596183155, NULL),
(145, 'text', 'CĐHA', 'Biện pháp phòng ngừa dây điện cực gãy, NGOẠI TRỪ', '', 'A. Mắc dây tròn không để gấp gãy;;;s_answer;;;B. Không treo dây khi còn nối với kẹp;;;s_answer;;;C. Thay mới;;;s_answer;;;D. Bảo quản cẩn thận', 'C. Thay mới', 1, 1, NULL, 1596183155, 1596183155, NULL),
(146, 'text', 'CĐHA', 'Khi khoa bị cúp điện, để đề phòng cháy máy do nguồn điện không ổn định cần phải:', '', 'A. Không phải xử trí gì, tự máy điều chỉnh điện theo nguồn.;;;s_answer;;;B. Tắt máy và giữ nguyên phích cắm ở ổ cắm khi cúp điện.;;;s_answer;;;C. Không bao giờ cắm điện vào ổ trừ lúc pin sạc hết điện.;;;s_answer;;;D. Rút điện ra khỏi ổ cắm điện khi cúp điện.', 'D. Rút điện ra khỏi ổ cắm điện khi cúp điện.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(147, 'text', 'CĐHA', 'Khi máy bị ướt do dịch chảy vào máy cần:', '', 'A. Lau khô máy, gởi bảo trì sửa chữa nếu nước chảy vào bên trong.;;;s_answer;;;B. Phòng ngừa bằng cách để máy nơi khô ráo và không để dịch chảy lên máy.;;;s_answer;;;C. Không cần làm gì do máy có khả năng chống nước.;;;s_answer;;;D. A và B đúng.', 'A. Lau khô máy, gởi bảo trì sửa chữa nếu nước chảy vào bên trong.', 1, 1, NULL, 1596183155, 1596183155, NULL),
(148, 'text', 'ĐDCB', 'Vết thương nhiễm trùng khi có các dấu hiệu:', '', 'A. Sốt;;;s_answer;;;B. Vết thương sưng, nóng, đỏ, đau;;;s_answer;;;C. Vết thương có dịch tiết hay mủ, có mùi hôi;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183163, 1596183163, NULL),
(149, 'text', 'ĐDCB', 'Hệ thống dẫn lưu nước tiểu cần phải:', '', 'A. Sạch sẽ, để cách sàn nhà;;;s_answer;;;B. Phải 1 chiều, kín;;;s_answer;;;C. Vật chứa thấp hơn bàng quang 60 cm;;;s_answer;;;D. B, C đúng', 'D. B, C đúng', 1, 1, NULL, 1596183163, 1596183163, NULL),
(150, 'text', 'ĐDCB', 'Kỹ thuật đật thông tiểu lưu, Đầu ống thông tiểu đặt vào lỗ tiểu sâu khoàng 4 – 5 cm. Khi thấy nước tiểu chảy ra ngoài, đẩy ống vào sâu …….cm để chắc chắn ống vào hẳn trong bàng quang.', '', 'A. 3 – 4 cm;;;s_answer;;;B. 3 – 5 cm ;;;s_answer;;;C. 3 – 6 cm;;;s_answer;;;D. 3 – 7 cm', 'B. 3 – 5 cm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(151, 'text', 'ĐDCB', 'Một điều dưỡng thành thật khai báo khi có sai phạm thể hiện điều dưỡng', '', 'A. Có tinh thần trách nhiệm ;;;s_answer;;;B. Hiểu rõ y lệnh;;;s_answer;;;C. Có kiến thức về thuốc;;;s_answer;;;D. Tác phong làm việc khoa học, chính xác', 'A. Có tinh thần trách nhiệm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(152, 'text', 'ĐDCB', 'Điều dưỡng A chuẩn bị tiêm thuốc kháng sinh cho người bệnh và người bệnh khai đã từng dị ứng với thuốc này, điều dưỡng phải', '', 'A. Ngưng tiêm thuốc và ghi chú vào hồ sơ cho tua trực sau;;;s_answer;;;B. Tiến hành test lẩy da thử phản ứng thuốc và đọc kết quả;;;s_answer;;;C. Tiến hành tiêm thuốc vì đã pha thuốc và bác sĩ đã ghi rõ chỉ định;;;s_answer;;;D. Hỏi rõ lại về tiền sử dị ứng thuốc, hoãn tiêm thuốc và báo bác sĩ ngay', 'D. Hỏi rõ lại về tiền sử dị ứng thuốc, hoãn tiêm thuốc và báo bác sĩ ngay', 1, 1, NULL, 1596183163, 1596183163, NULL),
(153, 'text', 'ĐDCB', 'Trong tiêm an toàn, để đảm bảo nguyên tắc không gây nguy hại cho người nhận mũi tiêm, điều dưỡng', '', 'A. Không dùng tay để đậy nắp kim sau tiêm;;;s_answer;;;B. Không tiêm nhiều lần vào một vị trí trên cùng một người bệnh ;;;s_answer;;;C. Bỏ kim tiêm vào hộp kháng thủng ngay sau khi tiêm;;;s_answer;;;D. Không tháo rời kim tiêm ra khỏi bơm tiêm sau khi tiêm', 'B. Không tiêm nhiều lần vào một vị trí trên cùng một người bệnh', 1, 1, NULL, 1596183163, 1596183163, NULL),
(154, 'text', 'ĐDCB', 'Trong tiêm an toàn, để đảm bảo nguyên tắc không gây nguy hại cho người tiêm, điều dưỡng', '', 'A. Bảo đảm đúng kỹ thuật vô khuẩn;;;s_answer;;;B. Không tiêm nhiều lần vào một vị trí trên cùng một người bệnh;;;s_answer;;;C. Bỏ kim tiêm vào hộp kháng thủng ngay sau khi tiêm;;;s_answer;;;D. Không tháo rời kim tiêm ra khỏi bơm tiêm sau khi tiêm', 'D. Không tháo rời kim tiêm ra khỏi bơm tiêm sau khi tiêm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(155, 'text', 'ĐDCB', 'Trong tiêm an toàn, để đảm bảo nguyên tắc không gây nguy hại cho cộng đồng, điều dưỡng', '', 'A. Bảo đảm đúng kỹ thuật vô khuẩn;;;s_answer;;;B. Không tiêm nhiều lần vào một vị trí trên cùng một người bệnh;;;s_answer;;;C. Bỏ kim tiêm vào hộp kháng thủng ngay sau khi tiêm ;;;s_answer;;;D. Không tháo rời kim tiêm ra khỏi bơm tiêm sau khi tiêm', 'C. Bỏ kim tiêm vào hộp kháng thủng ngay sau khi tiêm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(156, 'text', 'ĐDCB', 'Cỡ kim dùng để tiêm bắp là', '', 'A. 21-23G, dài 2.5-4 cm ;;;s_answer;;;B. 23-25 G, dài 1.5-2.5 cm;;;s_answer;;;C. 19-21 G, dài 2.5-4cm;;;s_answer;;;D. 26-27 G, dài 0.6 - 1.3cm', 'A. 21-23G, dài 2.5-4 cm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(157, 'text', 'ĐDCB', 'Cỡ kim dùng để tiêm tĩnh mạch là', '', 'A. 21-23G, dài 2.5-4 cm;;;s_answer;;;B. 23-25 G, dài 1.5-2.5 cm;;;s_answer;;;C. 19-21 G, dài 2.5-4cm ;;;s_answer;;;D. 26-27 G, dài 0.6 - 1.3cm', 'C. 19-21 G, dài 2.5-4cm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(158, 'text', 'ĐDCB', 'Cỡ kim dùng để tiêm dưới da là', '', 'A. 21-23G, dài 2.5-4 cm;;;s_answer;;;B. 23-25 G, dài 1.5-2.5 cm ;;;s_answer;;;C. 19-21 G, dài 2.5-4cm;;;s_answer;;;D. 26-27 G, dài 0.6 - 1.3cm', 'B. 23-25 G, dài 1.5-2.5 cm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(159, 'text', 'ĐDCB', 'Cỡ kim dùng để tiêm trong da là', '', 'A. 21-23G, dài 2.5-4 cm;;;s_answer;;;B. 23-25 G, dài 1.5-2.5 cm ;;;s_answer;;;C. 19-21 G, dài 2.5-4cm;;;s_answer;;;D. 26-27 G, dài 0.6 - 1.3cm', 'D. 26-27 G, dài 0.6 - 1.3cm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(160, 'text', 'ĐDCB', 'Lượng thuốc tiêm tối đa ở vùng cơ rộng ngoài đùi người lớn là:', '', 'A. 1 ml;;;s_answer;;;B. 2 ml;;;s_answer;;;C. 3 ml;;;s_answer;;;D. 5 ml', 'D. 5 ml', 1, 1, NULL, 1596183163, 1596183163, NULL),
(161, 'text', 'ĐDCB', 'Vị trí tiêm bắp ở vùng đùi là:', '', 'A. 1/3 giữa mặt trước đùi;;;s_answer;;;B. 1/3 giữa mặt ngoài đùi;;;s_answer;;;C. 1/3 giữa mặt trước - ngoài đùi ;;;s_answer;;;D. 1/3 trên mặt trước - ngoài đùi', 'C. 1/3 giữa mặt trước - ngoài đùi', 1, 1, NULL, 1596183163, 1596183163, NULL),
(162, 'text', 'ĐDCB', 'Khi cho người bệnh dùng thuốc uống đắng, điều dưỡng nên', '', 'A. Cho người bệnh uống sau khi ăn no;;;s_answer;;;B. Cho người bệnh ngậm nước đá và uống qua ống hút ;;;s_answer;;;C. Pha thuốc với đường;;;s_answer;;;D. Pha thuốc với mật ong', 'B. Cho người bệnh ngậm nước đá và uống qua ống hút', 1, 1, NULL, 1596183163, 1596183163, NULL),
(163, 'text', 'ĐDCB', 'Thuốc uống có thể làm tổn thương niêm mạc dạ dày, nên cho người bệnh dùng', '', 'A. Sau khi ăn no ;;;s_answer;;;B. Trước khi ăn để thuốc hấp thu tốt;;;s_answer;;;C. Bất kỳ lúc nào;;;s_answer;;;D. Ngay khi ngủ dậy', 'A. Sau khi ăn no', 1, 1, NULL, 1596183163, 1596183163, NULL),
(164, 'text', 'ĐDCB', 'Thuốc lợi tiểu nên uống vào lúc', '', 'A. Chiều tối trước khi đi ngủ;;;s_answer;;;B. Trước 15g ;;;s_answer;;;C. Sau khi ăn no;;;s_answer;;;D. Chuẩn bị bữa ăn', 'B. Trước 15g', 1, 1, NULL, 1596183163, 1596183163, NULL),
(165, 'text', 'ĐDCB', 'Lượng thuốc tiêm tối đa ở vùng cơ mông trên người lớn là:', '', 'A. 1,5 ml;;;s_answer;;;B. 2 ml;;;s_answer;;;C. 3 ml ;;;s_answer;;;D. 5 ml', 'C. 3 ml', 1, 1, NULL, 1596183163, 1596183163, NULL),
(166, 'text', 'ĐDCB', 'Lượng thuốc tiêm tối đa ở cơ Delta ở người lớn là', '', 'A. 0,5 ml;;;s_answer;;;B. 1 ml ;;;s_answer;;;C. 1,5 ml;;;s_answer;;;D. 2 ml', 'B. 1 ml', 1, 1, NULL, 1596183163, 1596183163, NULL),
(167, 'text', 'ĐDCB', 'Mục đích của tiêm trong da là:', '', 'A. Chủng ngừa, thử phản ứng thuốc ;;;s_answer;;;B. Tiêm thuốc vào lớp mô liên kết;;;s_answer;;;C. Tiêm thuốc vào lớp dưới thượng bì;;;s_answer;;;D. Tiêm thuốc vào vùng da trắng, ít va chạm', 'A. Chủng ngừa, thử phản ứng thuốc', 1, 1, NULL, 1596183163, 1596183163, NULL),
(168, 'text', 'ĐDCB', 'Đường tiêm để thực hiện tiêm một loại thuốc cần có tác dụng kéo dài là:', '', 'A. Tiêm bắp;;;s_answer;;;B. Tiêm tĩnh mạch;;;s_answer;;;C. Tiêm trong da;;;s_answer;;;D. Tiêm dưới da', 'D. Tiêm dưới da', 1, 1, NULL, 1596183163, 1596183163, NULL),
(169, 'text', 'ĐDCB', 'Theo khuyến cáo của Tổ Chức Y Tế Thế Giới, thực hiện mũi tiêm an toàn cần đúng những điều gì?', '', 'A. Tên người bệnh, nhãn thuốc, đường dùng thuốc, liều lượng, thời điểm, thiết bị, hình thức và cách bảo quản.;;;s_answer;;;B. Tên người bệnh, số giường, đường dùng thuốc, liều lượng, thời điểm, thiết bị, hình thức, cách bảo quản và phương pháp hủy chất thải;;;s_answer;;;C. Người bệnh, thuốc, đường dùng, liều lượng, thời điểm, thiết bị, hình thức, cách bảo quản và phương pháp hủy chất thải ;;;s_answer;;;D. Người bệnh, chất lượng thuốc, đường dùng, liều, thời gian, thiết bị, hình thức, cách bảo quản và phương pháp hủy chất thải', 'C. Người bệnh, thuốc, đường dùng, liều lượng, thời điểm, thiết bị, hình thức, cách bảo quản và phương pháp hủy chất thải', 1, 1, NULL, 1596183163, 1596183163, NULL),
(170, 'text', 'ĐDCB', 'Để thực hiện tiêm an toàn cho bản thân, nhiệm vụ quan trọng nhất của người tiêm là', '', 'A. Tham gia đầy đủ các chương trình đào tạo về tiêm an toàn;;;s_answer;;;B. Thực hiện đúng quy trình tiêm an toàn;;;s_answer;;;C. Thực hiện phân loại, thu gom chất thải sắc nhọn đúng quy định ;;;s_answer;;;D. Tuân thủ đúng quy trình tiêm, xử trí đúng và báo cáo khi xảy ra phơi nhiễm', 'D. Tuân thủ đúng quy trình tiêm, xử trí đúng và báo cáo khi xảy ra phơi nhiễm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(171, 'text', 'ĐDCB', 'Tiêm dưới da là đưa thuốc vào', '', 'A. Cơ vân;;;s_answer;;;B. Cơ trơn;;;s_answer;;;C. Lớp thượng bì;;;s_answer;;;D. Mô liên kết', 'D. Mô liên kết', 1, 1, NULL, 1596183163, 1596183163, NULL),
(172, 'text', 'ĐDCB', 'Phương pháp tiêm bắp Z tract nhằm mục đích', '', 'A. Giảm đau khi tiêm và giảm nguy cơ abces;;;s_answer;;;B. Phòng ngừa viêm cơ do thuốc;;;s_answer;;;C. Giảm nguy cơ dò thuốc vào lớp thượng bì khi rút kim;;;s_answer;;;D. Hạn chế thuốc rò rỉ vào mô dưới da và giảm đau khi tiêm', 'D. Hạn chế thuốc rò rỉ vào mô dưới da và giảm đau khi tiêm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(173, 'text', 'ĐDCB', 'Dự phòng tai biến shock phản vệ khi tiêm thuốc cho người bệnh', '', 'A. Tìm hiểu tiền sử dị ứng thuốc ;;;s_answer;;;B. Thử phản ứng thuốc trước khi tiêm nếu cần ;;;s_answer;;;C. Kiểm soát chỉ định thuốc trước khi tiêm ;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183163, 1596183163, NULL),
(174, 'text', 'ĐDCB', 'Nguyên nhân cơ bản nhất dẫn đến hành vi thiếu an toàn trong tiêm là', '', 'A. Thiếu phương tiện rửa tay/sát khuẩn tay ;;;s_answer;;;B. Thiếu ý thức tuân thủ quy trình tiêm an toàn của cán bộ y tế ;;;s_answer;;;C. Tình trạng quá tải người bệnh, quá tải công việc ;;;s_answer;;;D. Thiếu dụng cụ tiêm phù hợp với yêu cầu sử dụng', 'B. Thiếu ý thức tuân thủ quy trình tiêm an toàn của cán bộ y tế', 1, 1, NULL, 1596183163, 1596183163, NULL),
(175, 'text', 'ĐDCB', 'Điều dưỡng không đọc rõ y lệnh thuốc điều trị, trong khi đó bác sĩ đang rất bận và không thích làm phiền. Điều dưỡng nên', '', 'A. Vẫn sao phiếu thuốc theo suy diễn của điều dưỡng và thực hiện y lệnh;;;s_answer;;;B. Không thực hiện y lệnh thuốc đó;;;s_answer;;;C. Đợi bác sĩ có mặt và hỏi lại y lệnh thuốc;;;s_answer;;;D. Chủ động liên hệ bác sĩ và hỏi lại y lệnh thuốc', 'D. Chủ động liên hệ bác sĩ và hỏi lại y lệnh thuốc', 1, 1, NULL, 1596183163, 1596183163, NULL),
(176, 'text', 'ĐDCB', 'Điều dưỡng chuẩn bị tiêm một loại thuốc kháng sinh cho người bệnh tại giường, người bệnh hỏi thuốc gì và tác dụng của thuốc. Điều dưỡng cần đưa thông tin gì cho người bệnh:', '', 'A. Tên thuốc và tác dụng mong muốn của thuốc ;;;s_answer;;;B. Liệt kê các thuốc sử dụng trong ngày;;;s_answer;;;C. Liều thuốc sử dụng trong ngày và đường dùng;;;s_answer;;;D. Tên thuốc và đường dùng thuốc', 'A. Tên thuốc và tác dụng mong muốn của thuốc', 1, 1, NULL, 1596183163, 1596183163, NULL),
(177, 'text', 'ĐDCB', 'Khi điều dưỡng chuẩn bị tiêm insulin dưới da điều trị tiểu đường cho một nữ bệnh nhân 56 tuổi, bệnh nhân nói rằng “Sao ống chích hôm nay nhiều thuốc vậy? Thường ngày tôi được tiêm bằng ½ lượng thuốc này”. Điều dưỡng nên phản ứng thế nào?', '', 'A. Bảo người bệnh hàm lượng thuốc hôm nay giảm ½ nên liều tăng gấp đôi. ;;;s_answer;;;B. Phát các thuốc uống còn lại khác và không tiêm insulin cho người bệnh. ;;;s_answer;;;C. Xem lại y lệnh, kiểm tra lại hàm lượng, liều dùng thuốc và đo đường huyết cho người bệnh;;;s_answer;;;D. "Cho rằng người bệnh lớn tuổi, dễ quên nên vẫn tiếp tục bảo người bệnh nằm yên để được tiêm thuốc. "', 'C. Xem lại y lệnh, kiểm tra lại hàm lượng, liều dùng thuốc và đo đường huyết cho người bệnh', 1, 1, NULL, 1596183163, 1596183163, NULL),
(178, 'text', 'ĐDCB', 'Khi tiêm truyền tĩnh mạch, dấu hiệu nhận biết bệnh nhân bị biến chứng viêm tĩnh mạch do tính chất dịch truyền là', '', 'A. Đỏ, nóng và đau vị trí tiêm, sốt, bạch cầu tăng;;;s_answer;;;B. Nóng rát, đau, sờ mạch thấy cứng ;;;s_answer;;;C. Đỏ và đau vị trí tiêm, sốt, bạch cầu tăng;;;s_answer;;;D. Nóng và đau vị trí tiêm, sốt, bạch cầu tăng', 'B. Nóng rát, đau, sờ mạch thấy cứng', 1, 1, NULL, 1596183163, 1596183163, NULL),
(179, 'text', 'ĐDCB', 'Góc độ tiêm so với mặt da trong tiêm truyền tĩnh mạch là:', '', 'A. 10 – 15 độ;;;s_answer;;;B. 30 độ;;;s_answer;;;C. 45 độ;;;s_answer;;;D. Tùy tĩnh mạch nông hay sâu của người bệnh', 'D. Tùy tĩnh mạch nông hay sâu của người bệnh', 1, 1, NULL, 1596183163, 1596183163, NULL),
(180, 'text', 'ĐDCB', 'Một người bệnh có kim luồn lưu tại cánh tay trái được 48 giờ để tiêm truyền thuốc kháng sinh. Điều dưỡng phát hiện vùng da tại vị trí tiêm bị đỏ và khi sờ người bệnh than đau. Điều dưỡng đã tháo kim luồn và tiêm vị trí khác. Lý do điều dưỡng làm như vậy là gì?', '', 'A. Sự than phiền của người bệnh;;;s_answer;;;B. Tĩnh mạch tại vị trí tiêm bị viêm ;;;s_answer;;;C. Thời gian lưu kim quá lâu;;;s_answer;;;D. Hết mục đích điều trị', 'B. Tĩnh mạch tại vị trí tiêm bị viêm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(181, 'text', 'ĐDCB', 'Nguy cơ mất an toàn nào sau đây xảy ra do vô khuẩn không đúng?', '', 'A. Nhầm lẫn thuốc;;;s_answer;;;B. Gãy kim;;;s_answer;;;C. Áp xe nóng ;;;s_answer;;;D. Tắc mạch', 'C. Áp xe nóng', 1, 1, NULL, 1596183163, 1596183163, NULL);
INSERT INTO `qandas` (`id`, `type_question`, `knowledge`, `question`, `image`, `answer`, `right_answer`, `created_by`, `updated_by`, `deleted_by`, `created_at`, `updated_at`, `deleted_at`) VALUES
(182, 'text', 'ĐDCB', 'Đối với người bình thường, lượng nước tiểu khoảng:', '', 'A. 30ml/ giờ;;;s_answer;;;B. 15ml/ giờ;;;s_answer;;;C. 500ml/ 3 giờ;;;s_answer;;;D. 50-60ml/ giờ', 'D. 50-60ml/ giờ', 1, 1, NULL, 1596183163, 1596183163, NULL),
(183, 'text', 'ĐDCB', 'Khi thực hiện kỹ thuật thông tiểu liên tục cho người bệnh, hành động nào sau đây của điều dưỡng có thể làm tổn thương đường niệu của người bệnh:', '', 'A. Thao tác đặt ống thông nhẹ nhàng;;;s_answer;;;B. Giữ dương vật vuông góc;;;s_answer;;;C. Bơm bóng ngay sau khi thấy nước tiểu chảy ra ;;;s_answer;;;D. Cố định ống thông trên nếp bẹn', 'C. Bơm bóng ngay sau khi thấy nước tiểu chảy ra', 1, 1, NULL, 1596183163, 1596183163, NULL),
(184, 'text', 'ĐDCB', 'Phòng ngừa biến chứng xẹp bàng quang khi đặt thông tiểu liên tục cho người bệnh, điều dưỡng cần:', '', 'A. Dặn người bệnh uống nhiều nước;;;s_answer;;;B. Xoay trở và cho người bệnh vận động sớm;;;s_answer;;;C. Khóa dây dẫn nước tiểu và xả mỗi 2-3 giờ/ lần ;;;s_answer;;;D. Dùng chất trơn tan trong nước', 'C. Khóa dây dẫn nước tiểu và xả mỗi 2-3 giờ/ lần', 1, 1, NULL, 1596183163, 1596183163, NULL),
(185, 'text', 'ĐDCB', 'Ống thông thường dùng để lấy nước tiểu cho sản phụ trước sanh là:', '', 'A. Sonde Nelaton ;;;s_answer;;;B. Sonde Foley 2 nhánh;;;s_answer;;;C. Sonde Foley 3 nhánh;;;s_answer;;;D. Sonde Coudée', 'A. Sonde Nelaton', 1, 1, NULL, 1596183163, 1596183163, NULL),
(186, 'text', 'ĐDCB', 'Tai biến nào có thể xảy ra do thao tác điều dưỡng không an toàn khi đặt thông tiểu thường cho người bệnh:', '', 'A. Xuất huyết niệu đạo;;;s_answer;;;B. Xuất huyết bàng quang ;;;s_answer;;;C. Viêm bàng quang;;;s_answer;;;D. Teo bàng quang', 'A. Xuất huyết niệu đạo', 1, 1, NULL, 1596183163, 1596183163, NULL),
(187, 'text', 'ĐDCB', 'Một bệnh nhân nữ 60 tuổi có chỉ định đặt thông tiểu liên tục, cỡ ống thông thích hợp là:', '', 'A. 8Fr;;;s_answer;;;B. 10Fr;;;s_answer;;;C. 12Fr;;;s_answer;;;D. 16Fr', 'D. 16Fr', 1, 1, NULL, 1596183163, 1596183163, NULL),
(188, 'text', 'ĐDCB', 'Thời gian lưu tối đa của ống thông tiểu làm từ chất liệu PVC là :', '', 'A. 1 tuần;;;s_answer;;;B. 2 tuần;;;s_answer;;;C. 3 tuần;;;s_answer;;;D. Theo khuyến cáo của nhà sản xuất', 'D. Theo khuyến cáo của nhà sản xuất', 1, 1, NULL, 1596183163, 1596183163, NULL),
(189, 'text', 'ĐDCB', 'Khi chăm sóc bệnh nhân nữ thông tiểu liên tục, điều dưỡng nên:', '', 'A. Tưới rửa bàng quang thường xuyên cho sạch;;;s_answer;;;B. Cố định ống an toàn ở mặt trong của đùi bệnh nhân ;;;s_answer;;;C. Kiểm tra cầu bang quang mỗi 4 giờ;;;s_answer;;;D. Nhận định lượng dịch xuất nhập mỗi giờ', 'B. Cố định ống an toàn ở mặt trong của đùi bệnh nhân', 1, 1, NULL, 1596183163, 1596183163, NULL),
(190, 'text', 'ĐDCB', 'Một bệnh nhân đang được thông tiểu liên tục hỏi điều dưỡng lý do cố định ống thông ở mặt trong đùi, điều dưỡng giải thích là nhằm phòng ngừa:', '', 'A. Tổn thương niệu đạo khi bệnh nhân xoay trở ;;;s_answer;;;B. Nước tiểu trào ra đầu niệu đạo xung quanh ống thông;;;s_answer;;;C. Ống thông dịch chuyển vào trong bàng quang;;;s_answer;;;D. Ống thông dịch chuyển ra ngoài', 'A. Tổn thương niệu đạo khi bệnh nhân xoay trở', 1, 1, NULL, 1596183163, 1596183163, NULL),
(191, 'text', 'ĐDCB', 'Điều nào sau đây là ĐÚNG khi thông tiểu liên tục cho bệnh nhân nữ:', '', 'A. Khi thấy nước tiểu chảy ra tiếp tục đẩy ông thông vào trong 2,5-5cm  ;;;s_answer;;;B. Tổng chiều dài ống thông đưa vào là 4-5cm;;;s_answer;;;C. Đây là một kỹ thuật sạch;;;s_answer;;;D. Ống thông có thể rút ra khi cầu bàng quang (-)', 'A. Khi thấy nước tiểu chảy ra tiếp tục đẩy ông thông vào trong 2,5-5cm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(192, 'text', 'ĐDCB', 'Khi rút ống thông tiểu liên tục cho người bệnh, hành động nào sau đây của điều dưỡng sẽ ngừa được tổn thương niệu đạo:', '', 'A. Mang găng tay vô khuần;;;s_answer;;;B. Làm xẹp bong bóng ;;;s_answer;;;C. Lấy hết nước tiểu trong túi chứa;;;s_answer;;;D. Đặt tấm giấy lót không thấm nước dưới mông người bệnh', 'B. Làm xẹp bong bóng', 1, 1, NULL, 1596183163, 1596183163, NULL),
(193, 'text', 'ĐDCB', 'Nguyên tắc khi chăm sóc vết thương cho người bệnh, NGOẠI TRỪ:', '', 'A. Mỗi mâm băng chỉ dùng riêng cho một người bệnh;;;s_answer;;;B. Rửa vết thương theo nguyên tắc từ dưới lên trên ;;;s_answer;;;C. Thời gian bộc lộ vết thương càng ngắn càng tốt;;;s_answer;;;D. Rửa da chung quanh vết thương rộng ra ngòai 3-5 cm', 'B. Rửa vết thương theo nguyên tắc từ dưới lên trên', 1, 1, NULL, 1596183163, 1596183163, NULL),
(194, 'text', 'ĐDCB', 'Một trong những mục đích cắt chỉ vết khâu là:', '', 'A. Tránh nhiễm trùng ;;;s_answer;;;B. Giúp vết thương mau lành;;;s_answer;;;C. Cầm máu;;;s_answer;;;D. Thoát lưu dịch và mủ khi vết thương nhiễm trùng', 'D. Thoát lưu dịch và mủ khi vết thương nhiễm trùng', 1, 1, NULL, 1596183163, 1596183163, NULL),
(195, 'text', 'ĐDCB', 'Khi thay băng vết thương, nhận định nào sau đây gợi ý vết thương đang nhiễm trùng', '', 'A. Miếng băng dơ tháo ra từ vết thương có nhiều dịch tiết và mùi ;;;s_answer;;;B. Bề mặt vết thương rướm máu khi thay băng;;;s_answer;;;C. Da xung quanh vết thương tại vị trí dán băng keo ửng đỏ;;;s_answer;;;D. Vết thương có vùng khuyết mất mô', 'A. Miếng băng dơ tháo ra từ vết thương có nhiều dịch tiết và mùi', 1, 1, NULL, 1596183163, 1596183163, NULL),
(196, 'text', 'ĐDCB', 'Trên một người bệnh có nhiều vết thương, điều dưỡng nên chăm sóc vết thương nào trước?', '', 'A. Vết thương có nhiều chất tiết thấm ướt băng;;;s_answer;;;B. Vết thương khô sạch;;;s_answer;;;C. Vết thương vô khuẩn không thấm dịch;;;s_answer;;;D. Vết thương đến ngày cắt chỉ', 'D. Vết thương đến ngày cắt chỉ', 1, 1, NULL, 1596183163, 1596183163, NULL),
(197, 'text', 'ĐDCB', 'Người bệnh có vết thương hở, đã sạch mủ, để giúp mô hạt mọc tốt nên', '', 'A. Tăng cường thuốc kháng sinh;;;s_answer;;;B. Dùng dầu mù u đắp lên vết thương ;;;s_answer;;;C. Rửa nhỏ giọt liên tục lên vết thương;;;s_answer;;;D. Thường xuyên thay băng', 'B. Dùng dầu mù u đắp lên vết thương', 1, 1, NULL, 1596183163, 1596183163, NULL),
(198, 'text', 'ĐDCB', 'Giai đoạn tăng sinh đối với một vết thương bình thường kéo dài từ:', '', 'A. 3 ngày sau khi tổn thương đến khoảng 30 ngày;;;s_answer;;;B. 4 ngày sau khi tổn thương đến khoảng 21 ngày ;;;s_answer;;;C. 6 ngày sau khi tổn thương đến khoảng 15 ngày;;;s_answer;;;D. 8 ngày sau khi tổn thương đến khoảng 10 ngày', 'B. 4 ngày sau khi tổn thương đến khoảng 21 ngày', 1, 1, NULL, 1596183163, 1596183163, NULL),
(199, 'text', 'ĐDCB', 'Lý do không dùng dung dịch thuốc đỏ để rửa vết thương là:', '', 'A. Gây hoại tử mô;;;s_answer;;;B. Khó nhận định vết thương do có màu ;;;s_answer;;;C. Làm chậm tiến trình lành vết thương;;;s_answer;;;D. Hấp thu Iode vào cơ thể', 'B. Khó nhận định vết thương do có màu', 1, 1, NULL, 1596183163, 1596183163, NULL),
(200, 'text', 'ĐDCB', 'Loại băng transparent film thích hợp cho:', '', 'A. Vết thương chỉ khâu có dịch ;;;s_answer;;;B. Vết thương loét tì độ IV;;;s_answer;;;C. Vết thương nhỏ không rỉ dịch ;;;s_answer;;;D. Vết thương bỏng trên bàn tay', 'C. Vết thương nhỏ không rỉ dịch', 1, 1, NULL, 1596183163, 1596183163, NULL),
(201, 'text', 'ĐDCB', 'Điều đầu tiên điều dưỡng cần làm khi vết thương ở chân của người bệnh đột ngột xuất huyết', '', 'A. Báo bác sĩ ;;;s_answer;;;B. Lấy dấu sinh hiệu;;;s_answer;;;C. Điều chỉnh tốc độ dịch truyền nhanh hơn;;;s_answer;;;D. Đè ép trực tiếp lên vết thương', 'D. Đè ép trực tiếp lên vết thương', 1, 1, NULL, 1596183163, 1596183163, NULL),
(202, 'text', 'ĐDCB', 'Điều dưỡng có thể giúp giảm lượng máu mất bằng cách:', '', 'A. Lập ngay đường truyền tĩnh mạch ;;;s_answer;;;B. Chườm lạnh vết thương;;;s_answer;;;C. Nâng chi đang xuất huyết cao hơn thân người ;;;s_answer;;;D. Kiểm tra tuần hoàn ngoại biên mỗi 10-15 phút', 'C. Nâng chi đang xuất huyết cao hơn thân người', 1, 1, NULL, 1596183163, 1596183163, NULL),
(203, 'text', 'ĐDCB', 'Khuyết điểm của oxy già:', '', 'A. Bất hoạt với vết thương nhiều dịch tiết ;;;s_answer;;;B. Gây độc nguyên bào sợi và tổn thương mô hạt mới mọc ;;;s_answer;;;C. Chống lại vi khuẩn kỵ khí ;;;s_answer;;;D. Nguy cơ bị hấp thu khi sử dụng trên vết thương sâu', 'B. Gây độc nguyên bào sợi và tổn thương mô hạt mới mọc', 1, 1, NULL, 1596183163, 1596183163, NULL),
(204, 'text', 'ĐDCB', 'Sonde Foley dùng trong thông tiểu thường', '', 'A. Đúng;;;s_answer;;;B. Sai', 'B. Sai', 1, 1, NULL, 1596183163, 1596183163, NULL),
(205, 'text', 'ĐDCB', 'Người bệnh có cân nặng 80kg, tiêm dưới da với góc độ là', '', 'A. 30 độ;;;s_answer;;;B. 45 độ;;;s_answer;;;C. 60 độ;;;s_answer;;;D. 90 độ', 'D. 90 độ', 1, 1, NULL, 1596183163, 1596183163, NULL),
(206, 'text', 'ĐDCB', 'Khi có hiện tượng thoát dịch ra ngoài thành mạch, cần xử trí ban đầu như sau', '', 'A. Cho dịch chảy với tốc độ chậm lại;;;s_answer;;;B. Dùng khăn lạnh đắp lên vị trí dịch thoát ra ngoài thành mạch ;;;s_answer;;;C. Chườm nóng;;;s_answer;;;D. Dùng trứng gà luộc còn ấm lăn nhẹ lên vị trí dịch thoát ra ngoài thành mạch', 'B. Dùng khăn lạnh đắp lên vị trí dịch thoát ra ngoài thành mạch', 1, 1, NULL, 1596183163, 1596183163, NULL),
(207, 'text', 'ĐDCB', 'Trường hợp nào cần phải thông tiểu liên tục', '', 'A. Bí tiểu (1);;;s_answer;;;B. Hôn mê (2);;;s_answer;;;C. Phẫu thuật (3);;;s_answer;;;D. (1), (2), (3)', 'C. Phẫu thuật (3)', 1, 1, NULL, 1596183163, 1596183163, NULL),
(208, 'text', 'ĐDCB', 'Khi đưa ống vào niệu đạo nếu thấy gặp trở ngại chúng ta cần phải', '', 'A. Tiếp tục dùng lực đẩy mạnh cho đến khi qua được trở ngại;;;s_answer;;;B. Ngưng lại và báo ngay cho bác sĩ;;;s_answer;;;C. Bảo người bệnh hít thở đều ;;;s_answer;;;D. Gọi người khác đến giúp', 'C. Bảo người bệnh hít thở đều', 1, 1, NULL, 1596183163, 1596183163, NULL),
(209, 'text', 'ĐDCB', 'Trong khi đang tiêm thuốc người bệnh than khó thở, toàn thân nổi mẫn đỏ, ngứa. Việc làm đầu tiên của ĐD là:', '', 'A. Tiếp tục bơm hết lượng thuốc còn lại để đảm bảo đúng liều dùng cho người bệnh;;;s_answer;;;B. Kêu gọi hỗ trợ;;;s_answer;;;C. Ngưng ngay mũi thuốc đang tiêm ;;;s_answer;;;D. Lấy dấu hiệu sinh tồn', 'C. Ngưng ngay mũi thuốc đang tiêm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(210, 'text', 'ĐDCB', 'Điều nào sau đây đúng khi chăm sóc vết thương', '', 'A. Áp dụng kỹ thuật vô khuẩn (1);;;s_answer;;;B. Dung dịch rửa vết thương tốt nhất là Betadine (2);;;s_answer;;;C. Rửa vết thương theo trình tự từ ngoài vào trong, từ trên xuống dưới (3);;;s_answer;;;D. (1) và (2)', 'A. Áp dụng kỹ thuật vô khuẩn (1)', 1, 1, NULL, 1596183163, 1596183163, NULL),
(211, 'text', 'ĐDCB', 'Khi dùng thuốc đặt vào niêm mạc hậu môn chúng ta cần phải', '', 'A. Bôi chất trơn lên thuốc;;;s_answer;;;B. Nhúng qua nước đá;;;s_answer;;;C. Ngâm qua nước đá ;;;s_answer;;;D. Ngâm qua nước ấm', 'C. Ngâm qua nước đá', 1, 1, NULL, 1596183163, 1596183163, NULL),
(212, 'text', 'ĐDCB', 'Tiêm là một trong những biện pháp đưa thuốc vào cơ thể nhằm mục đích', '', 'A. Điều trị ;;;s_answer;;;B. Chẩn đoán và điều trị ;;;s_answer;;;C. Điều trị và tiêm chủng;;;s_answer;;;D. Chẩn đoán, điều trị và tiêm chủng', 'D. Chẩn đoán, điều trị và tiêm chủng', 1, 1, NULL, 1596183163, 1596183163, NULL),
(213, 'text', 'ĐDCB', 'Để dụng cụ tiêm không bị nhiễm khuẩn, điều dưỡng cần phải:', '', 'A. "Sử dụng bơm, kim tiêm còn trong bao gói nguyên vẹn, còn hạn sử dụng ";;;s_answer;;;B. Sử dụng bơm, kim tiêm còn trong bao gói nguyên vẹn, còn hạn sử dụng và kim tiêm không được chạm vào tay điều dưỡng hoặc vật dụng xung quanh trước khi tiêm. ;;;s_answer;;;C. Kim tiêm không được chạm vào tay điều dưỡng hoặc vật dụng xung quanh trước khi tiêm và không nên tháo dời kim tiêm ra khỏi nắp kim trước khi tiêm ;;;s_answer;;;D. Kim tiêm không được chạm vào tay điều dưỡng hoặc vật dụng xung quanh trước khi tiêm và rửa tay trước khi chuẩn bị các phương tiện tiêm và trước khi tiêm', 'D. Kim tiêm không được chạm vào tay điều dưỡng hoặc vật dụng xung quanh trước khi tiêm và rửa tay trước khi chuẩn bị các phương tiện tiêm và trước khi tiêm', 1, 1, NULL, 1596183163, 1596183163, NULL),
(214, 'text', 'ĐDCB', 'Nguyên nhân cơ bản nhất dẫn đến hành vi thiếu an toàn trong tiêm là:', '', 'A. Thiếu phương tiện rửa tay/sát khuẩn tay ;;;s_answer;;;B. Thiếu ý thức tuân thủ quy trình tiêm an toàn của cán bộ y tế;;;s_answer;;;C. Tình trạng quá tải người bệnh, quá tải công việc ;;;s_answer;;;D. Thiếu dụng cụ tiêm phù hợp với yêu cầu sử dụng', 'B. Thiếu ý thức tuân thủ quy trình tiêm an toàn của cán bộ y tế', 1, 1, NULL, 1596183163, 1596183163, NULL),
(215, 'text', 'ĐDCB', 'Khi tiêm truyền tĩnh mạch, dấu hiệu nhận biết bệnh nhân bị biến chứng viêm tĩnh mạch do tính chất dịch truyền là:', '', 'A. Đỏ, nóng và đau vị trí tiêm, sốt, bạch cầu tăng;;;s_answer;;;B. Nóng rát, đau, sờ mạch thấy cứng ;;;s_answer;;;C. Đỏ và đau vị trí tiêm, sốt, bạch cầu tăng;;;s_answer;;;D. Nóng và đau vị trí tiêm, sốt, bạch cầu tăng', 'B. Nóng rát, đau, sờ mạch thấy cứng', 1, 1, NULL, 1596183163, 1596183163, NULL),
(216, 'text', 'ĐDCB', 'Người bệnh được truyền 500ml Lactat Ringer (với bộ dây truyền 20 giọt/1ml), giờ bắt đầu là 9g, tốc độ truyền dịch là XXV giọt/phút. Giờ kết thúc chai dịch sẽ là:', '', 'A. 13g40;;;s_answer;;;B. 14g40;;;s_answer;;;C. 15g40 ;;;s_answer;;;D. 16g40', 'C. 15g40', 1, 1, NULL, 1596183163, 1596183163, NULL),
(217, 'text', 'ĐDCB', 'Khi đang truyền dịch, người bệnh có dấu hiệu thở mệt, hồi hộp, tĩnh mạch cổ nổi, người bệnh có vấn đề gì?', '', 'A. Thiếu thể tích tuần hoàn;;;s_answer;;;B. Phù ngoại biên;;;s_answer;;;C. Quá tải dịch ;;;s_answer;;;D. Sốc phản vệ.', 'C. Quá tải dịch', 1, 1, NULL, 1596183163, 1596183163, NULL),
(218, 'text', 'ĐDCB', 'Khi xác định người bệnh có dấu hiệu quá tải dịch, điều dưỡng nên xử trí:', '', 'A. Báo bác sĩ, ngưng dịch truyền, chuẩn bị X-quang tại giường theo y lệnh;;;s_answer;;;B. Ngưng dịch truyền, cho người bệnh nằm tư thế Fowler, báo bác sĩ, chuẩn bị X-quang tại giường theo y lệnh ;;;s_answer;;;C. Cho người bệnh nằm tư thế Fowler, ngưng dịch truyền, báo bác sĩ và chuẩn bị X-quang tại giường theo y lệnh.;;;s_answer;;;D. Ngưng dịch truyền, lấy dấu sinh hiệu và chuẩn bị X-quang tại giường theo y lệnh.', 'B. Ngưng dịch truyền, cho người bệnh nằm tư thế Fowler, báo bác sĩ, chuẩn bị X-quang tại giường theo y lệnh', 1, 1, NULL, 1596183163, 1596183163, NULL),
(219, 'text', 'ĐDCB', 'Cách phòng ngừa nhiễm trùng tiểu đơn giản và hiệu quả là', '', 'A. Dùng thuốc kháng sinh ngay khi có chỉ định đặt thông tiểu;;;s_answer;;;B. Quản lý và chăm sóc ống thông đúng cách ;;;s_answer;;;C. Thay ống thông tiểu mỗi 7 ngày;;;s_answer;;;D. Vệ sinh bộ phận sinh dục khi dơ', 'B. Quản lý và chăm sóc ống thông đúng cách', 1, 1, NULL, 1596183163, 1596183163, NULL),
(220, 'text', 'ĐDCB', 'Nên chọn Sonde tiểu có thể tích là bao nhiêu để lưu ống thông tiểu giữ lại ở người lớn:', '', 'A. 10 ml ;;;s_answer;;;B. 15 ml;;;s_answer;;;C. 20 ml;;;s_answer;;;D. Loại nào cũng được', 'A. 10 ml', 1, 1, NULL, 1596183163, 1596183163, NULL),
(221, 'text', 'ĐDCB', 'Cách tháo bỏ nước tiểu trong túi chứa của hệ thống thông tiểu liên tục là:', '', 'A. Túi chứa nước tiểu chỉ nên được tháo mỗi mỗi tua và được chứa vào thùng chứa sạch riêng cho mỗi người bệnh (1);;;s_answer;;;B. Nhân viên phải rửa tay và mang găng sạch khi tháo nước tiểu (2);;;s_answer;;;C. Khi tháo nước tiểu phải tháo cho hết để tránh vi khuẩn phát triển trong nước tiểu còn ứ đọng (3);;;s_answer;;;D. (1), (2), (3)', 'D. (1), (2), (3)', 1, 1, NULL, 1596183163, 1596183163, NULL),
(222, 'text', 'ĐDCB', 'Mục đích đặt ống thông tiểu cho người bệnh trong phẫu thuật là:', '', 'A. Giải áp;;;s_answer;;;B. Ngừa nhiễm khuẩn trong phòng mổ ;;;s_answer;;;C. Theo dõi tính chất nước tiểu;;;s_answer;;;D. Cầm máu', 'B. Ngừa nhiễm khuẩn trong phòng mổ', 1, 1, NULL, 1596183163, 1596183163, NULL),
(223, 'text', 'ĐDCB', 'Dung dịch rửa vết thương nhiều dịch tiết là', '', 'A. Nước muối sinh lý 0,9%;;;s_answer;;;B. Betadine;;;s_answer;;;C. Iode;;;s_answer;;;D. Cồn 70 độ', 'A. Nước muối sinh lý 0,9%', 1, 1, NULL, 1596183163, 1596183163, NULL),
(224, 'text', 'ĐDCB', 'Vết thương thẩm mỹ thường cắt chỉ trong khoảng thời gian:', '', 'A. 1-2 ngày;;;s_answer;;;B. 3-5 ngày ;;;s_answer;;;C. 7 ngày;;;s_answer;;;D. 10 ngày', 'B. 3-5 ngày', 1, 1, NULL, 1596183163, 1596183163, NULL),
(225, 'text', 'Di truyền', 'Chuyển đoạn giữa NST số 13 và 21 được gọi là chuyển đoạn gì', '', 'A. Robersonian;;;s_answer;;;B. Duplication;;;s_answer;;;C. Deletion;;;s_answer;;;D. Triploidy', 'A. Robersonian', 1, 1, NULL, 1596183170, 1596183170, NULL),
(226, 'text', 'GM', 'Xử trí tác dụng phụ sau gây tê tủy sống kết hợp NMC (CSE) trong GĐSK.', '', 'A. HA tâm thu giảm 20% so với chưa gây tê hoặc nhỏ hơn 90mmHg, truyền dịch nhanh và dùng Ephedrin 5mg/ lần cho tới khi đạt như ban đầu.;;;s_answer;;;B. Mạch nhanh: 150 l/phút, ngưng truyền dịch ngay.;;;s_answer;;;C. Mạch chậm: <100l/phút dùng Atropin 0,5mg TMC, liều tối đa 1mg.;;;s_answer;;;D. Cả A,B,C đều đúng.', 'A. HA tâm thu giảm 20% so với chưa gây tê hoặc nhỏ hơn 90mmHg, truyền dịch nhanh và dùng Ephedrin 5mg/ lần cho tới khi đạt như ban đầu.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(227, 'text', 'GM', 'Xử trí run sau gây tê tủy sống kết hợp NMC (CSE) trong GĐSK.', '', 'A. Cho Priperan 10mg pha loãng TMC.;;;s_answer;;;B. Cho Pethidine 25 mg TMC kèm đắp ấm cho BN.;;;s_answer;;;C. Ngưng tryền Lactat-ringer hoặc NaCl 0,9%.;;;s_answer;;;D. A, B và C đúng', 'B. Cho Pethidine 25 mg TMC kèm đắp ấm cho BN.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(228, 'text', 'GM', 'Xử trí mạch chậm sau gây tê tủy sống kết hợp NMC (CSE) trong GĐSK', '', 'A. Cho BN nằm đầu cao 45º.;;;s_answer;;;B. Mạch <50l/phút dùng Atropin 0,5mg TMC, liều tối đa 1mg;;;s_answer;;;C. Cho BN thở Oxy qua Mask.;;;s_answer;;;D. Truyền dịch nhanh CL giọt/phút.', 'B. Mạch <50l/phút dùng Atropin 0,5mg TMC, liều tối đa 1mg', 1, 1, NULL, 1596183180, 1596183180, NULL),
(229, 'text', 'GM', 'Theo dõi sau gây tê tủy sống kết hợp NMC (CSE) trong GĐSK.', '', 'A. Theo dỏi bằng SpO2 mỗi 1 giờ đầu.;;;s_answer;;;B. Theo dỏi bằng SpO2 mỗi 30 phút trong 1 giờ đầu.;;;s_answer;;;C. Theo dỏi bằng SpO2 1 giờ đầu, mỗi giờ trong 3 giờ sau.;;;s_answer;;;D. Theo dỏi bằng SpO2 mỗi 30 phút trong 1 giờ đầu, mỗi giờ trong 3 giờ sau, mỗi 2 giờ trong những giờ tiếp theo.', 'D. Theo dỏi bằng SpO2 mỗi 30 phút trong 1 giờ đầu, mỗi giờ trong 3 giờ sau, mỗi 2 giờ trong những giờ tiếp theo.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(230, 'text', 'GM', 'Đáng giá đau sau gây tê tủy sống kết hợp NMC (CSE) trong GĐSK dựa vaò thang điểm VAS.', '', 'A. BN có VAS >1, tăng liều thuốc tê lên 2ml/lần/giờ, tối đa 12ml/giờ.;;;s_answer;;;B. BN có VAS >2, tăng liều thuốc tê lên 2ml/lần/giờ, tối đa 12ml/giờ;;;s_answer;;;C. BN có VAS >3, tăng liều thuosc tê lên 2ml/lần/giờ, tối đa 12ml/giờ;;;s_answer;;;D. BN có VAS >4, tăng liều thuosc tê lên 2ml/lần/giờ, tối đa 12ml/giờ.', 'C. BN có VAS >3, tăng liều thuosc tê lên 2ml/lần/giờ, tối đa 12ml/giờ', 1, 1, NULL, 1596183180, 1596183180, NULL),
(231, 'text', 'GM', 'Đánh giá ức chế vận động theo thang điểm Bromage cải tiến trong tê tủy sống kết hợp NMC (CSE) trong GĐSK.', '', 'A. Độ 1 có thể duỗi khớp gối.;;;s_answer;;;B. Độ 2 ức chế gần như hoàn toàn (chỉ cử động được bần chân).;;;s_answer;;;C. Độ 3 không thể vận động được khớp háng.;;;s_answer;;;D. Độ 4 có thể vận động được khớp háng khi nằm ngữa', 'B. Độ 2 ức chế gần như hoàn toàn (chỉ cử động được bần chân).', 1, 1, NULL, 1596183180, 1596183180, NULL),
(232, 'text', 'GM', 'Đánh giá thang điểm VAS gồm các mức độ sau', '', 'A. 2-4 đau nhẹ;;;s_answer;;;B. 4-6 đau vừa;;;s_answer;;;C. 6-8 đau nhiều;;;s_answer;;;D. Cả A, B, C đúng', 'D. Cả A, B, C đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(233, 'text', 'GM', 'Chống chỉ định tê tủy sống kết hợp NMC (CSE) trong GĐSK.', '', 'A. Bệnh lý van tim nặng;;;s_answer;;;B. Thiếu máu nặng hay thiếu thể tích tuần hoàn chưa bồi hoàn;;;s_answer;;;C. BN từ chối không hợp tác;;;s_answer;;;D. Cả A, B, C đúng', 'D. Cả A, B, C đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(234, 'text', 'GM', 'Bình thường nước và thức ăn sẽ trống trong dạ dày sau:', '', 'A. 2 giờ khi uống nước trong, 6 giờ sau ăn, nhủ nhi 4 giờ sau bú mẹ.;;;s_answer;;;B. 1 giờ khi uống nước trong, 4 giờ sau ăn, nhủ nhi 4 giờ sau bú mẹ.;;;s_answer;;;C. 3 giờ khi uống nước trong,  5 giờ sau ăn, nhủ nhi 2 giờ sau bú mẹ.;;;s_answer;;;D. 1 giờ khi uống nước trong, 6 giờ sau ăn, nhủ nhi 4 giờ sau bú mẹ', 'A. 2 giờ khi uống nước trong, 6 giờ sau ăn, nhủ nhi 4 giờ sau bú mẹ.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(235, 'text', 'GM', 'Yếu tố thuận lợi cho những BN có nguy cơ hít sặc (sự làm trống dạ dày chậm hơn)', '', 'A. Bệnh lý thực quản;;;s_answer;;;B. Tiền căn loét dạ dày, tá tràng;;;s_answer;;;C. Phụ nữ có thai trên 4 tháng;;;s_answer;;;D. Cả A, B, C đúng', 'D. Cả A, B, C đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(236, 'text', 'GM', 'Nguyên tắc nhịn ăn uống trước phẫu thuật cho BN mổ chương trình.', '', 'A. Nhịn đói trước phẫu thuật ít nhất 6 giờ, nước lọc ít nhất 2 giờ cho tất cả bệnh nhân;;;s_answer;;;B. Nhịn đói trước phẫu thuật ít nhất 8 giờ, nước lọc ít nhất 3 giờ cho tất cả bệnh nhân;;;s_answer;;;C. Nhịn đói trước phẫu thuật ít nhất 7 giờ, nước lọc ít nhất 3 giờ cho tất cả bệnh nhân;;;s_answer;;;D. Cả A, B, C đúng', 'A. Nhịn đói trước phẫu thuật ít nhất 6 giờ, nước lọc ít nhất 2 giờ cho tất cả bệnh nhân', 1, 1, NULL, 1596183180, 1596183180, NULL),
(237, 'text', 'GM', 'Nguyên tắc nhịn ăn uống trước phẫu thuật cho sản phụ mổ lấy thai chương trình.', '', 'A. Nhịn đói trước phẫu thuật ít nhất 6 giờ, thức ăn nhẹ (súp, cháo);;;s_answer;;;B. Nhịn đói trước phẫu thuật ít nhất 8 giờ, thức ăn thông thường;;;s_answer;;;C. Không sử dụng bất kỳ bạc hà, sing-gum hoặc kẹo trong suốt 6 giờ nhịn ăn.;;;s_answer;;;D. Cả A, B, C đúng', 'D. Cả A, B, C đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(238, 'text', 'GM', 'Đối với BN hoặc sản phụ mổ bán khẩn thì kháng tiết dạ dày.', '', 'A. Ranitidin (Zantac): 50mg TMC hoặc 300mg uống trước mổ 60 phút;;;s_answer;;;B. Metoclopramide: uống 30 phút trước mổ;;;s_answer;;;C. Metoclopramide: 100mg TMC trước 120 phút;;;s_answer;;;D. Cả A, B, C đúng', 'A. Ranitidin (Zantac): 50mg TMC hoặc 300mg uống trước mổ 60 phút', 1, 1, NULL, 1596183180, 1596183180, NULL),
(239, 'text', 'GM', 'Thuốc thường quy tăng co bóp cơ tử cung sau khi kẹp dây rốn hoặc khi bóc nhau xong trong mổ lấy thai.', '', 'A. Oxyticin 10UI/NaCl 0,9% 100ml: TTM nhanh;;;s_answer;;;B. Oxyticin 10UI/NaCl 0,9% 500ml: TTM 90 ml/ giờ;;;s_answer;;;C. Oxyticin 20UI/NaCl 0,9% 500ml: TTM nhanh;;;s_answer;;;D. Câu A, B đúng', 'D. Câu A, B đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(240, 'text', 'GM', 'Kê tư thế đúng trong khởi mê mổ lấy thai.', '', 'A. Chuẩn bị BN qua bàn phẫu thuật với tư thế nghiêng (T) 15º.;;;s_answer;;;B. Kê đầu, cổ, vai tư thế thuận tiên cho việc đặt NKQ.;;;s_answer;;;C. Bắt buộc cho đầu cao 30º.;;;s_answer;;;D. Câu A, B đúng', 'D. Câu A, B đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(241, 'text', 'GM', 'Ở BN gây mê NKQ, diều chỉnh duy trì EtCO2 ở mức độ.', '', 'A. 30 – 45;;;s_answer;;;B. 35 – 45;;;s_answer;;;C. 30 – 45;;;s_answer;;;D. 35 – 45', 'A. 30 – 45', 1, 1, NULL, 1596183180, 1596183180, NULL),
(242, 'text', 'GM', 'Tốc độ truyền Paracetamol để giảm đau sau phẫu thuật.', '', 'A. Paracetamol 1g, 1 chai TTM/15 phút;;;s_answer;;;B. Paracetamol 1g, 1 chai TTM/15 phút;;;s_answer;;;C. Paracetamol 1g, 1 chai TTM/15 phút;;;s_answer;;;D. Câu A, B đúng.', 'A. Paracetamol 1g, 1 chai TTM/15 phút', 1, 1, NULL, 1596183180, 1596183180, NULL),
(243, 'text', 'GM', 'Liều Fentanyl sau kẹp rốn ở NB mê NKQ mổ lấy thai.', '', 'A. Fentanyl 1mcg/kg;;;s_answer;;;B. Fentanyl 1 – 2 mcg/kg;;;s_answer;;;C. Fentanyl 2 mcg/kg;;;s_answer;;;D. Câu A, B đúng.', 'B. Fentanyl 1 – 2 mcg/kg', 1, 1, NULL, 1596183180, 1596183180, NULL),
(244, 'text', 'GM', 'Phẫu thuật cấp cứu cần đặt sond dạ dày vì, ngoại trừ.', '', 'A. NB không nhịn ăn uống trước phẫu thuật.;;;s_answer;;;B. Chấn thương làm ngưng trệ tiêu hóa;;;s_answer;;;C. Bn được xem là có dạ dày đầy.;;;s_answer;;;D. pH dạ dày thấp.', 'D. pH dạ dày thấp.', 1, 1, NULL, 1596183180, 1596183180, NULL),
