(283, 'text', 'GM', 'Nguyên nhân xẹp phổi của tai biến do đặt nội khí quản.', '', 'A.  Do đặt NKQ sâu vào một bên,;;;s_answer;;;B. Khi xoay trở ống NKQ bị di lệch vào sâu.;;;s_answer;;;C. Không nghe rì rào phế nang 1 bên phổi;;;s_answer;;;D. Cả A,B,C đúng', 'D. Cả A,B,C đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(284, 'text', 'GM', 'Triệu chứng của xẹp phổi của tai biến do đặt nội khí quản.', '', 'A. Không nghe rì rào phế nang 1 bên phổi;;;s_answer;;;B. Không nghe rì rào phế nang 2 bên phổi;;;s_answer;;;C. Một bên phổi bị xẹp.;;;s_answer;;;D. Cả A,B,C đúng.', 'D. Cả A,B,C đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(285, 'text', 'GM', 'Co thắt thanh, khí, phế quản trong gây mê, nguyên nhân thường gặp là:', '', 'A. Kích thích vùng hầu họng;;;s_answer;;;B. Kích thích dây thần khinh X, vùng hội âm;;;s_answer;;;C. Rút ống NKQ sớm;;;s_answer;;;D. Cả A,B,C đúng', 'D. Cả A,B,C đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(286, 'text', 'GM', 'Co thắt thanh, khí, phế quản trong gây mê, triệu chứng thường gặp là:', '', 'A. Bệnh có dấu hiệu khó thở;;;s_answer;;;B. Thở co kéo;;;s_answer;;;C. Rút NKQ quá sớm;;;s_answer;;;D. Câu A, B đúng.', 'D. Câu A, B đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(287, 'text', 'GM', 'Xử trí tai biến, biến chứng gây mê khi bị co thắt thanh, khí phế quản nhẹ:', '', 'A. Ngưng kích thích.;;;s_answer;;;B. Oxy áp lực dương 100%.;;;s_answer;;;C. Xịt Ventoline, cho dản cơ nếu bệnh nhân thở lại.;;;s_answer;;;D. Câu A,B,C đúng.', 'D. Câu A,B,C đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(288, 'text', 'GM', 'Xử trí tai biến, biến chứng gây mê khi bị co thắt thanh, khí, phế quản nặng do nguyên nhân cơ học.', '', 'A. Kiểm tra vị trí ống.;;;s_answer;;;B. Kiểm tra có hít sặc.;;;s_answer;;;C. Hút ống NKQ nhẹ nhàng.;;;s_answer;;;D. Câu A, B, C đúng', 'D. Câu A, B, C đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(289, 'text', 'GM', 'Đề phòng tai biến co thắt thanh, khí, phế quản trong gây mê:', '', 'A. Xịt Lidocaine 2% vào vùng hầu họng;;;s_answer;;;B. Tăng độ mê;;;s_answer;;;C. Hút sạch đàm nhớt trước đặt NKQ.;;;s_answer;;;D. Thao tác đặt NKQ, rút NKQ nhẹ nhàng, dẫn mê đủ liều.', 'D. Thao tác đặt NKQ, rút NKQ nhẹ nhàng, dẫn mê đủ liều.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(290, 'text', 'GM', 'Nguyên nhân tắc nghẽn đường thở trong gây mê:', '', 'A. Tụt hệ thống nối.;;;s_answer;;;B. Tắt nghẽn lòng ống;;;s_answer;;;C. Gập ống NKQ;;;s_answer;;;D. Câu A,B,C đúng.', 'D. Câu A,B,C đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(291, 'text', 'GM', 'Triệu chứng tắc nghẽn đường thở trong gây mê:', '', 'A. Huyết áp tăng, mạch giảm;;;s_answer;;;B. Hút đờm nhớt BN tăng tiết.;;;s_answer;;;C. Da xanh tím, vã mồ hôi, mạch nhanh.;;;s_answer;;;D. Cố định ống NKQ không tốt.', 'C. Da xanh tím, vã mồ hôi, mạch nhanh.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(292, 'text', 'GM', 'Xử trí tắt nghẽn đường thở do tai biến trong gây mê:', '', 'A. Hút đàm nhớt trước đặt NKQ;;;s_answer;;;B. Hút đàm nhớt, lấy dị vật ra khỏi đường thở, khiểm tra ống nối, ống NKQ.;;;s_answer;;;C. Bóp bóng theo nhịp thở của BN.;;;s_answer;;;D. Câu A,B,C sai.', 'B. Hút đàm nhớt, lấy dị vật ra khỏi đường thở, khiểm tra ống nối, ống NKQ.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(293, 'text', 'GM', 'Nguyên nhân nhịp bất thường của tai biến, biến chứng trong gây mê, ngoại trừ.', '', 'A. Suy nhược trung tâm hô hấp do thuốc mê, mê quá liều;;;s_answer;;;B. Tư thế bất thường cản trở hô hấp.;;;s_answer;;;C. BN ho sặc trong lúc duy trì mê.;;;s_answer;;;D. Thiếu dưởng khí, thừa thán khí.', 'C. BN ho sặc trong lúc duy trì mê.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(294, 'text', 'GM', 'Nguyên nhân suy nhược hoạt động cơ hô hấp:', '', 'A. Thuốc mê quá liều;;;s_answer;;;B. Thuốc dản cơ quá liều;;;s_answer;;;C. Tê tủy sống, ngoài màng cứng cao.;;;s_answer;;;D. Câu B,C đúng.', 'D. Câu B,C đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(295, 'text', 'GM', 'Đề phòng nhịp thở bất thường do tai tai biến, biến chứng trong gây mê.', '', 'A. Thở Oxy, hổ trợ hô hấp thích hợp khi cần.;;;s_answer;;;B. Tắt nghẽn đường thở.;;;s_answer;;;C. Theo dõi sát BN, hóa giải dản cơ và giảm đau nếu cần.;;;s_answer;;;D. Câu A,B,C sai.', 'C. Theo dõi sát BN, hóa giải dản cơ và giảm đau nếu cần.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(296, 'text', 'GM', 'Nguyên nhân ho sặc trong gây mê.', '', 'A. Do tắt nghẽn đường thở.;;;s_answer;;;B. Do đặt NKQ thô bạo;;;s_answer;;;C. Thao tác phẫu thuật nhẹ nhàng.;;;s_answer;;;D. Ho thường xãy ra khi chích Fentanyl nhanh lúc đặt NKQ mà BN chưa ngủ sâu bằng thuốc mê TM.', 'D. Ho thường xãy ra khi chích Fentanyl nhanh lúc đặt NKQ mà BN chưa ngủ sâu bằng thuốc mê TM.', 1, 1, NULL, 1596183180, 1596183180, NULL);
INSERT INTO `qandas` (`id`, `type_question`, `knowledge`, `question`, `image`, `answer`, `right_answer`, `created_by`, `updated_by`, `deleted_by`, `created_at`, `updated_at`, `deleted_at`) VALUES
(297, 'text', 'GM', 'Nguyên nhân nấc cục trong gây mê.', '', 'A. Tăng độ mê nếu BN chưa đạt độ mê.;;;s_answer;;;B. Hút dạ dày cho xẹp.;;;s_answer;;;C. Giúp thở với tầng số nhanh hơn.;;;s_answer;;;D. Do kích thích các tận cùng cảm giác cảu dây thâng kinh hoành (nhánh TK X), phẫu thuật vùng hạ vị lôi kéo khi độ mê chưa đạt.', 'D. Do kích thích các tận cùng cảm giác cảu dây thâng kinh hoành (nhánh TK X), phẫu thuật vùng hạ vị lôi kéo khi độ mê chưa đạt.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(298, 'text', 'GM', 'Xử trí nấc cục trong gây mê:', '', 'A. Dùng thêm thuốc mê, dản cơ.;;;s_answer;;;B. Giúp thở với tần số nhanh hơn.;;;s_answer;;;C. Hút dạ dày cho xẹp.;;;s_answer;;;D. Câu A,B,C đúng.', 'D. Câu A,B,C đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(299, 'text', 'GM', 'Nguyên nhân gây ra hiện tượng giật trong gây mê:', '', 'A. Ứ đọng thán khí.;;;s_answer;;;B. BN tự thở được nhưng hơi thở ngắn.;;;s_answer;;;C. Cơ liên sườn bị liệt do mê sâu hay dùng thuốc dản cơ.;;;s_answer;;;D. Câu A, C đúng.', 'D. Câu A, C đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(300, 'text', 'GM', 'Đề phòng nhịp nhanh xoang trong gây mê.', '', 'A. Bù đủ khối lượng tuần hoàn;;;s_answer;;;B. Xịt Xylocain, tiêm Xylocain tĩnh mạch trước đặt NKQ.;;;s_answer;;;C. Điều trị cường giáp ổn định.;;;s_answer;;;D. Câu A,B,C đúng.', 'D. Câu A,B,C đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(301, 'text', 'GM', 'Ở BN mổ nội soi bụng, khi thực hiện bơm thán khí, cần tuân thủ nguyên tắc.', '', 'A. BN phải ở tư thế nằm ngữa.;;;s_answer;;;B. Tốc độ bơm chậm từ 4 – 6 lít phút.;;;s_answer;;;C. Thể tích bơm vào từ 4 – 6 lít.;;;s_answer;;;D. Câu A,B,C đúng.', 'D. Câu A,B,C đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(302, 'text', 'GM', 'Áp lực bơm CO2 trong ổ bụng ở mổ nội soi:', '', 'A. Áp lực tối đa trong ổ bụng 10 mmHg.;;;s_answer;;;B. Áp lực tối đa trong ổ bụng 14 mmHg.;;;s_answer;;;C. Áp lực tối đa trong ổ bụng 20 mmHg.;;;s_answer;;;D. Cả A,B,C đúng.', 'B. Áp lực tối đa trong ổ bụng 14 mmHg.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(303, 'text', 'GM', 'Tư thế đúng của BN khi xả khí CO2 ở mổ nội soi ổ bụng', '', 'A. Nằm ngữa, đầu thấp;;;s_answer;;;B. Nằm ngữa, đầu cao.;;;s_answer;;;C. Nằm ngữa, ở mặt phẳng ngang.;;;s_answer;;;D. Câu A,B,C sai.', 'C. Nằm ngữa, ở mặt phẳng ngang.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(304, 'text', 'GM', 'Liều lượng sử dụng Xylocaine trong chống rung thất và nhịp nhanh thất:', '', 'A. Liều 1,5 mg/kg (TM), lặp lại 0,5mg/kg mỗi 8 phút, liều tổng 3mg/kg.;;;s_answer;;;B. Truyền tĩnh mạch liên tục lidocaine 2 -4mg/phút sau khi hồi sức thành công.;;;s_answer;;;C. Câu A,B đúng;;;s_answer;;;D. Câu A,B sai.', 'C. Câu A,B đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(305, 'text', 'GM', 'Liều Natri Bicarbonat 4,2% 250ml trong điều chỉnh toan máu:', '', 'A. 1 – 2 mEq/kg khi ngưng tim kéo dài trên 10 phút.;;;s_answer;;;B. Liều lặp lại 0,5 mEq/kg mỗi 20 – 25 phút kế tiếp nếu tim vẫn chưa đập lại.;;;s_answer;;;C. Câu A,B đúng.;;;s_answer;;;D. Câu A,B sai.', 'C. Câu A,B đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(306, 'text', 'GM', 'Liều lượng Atropine trong cấp cứu ngưng tim ngưng thở:', '', 'A. 0,25 – 0,5 mg mỗi lần, khi tim đập lại nhưng mạch chậm;;;s_answer;;;B. 0,5 mg mỗi lần, khi huyết áp thấp.;;;s_answer;;;C. 0,5mg ống pha loãng chích chậm khi huyết áp thấp.;;;s_answer;;;D. Cả A,B,C sai.', 'A. 0,25 – 0,5 mg mỗi lần, khi tim đập lại nhưng mạch chậm', 1, 1, NULL, 1596183180, 1596183180, NULL),
(307, 'text', 'GM', 'Tác dụng của Propofol trừ:', '', 'A. Giảm nhịp tim;;;s_answer;;;B. Giảm huyết áp;;;s_answer;;;C. Giảm đau;;;s_answer;;;D. Dùng cho BN nôn và ít nôn.', 'C. Giảm đau', 1, 1, NULL, 1596183180, 1596183180, NULL),
(308, 'text', 'GM', 'Đánh giá độ đau của BN vừa phẫu thuật xong dựa vào:', '', 'A. Loại phẫu thuật;;;s_answer;;;B. Cường độ đau;;;s_answer;;;C. Thời gian phẫu thuật;;;s_answer;;;D. Tất cả các ý trên.', 'D. Tất cả các ý trên.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(309, 'text', 'GM', 'Dịch tinh thể có khả năng làm phù khoảng kẻ do:', '', 'A. Áp lực thấp hơn áp lực huyết tương;;;s_answer;;;B. Các chất phân tử cao không qua được màng;;;s_answer;;;C. Na+ kéo nước từ lòng mạch vào khoảng kẻ.;;;s_answer;;;D. Cả A,B,C đúng.', 'B. Các chất phân tử cao không qua được màng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(310, 'text', 'GM', 'Thuốc nào làm tăng tác dụng của thuốc giản cơ:', '', 'A. Gentamycin;;;s_answer;;;B. Cephalosporin;;;s_answer;;;C. Thuốc Calci;;;s_answer;;;D. Penicillin.', 'A. Gentamycin', 1, 1, NULL, 1596183180, 1596183180, NULL),
(311, 'text', 'GM', 'Nguyên nhân chính gây hạ thân nhiệt nặng ở bệnh nhân phẫu thuật nội soi ổ bụng là:', '', 'A. Do máy điều hòa;;;s_answer;;;B. Do dịch truyền để trong phòng mổ;;;s_answer;;;C. Do cacbon dioxide ;;;s_answer;;;D. Do ánh sáng của đèn nội soi.', 'C. Do cacbon dioxide', 1, 1, NULL, 1596183180, 1596183180, NULL),
(312, 'text', 'GM', 'Biến chứng có thể xảy ra trong phẫu thuật nội soi ổ bụng, ngoại trừ:', '', 'A. Chảy máu ;;;s_answer;;;B. Nhiễm trùng ;;;s_answer;;;C. Rối loạn nhịp tim ;;;s_answer;;;D. Nhiễm độc tia laser', 'B. Nhiễm trùng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(313, 'text', 'GM', 'Nguyên nhân chính gây biến chứng rối loạn nhịp tim nặng trong phẫu thuật nội soi ổ bụng là do', '', 'A. Đặt troca chạm vào gan ;;;s_answer;;;B. Đặt troca gây thủng cơ hoành ;;;s_answer;;;C. Đặt troca gây thủng mặc treo tràng trên ;;;s_answer;;;D. Do CO2', 'D. Do CO2', 1, 1, NULL, 1596183180, 1596183180, NULL),
(314, 'text', 'GM', 'Những biến chứng nguy hiểm đến tính mạng của bệnh nhân ngay trên bàn mổ trong phẫu thuật nội soi, ngoại trừ:', '', 'A. Rung thất;;;s_answer;;;B. Tràn khí màng phổi, tràn khí trung thất ;;;s_answer;;;C. Thuyên tắc khí ;;;s_answer;;;D. Thủng tạng rỗng', 'D. Thủng tạng rỗng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(315, 'text', 'GM', 'Những bệnh lý nào có thể phẫu thuật nội soi, ngoại trừ:', '', 'A. Nang ruột đôi ;;;s_answer;;;B. Tràn mủ và dày dính màng tim;;;s_answer;;;C. Còn ống động mạch ;;;s_answer;;;D. Tăng áp lực tĩnh mạch cửa', 'D. Tăng áp lực tĩnh mạch cửa', 1, 1, NULL, 1596183180, 1596183180, NULL),
(316, 'text', 'GM', 'Trong phẫu thuật trên bệnh nhân suy gan ,Gây mê cần chú ý:', '', 'A. Huyết áp giảm;;;s_answer;;;B. Thiếu Oxy ;;;s_answer;;;C. Tất cả đều đúng', 'C. Tất cả đều đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
(317, 'text', 'GM', 'Thuốc dãn cơ ưu tiên dung trên bệnh nhân bị suy gan :', '', 'A. Rocuronium ;;;s_answer;;;B. Vecuronium ;;;s_answer;;;C. Suxamethonium ;;;s_answer;;;D. Atrarium', 'D. Atrarium', 1, 1, NULL, 1596183180, 1596183180, NULL),
(318, 'text', 'GM', 'Thuốc nào không dung cho bệnh nhân suy gan:', '', 'A. Fentanyl ;;;s_answer;;;B. Midazolam ;;;s_answer;;;C. Propofol ;;;s_answer;;;D. Acetaminophen', 'D. Acetaminophen', 1, 1, NULL, 1596183180, 1596183180, NULL),
(319, 'text', 'GM', 'Phản ứng phụ của Succinylcholine là gì ?', '', 'A. Tăng nhịp tim ;;;s_answer;;;B. Tăng Kali máu ;;;s_answer;;;C. Giảm Kali máu ;;;s_answer;;;D. A và B đúng.', 'D. A và B đúng.', 1, 1, NULL, 1596183180, 1596183180, NULL),
(320, 'text', 'GM', 'Liều Neostigmine dùng hóa giải dãn cơ:', '', 'A. 4 mcg/kg;;;s_answer;;;B. 40 mcg/kg;;;s_answer;;;C. 0.4 mg/kg;;;s_answer;;;D. 0.04 mg/kg', 'D. 0.04 mg/kg', 1, 1, NULL, 1596183180, 1596183180, NULL),
(321, 'text', 'GM', 'Liều đặt nội khí quản theo khuyến cáo của thuốc Esmeron', '', 'A. 0.4mg/kg;;;s_answer;;;B. 0.5mg/kg;;;s_answer;;;C. 0.6mg/kg;;;s_answer;;;D. 0.8mg/kg', 'C. 0.6mg/kg', 1, 1, NULL, 1596183180, 1596183180, NULL),
(322, 'text', 'GM', 'Bệnh nhân có tiền căn suyễn, thuốc nào không nên dùng cho bệnh nhân này:', '', 'A. Propofol;;;s_answer;;;B. Esmeron;;;s_answer;;;C. Etomidate;;;s_answer;;;D. Tracrium', 'D. Tracrium', 1, 1, NULL, 1596183180, 1596183180, NULL),
(323, 'text', 'GM', 'EtCO2 có giá trị bình thường là:', '', 'A. 40 ± 5 mmHg;;;s_answer;;;B. 35 ± 5 mmHg;;;s_answer;;;C. 30 – 45 mmHg;;;s_answer;;;D. 35 – 50 mmHg', 'A. 40 ± 5 mmHg', 1, 1, NULL, 1596183180, 1596183180, NULL),
(324, 'text', 'GM', 'Trường hợp nào là chống chỉ định của tê tủy sống:', '', 'A. Bệnh nhân có bệnh đái tháo đường ;;;s_answer;;;B.  Bệnh nhân có chức năng gan, thận suy kém;;;s_answer;;;C. Bệnh nhân bị bệnh lao phổi;;;s_answer;;;D. Bệnh nhân không đồng ý gây tê', 'D. Bệnh nhân không đồng ý gây tê', 1, 1, NULL, 1596183180, 1596183180, NULL),
(325, 'text', 'GM', 'Tác dụng của Lidocaine trên hệ thần kinh trung ương tùy thuộc vào:', '', 'A. Sự hiện diện của các thuốc tê khác;;;s_answer;;;B. Nồng độ của dung dịch thuốc tê;;;s_answer;;;C. PaCO;;;s_answer;;;D. Liều lượng của thuốc tê;;;s_answer;;;E. Tất cả đều đúng', 'E. Tất cả đều đúng', 1, 1, NULL, 1596183180, 1596183180, NULL),
