(406, 'text', 'GM', 'Cách di chuyển trong phòng mổ của 2 nhân viên đang mặc áo choàng và mang găng vô khuẩn, NGOẠI TRỪ:', '', 'A. Mặt đối mặt hoặc lưng đối lưng ở khoảng trống;;;s_answer;;;B. Chỉ lưng đối lưng khi ngay sau lưng một trong 2 người là vùng không vô khuẩn;;;s_answer;;;C. Chỉ lưng đối lưng khi ngay trước mặt một trong 2 người là vùng vô khuẩn;;;s_answer;;;D. Chỉ mặt đối mặt khi ngay sau lưng một trong 2 người là vùng không vô khuẩn', 'B. Chỉ lưng đối lưng khi ngay sau lưng một trong 2 người là vùng không vô khuẩn', 1, 1, NULL, 1596183180, 1596183180, NULL),
(407, 'text', 'GM', 'Một trong những thứ tự chà tay bằng bàn chải trong rửa tay ngoại khoa là:', '', 'A. Móng tay, lòng bàn tay, từng ngón tay, lưng bàn tay, cẳng tay;;;s_answer;;;B. Móng tay, từng ngón tay, lòng bàn tay, lưng bàn tay, cẳng tay;;;s_answer;;;C. Móng tay, lưng bàn tay, từng ngón tay, lòng bàn tay, cẳng tay;;;s_answer;;;D. Móng tay, từng ngón tay, lưng bàn tay, lòng bàn tay, cẳng tay', 'B. Móng tay, từng ngón tay, lòng bàn tay, lưng bàn tay, cẳng tay', 1, 1, NULL, 1596183180, 1596183180, NULL),
(408, 'text', 'GM', 'Điều dưỡng vòng trong sau khi mặc áo choàng và mang găng vô khuẩn thì vùng vô khuẩn trên áo được tính:', '', 'A. Mặt trước áo choàng tính từ thắt lưng trở lên và cách mí cổ áo 5 cm trở xuống;;;s_answer;;;B. Mặt trước áo choàng tính từ thắt lưng trở lên;;;s_answer;;;C. Mặt trước áo choàng;;;s_answer;;;D. Mặt trước áo choàng và cách mí cổ áo 5 cm', 'A. Mặt trước áo choàng tính từ thắt lưng trở lên và cách mí cổ áo 5 cm trở xuống', 1, 1, NULL, 1596183180, 1596183180, NULL),
(409, 'text', 'GM', 'Cách sử dụng kim cho loại khâu nào là phù hợp:', '', 'A. Kim tam giác dùng để khâu mạch máu;;;s_answer;;;B. Kim tam giác dùng để khâu cơ;;;s_answer;;;C. Kim tam giác dùng để khâu niêm mạc;;;s_answer;;;D. Kim tròn dùng để khâu mạch máu', 'D. Kim tròn dùng để khâu mạch máu', 1, 1, NULL, 1596183180, 1596183180, NULL),
(410, 'text', 'GM', 'Việc rửa da trước mổ trong phòng mổ được thực hiện bởi:', '', 'A. Điều dưỡng vòng trong;;;s_answer;;;B. Điều dưỡng vòng ngoài;;;s_answer;;;C. Điều dưỡng trưởng tua;;;s_answer;;;D. Phẫu thuật viên phụ', 'B. Điều dưỡng vòng ngoài', 1, 1, NULL, 1596183180, 1596183180, NULL),
(411, 'text', 'GM', 'Những chỗ nguy hiểm gây nổ và cháy:', '', 'A. Các chất tẩy uế dự trữ;;;s_answer;;;B. Phải có 1 chất hơi có thể cháy được;;;s_answer;;;C. Các dụng cụ điện;;;s_answer;;;D. Các chất hơi có thể cháy', 'A. Các chất tẩy uế dự trữ', 1, 1, NULL, 1596183180, 1596183180, NULL),
(412, 'text', 'GM', 'Loại dụng cụ nào chữa cháy cho vật gây cháy có nguồn gốc từ điện', '', 'A. Bọt nước;;;s_answer;;;B. Bọt rượu;;;s_answer;;;C. Thùng bơm nước Soda;;;s_answer;;;D. Carbon Tetrachloride', 'D. Carbon Tetrachloride', 1, 1, NULL, 1596183180, 1596183180, NULL),
(413, 'text', 'GM', 'Tư thế Nằm sắp dùng trong phẫu thuật:', '', 'A. Xương sọ vùng trán;;;s_answer;;;B. Xương sọ vùng chẩm;;;s_answer;;;C. Túi mật;;;s_answer;;;D. Vùng chậu', 'B. Xương sọ vùng chẩm', 1, 1, NULL, 1596183180, 1596183180, NULL),
(414, 'text', 'GM', 'Tư thế nằm để mổ u xơ tiền liệt tuyến', '', 'A. Nằm ngửa;;;s_answer;;;B. Nằm sắp;;;s_answer;;;C. Nằm nghiêng;;;s_answer;;;D. Nằm sản phụ khoa', 'D. Nằm sản phụ khoa', 1, 1, NULL, 1596183180, 1596183180, NULL),
(415, 'text', 'GM', 'Chỉ nào là chỉ tự tiêu:', '', 'A. Chỉ Coated Vicryl;;;s_answer;;;B. Chỉ Silk;;;s_answer;;;C. Chỉ Cotton;;;s_answer;;;D. Chỉ Ethilon', 'A. Chỉ Coated Vicryl', 1, 1, NULL, 1596183180, 1596183180, NULL),
(416, 'text', 'GM', 'Chỉ nào là chỉ không tiêu', '', 'A. Chỉ Nurolon;;;s_answer;;;B. Chỉ PDS II;;;s_answer;;;C. Chỉ Monocryl;;;s_answer;;;D. Chỉ Catgut', 'A. Chỉ Nurolon', 1, 1, NULL, 1596183181, 1596183181, NULL),
(417, 'text', 'GM', 'Đối với người bệnh phẫu thuật có chỉ định đặt ống thông tiểu, loại bỏ ống thông tiểu khi nào:', '', 'A. Tốt nhất là loại bỏ ống thông tiểu trong vòng 12 giờ sau phẫu thuật;;;s_answer;;;B. Tốt nhất là loại bỏ ống thông tiểu trong vòng 24 giờ sau phẫu thuật;;;s_answer;;;C. Tốt nhất là loại bỏ ống thông tiểu trong vòng 36 giờ sau phẫu thuật;;;s_answer;;;D. Tốt nhất là loại bỏ ống thông tiểu trong vòng 72 giờ sau phẫu thuật', 'B. Tốt nhất là loại bỏ ống thông tiểu trong vòng 24 giờ sau phẫu thuật', 1, 1, NULL, 1596183181, 1596183181, NULL),
(418, 'text', 'GPBL', 'Điều nào đúng khi mô tả đại thể bệnh phẩm sảy thai?', '', 'A. Màng nhau: chỗ bám, màu sắc, độ trong suốt, có toàn vẹn không; thai ngoài màng nhau không. Chảy máu ở rìa hoặc ở màng nhau.;;;s_answer;;;B. Dây rốn: chỗ bám, màu sắc, những biến đổi khu trú.;;;s_answer;;;C. Gai rau: có thoái hoá nước không (ghi nhận tỉ lệ phần trăm các gai rau bị tổn thương và kích thước của các túi nước), các tổn thương khu trú.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(419, 'text', 'GPBL', 'Các mẫu cần lấy từ mô nhau của bệnh phẩm sẩy thai:', '', 'A. Màng nhau nằm ngoài bánh nhau, dây rốn.;;;s_answer;;;B. Bản đệm, gai nhau từ màng đệm lược, bao gồm nhau phía mặt mẹ.;;;s_answer;;;C. Không lấy mẫu màng nhau.;;;s_answer;;;D. Cả A và B.', 'D. Cả A và B.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(420, 'text', 'GPBL', 'Những đặc điểm cần mô tả đại thể ở bệnh phẩm hạch nạo vét:', '', 'A. Số lượng hạch trong mỗi nhóm hạch; kích thước hạch lớn nhất trong mỗi nhóm hạch; hình dạng, màu sắc, nghi có di căn không.;;;s_answer;;;B. Kích thước hạch lớn nhất trong mỗi nhóm hạch; hình dạng, màu sắc, nghi có di căn không.;;;s_answer;;;C. Số lượng hạch trong mỗi nhóm hạch; kích thước hạch lớn nhất trong mỗi nhóm hạch.;;;s_answer;;;D. Số lượng hạch trong mỗi nhóm hạch; hình dạng, màu sắc, nghi có di căn không.', 'A. Số lượng hạch trong mỗi nhóm hạch; kích thước hạch lớn nhất trong mỗi nhóm hạch; hình dạng, màu sắc, nghi có di căn không.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(421, 'text', 'GPBL', 'Điều nào đúng khi cắt lọc khảo sát vi thể bệnh phẩm hạch nạo vét?', '', 'A. Chỉ cần khảo sát những hạch lớn hơn 3mm.;;;s_answer;;;B. Những hạch nhỏ < 3mm phải lấy đúc riêng từng khối, không được để chung trong một khuôn nhựa.;;;s_answer;;;C. Hạch lớn được cắt đôi, sau đó có thể cắt lát 2 - 3 mm, diện cắt càng lớn càng tốt và có thể để riêng trong từng khuôn nhựa.;;;s_answer;;;D. Hạch lớn được cắt cho vừa khuôn nhựa, lấy một mẫu đại diện, phần còn lại để trong lọ chứa formol, để riêng theo từng nhóm.', 'C. Hạch lớn được cắt đôi, sau đó có thể cắt lát 2 - 3 mm, diện cắt càng lớn càng tốt và có thể để riêng trong từng khuôn nhựa.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(422, 'text', 'GPBL', 'Số lượng hạch tối thiểu cần lấy ở bệnh phẩm hạch nạo vét.', '', 'A. 12;;;s_answer;;;B. 25;;;s_answer;;;C. Tất cả hạch cần được xét nghiệm mô bệnh học.;;;s_answer;;;D. Không có số lượng tối thiểu; lấy mẫu những hạch nghi ngờ.', 'C. Tất cả hạch cần được xét nghiệm mô bệnh học.', 1, 1, NULL, 1596183283, 1596183283, NULL);
INSERT INTO `qandas` (`id`, `type_question`, `knowledge`, `question`, `image`, `answer`, `right_answer`, `created_by`, `updated_by`, `deleted_by`, `created_at`, `updated_at`, `deleted_at`) VALUES
(423, 'text', 'GPBL', 'Chọn câu đúng nhất. Quy trình chuẩn bị trong phẫu tích bệnh phẩm ruột thừa:', '', 'A. Đo kích thước ruột thừa (chiều dài và đường kính lớn nhất).;;;s_answer;;;B. Cắt mẫu ra làm 2 phần, bằng cách cắt ngang cách đỉnh ruột thừa 2cm.;;;s_answer;;;C. Cắt ngang theo đường kính ở phần đầu gần mỗi 5 mm. Cắt dọc đầu xa ra 2 phần;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(424, 'text', 'GPBL', 'Chọn câu đúng nhất. Nếu khi cắt lọc phát hiện có u trong mẫu mô ruột thừa phải:', '', 'A. Bôi mực;;;s_answer;;;B. Cắt thêm 2 mẫu ở rìa phẫu thuật;;;s_answer;;;C. Bôi mực và cắt thêm 1 mẫu ở rìa phẫu thuật.;;;s_answer;;;D.  Cắt thêm 1 phẫn ở đầu nhọn ruột thừa.', 'C. Bôi mực và cắt thêm 1 mẫu ở rìa phẫu thuật.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(425, 'text', 'GPBL', 'Bệnh phẩm ruột thừa sau cắt lọc phải có đầy đủ phần :', '', 'A. Đầu.;;;s_answer;;;B. Thân. ;;;s_answer;;;C. Đuôi ruột thừa;;;s_answer;;;D. Đầu, thân, đuôi ruột thừa.', 'D. Đầu, thân, đuôi ruột thừa.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(426, 'text', 'GPBL', 'Chọn câu đúng nhất. Quy trình chuẩn bị phẫu tích bệnh phẩm cắt bỏ tử cung:', '', 'A. Cắt ngang mỗi nửa thành những lát song song dày khoảng 1cm, nhưng còn để chúng dính với nhau ở một đầu. Quan sát kỹ từng mặt cắt.;;;s_answer;;;B. Cắt ngang mỗi nửa thành những lát song song dày khoảng 3cm;;;s_answer;;;C. Đánh dấu nửa sau (bằng 1 khía lõm hoặc chỗ gắn của vòi trứng);;;s_answer;;;D. Cắt cổ tử cung vài lát dọc theo ống cổ ngoài cổ tử cung.', 'A. Cắt ngang mỗi nửa thành những lát song song dày khoảng 1cm, nhưng còn để chúng dính với nhau ở một đầu. Quan sát kỹ từng mặt cắt.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(427, 'text', 'GPBL', 'Mô tả đại thể trong phẫu tịch bệnh phẩm cắt bỏ tử cung, chọn câu SAI:', '', 'A.  Loại phẫu thuật cắt bỏ tử cung: toàn phần, tận gốc, cắt bỏ phần phụ.;;;s_answer;;;B. Hình dạng tử cung: có biến dạng không, có nốt gồ dưới thanh mạc không.;;;s_answer;;;C. Cổ tử cung: có dải xơ dày dính không.;;;s_answer;;;D.  Bề dày thành tử cung, có các bất thường không.', 'C. Cổ tử cung: có dải xơ dày dính không.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(428, 'text', 'GPBL', 'Mô tả u cơ trơn tử cung trong phẫu tích bệnh phẩm cắt bỏ tử cung:', '', 'A. Số lượng, vị trí (dưới thanh mạc, trong thành, dưới nội mạc);;;s_answer;;;B. Kích thước;;;s_answer;;;C. Có cuống hoặc không cuống, chảy máu, hoại tử, vôi hóa không, loét nội mạc tử cung bên trên không.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(429, 'text', 'GPBL', 'Trong cắt lọc bệnh phẩm cắt bỏ tử cung, cắt cổ tử cung bao nhiêu lát cắt cho nửa trước và cho nửa sau:', '', 'A. 1;;;s_answer;;;B. 2;;;s_answer;;;C. 3;;;s_answer;;;D. 4', 'A. 1', 1, 1, NULL, 1596183283, 1596183283, NULL),
(430, 'text', 'GPBL', 'Phẫu tích bệnh phẩm sinh thiết cổ tử cung, quy trình chuẩn bị gồm:', '', 'A. Không cắt nếu mẫu bệnh phẩm < 4mm.;;;s_answer;;;B. Tất cả bệnh phẩm phải được xử lý dù rất nhỏ.;;;s_answer;;;C. Lấy tất cả các mẫu bệnh phẩm rất nhỏ còn sót lại trong bình chứa.;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(431, 'text', 'GPBL', 'Chọn câu SAI. Mô tả đại thể trong phẫu tích bệnh phẩm sinh thiết cổ tử cung:', '', 'A. Số lượng mẫu, màu sắc, kích thước.;;;s_answer;;;B. Kích thước chung của tập hợp bệnh phẩm.;;;s_answer;;;C. Ghi nhận sự hiện diện của biểu mô; biểu mô có bị trợt không, có loét không, có bất thường khác không.;;;s_answer;;;D. Tất cả đều sai.', 'D. Tất cả đều sai.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(432, 'text', 'GPBL', 'Chọn ý đúng nhất. Quy trình cắt lọc trong phẫu tích bệnh phẩm sinh thiết cổ tử cung:', '', 'A. Nếu bệnh phẩm là mô nạo ống cổ tử cung thì phải vùi khối parafin riêng, kể cả chất nhầy đi kèm của tuyến cổ trong.;;;s_answer;;;B. Nếu bệnh phẩm là mô nạo ống cổ tử cung thì phải vùi khối parafin chung.;;;s_answer;;;C. Nếu bệnh phẩm là mô nạo ống cổ tử cung thì phải chất nhầy đi kèm của tuyến cổ trong.;;;s_answer;;;D. Nếu bệnh phẩm là mô nạo ống cổ tử cung thì phải vùi khối parafin riêng.', 'A. Nếu bệnh phẩm là mô nạo ống cổ tử cung thì phải vùi khối parafin riêng, kể cả chất nhầy đi kèm của tuyến cổ trong.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(433, 'text', 'GPBL', 'Trong phẫu tích bệnh phẩm sinh thiết cổ tử cung, không cắt nếu mẫu bệnh phẩm:', '', 'A. < 4mm.;;;s_answer;;;B. < 5mm.;;;s_answer;;;C. < 6mm.;;;s_answer;;;D.  < 7mm.', 'A. < 4mm.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(434, 'text', 'GPBL', 'Điền vào chỗ trống. Quy trình chuẩn bị trong phẫu tích bệnh phẩm cắt bỏ vòi trứng:” Nếu vòi tử cung tương đối bình thường về kích thước: cắt hàng loạt cách nhau ____ và ___  ___. Lát cắt không đứt rời hoàn toàn, do đó, các mẫu vẫn còn dính nhau nhờ lớp thanh mạc.”', '', 'A. 5mm/ quan sát.;;;s_answer;;;B. 6mm/ quan sát.;;;s_answer;;;C. 7mm/ mô tả.;;;s_answer;;;D. 8mm/ mô tả.', 'A. 5mm/ quan sát.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(435, 'text', 'GPBL', 'Chọn câu đúng nhất. Quy trình chuẩn bị trong phẫu tích bệnh phẩm cắt bỏ vòi trứng:', '', 'A. Cố định bệnh phẩm trước khi cắt. Nếu vòi tử cung bị dính vào tử cung: cố định toàn bộ.;;;s_answer;;;B.  Đo chiều dài và đường kính lớn nhất.;;;s_answer;;;C. Nếu vòi tử cung to lên bất thường, cắt một lát dọc, sau đó là các lát cắt song song nếu cần.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(436, 'text', 'GPBL', 'Chọn câu SAI. Quy trình mô tả đại thể trong phẫu tích bệnh phẩm cắt bỏ vòi trứng:', '', 'A. Chiều dài và đường kính nhỏ nhất.;;;s_answer;;;B. Thanh mạc: có tơ huyết, chảy máu, dính vào buồng trứng hoặc cơ quan khác không.;;;s_answer;;;C. Niêm mạc: có teo, tăng sản, mặt ngoài của loa vòi; có lộn ngược không.;;;s_answer;;;D. Lòng: có rõ ràng, có dãn, có chất chứa bên trong không, đường kính thế nào (nếu to bất thường).', 'A. Chiều dài và đường kính nhỏ nhất.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(437, 'text', 'GPBL', 'Chọn câu đúng nhất. Quy trình cắt lọc trong phẫu tích bệnh phẩm cắt bỏ vòi trứng:', '', 'A.  Đối với vòi trứng nghi ngờ có thai ngoài tử cung: lấy đại diện mẫu mô có vẻ là sản phẩm của thai.;;;s_answer;;;B. Đối với vòi trứng nghi ngờ có thai ngoài tử cung: lấy tất cả các mẫu mô có vẻ là sản phẩm của thai. ;;;s_answer;;;C.  Đối với vòi trứng nghi ngờ có thai ngoài tử cung: lấy tất cả các mẫu mô có vẻ là sản phẩm của thai. Nếu đại thể không rõ, lấy nhiều lát cắt từ 1 thành ống tại vùng chảy máu cũng như lấy nhiều lát từ cục máu đông trong lòng vòi. Nếu không tìm thấy các thành phần của thai trên vi thể, cắt thêm nhiều lát nữa.;;;s_answer;;;D. Đối với vòi trứng nghi ngờ có thai ngoài tử cung: lấy nhiều lát cắt từ 1 thành ống tại vùng chảy máu cũng như lấy nhiều lát từ cục máu đông trong lòng vòi.', 'C.  Đối với vòi trứng nghi ngờ có thai ngoài tử cung: lấy tất cả các mẫu mô có vẻ là sản phẩm của thai. Nếu đại thể không rõ, lấy nhiều lát cắt từ 1 thành ống tại vùng chảy máu cũng như lấy nhiều lát từ cục máu đông trong lòng vòi. Nếu không tìm thấy các ', 1, 1, NULL, 1596183283, 1596183283, NULL),
(438, 'text', 'GPBL', 'Nguyên tắc lấy mẫu của bệnh phẩm nạo hoặc sinh thiết nội mạc tử cung là gì? Chọn câu đúng nhất.', '', 'A. Lấy toàn bộ bệnh phẩm, không để sót bệnh phẩm.;;;s_answer;;;B.  Lấy vừa đủ block.;;;s_answer;;;C. Lấy khoảng 1 cm3.;;;s_answer;;;D.  Tùy thuộc vào người thực hiện.', 'A. Lấy toàn bộ bệnh phẩm, không để sót bệnh phẩm.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(439, 'text', 'GPBL', 'Đối với bệnh phẩm nạo lòng tử cung sau sảy thai, khi quan sát đại thể cần tìm và mô tả cấu trúc nào dưới đây? Chọn câu đúng nhất.', '', 'A. Màng nhau;;;s_answer;;;B. Lông nhau và cục máu đông.;;;s_answer;;;C. Màng rụng;;;s_answer;;;D. Tất cả đều sai', 'B. Lông nhau và cục máu đông.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(440, 'text', 'GPBL', 'Đối với bệnh phẩm cắt bỏ tử cung do ung thư cổ tử cung, cắt lọc cổ tử cung như thế nào? Chọn câu đúng nhất.', '', 'A. Cắt 1 – 3 lát đại diện cho u.;;;s_answer;;;B. Toàn bộ mẫu phải được cắt lát từ 12 giờ tới 3 giờ, từ 3 giờ tới 6 giờ, từ 6 giờ tới 9 giờ, từ 9 giờ tới 12 giờ và vùi nến phân biệt.;;;s_answer;;;C. Chỉ cắt chỗ chứa u.;;;s_answer;;;D. Cắt 1 mẫu chứa u và 1 mẫu ngoài u để so sánh.', 'B. Toàn bộ mẫu phải được cắt lát từ 12 giờ tới 3 giờ, từ 3 giờ tới 6 giờ, từ 6 giờ tới 9 giờ, từ 9 giờ tới 12 giờ và vùi nến phân biệt.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(441, 'text', 'GPBL', 'Cắt lọc bệnh phẩm cắt bỏ tử cung do ung thư cổ tử cung cần cắt mẫu gì để đánh giá mức độ xâm lấn của u cổ tử cung? Chọn câu đúng nhất.', '', 'A. Eo tử cung;;;s_answer;;;B. Túi cùng âm đạo;;;s_answer;;;C. Chu cung;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(442, 'text', 'GPBL', 'Nguyên tắc cắt lọc bệnh phẩm cắt bỏ tử cung do ung thư cổ tử cung là gì? Chọn câu đúng nhất.', '', 'A. Không để sót tổn thương, các mảnh cắt phải đại diện cho tổn thương, phải lấy được rìa diện cắt, hạch (nếu có).;;;s_answer;;;B. Khi có nhiều tổn thương có thể chọn một tổn thương cắt lọc đại diện.;;;s_answer;;;C. Không nhất thiết phải lấy rìa diện cắt.;;;s_answer;;;D. Tất cả đều sai.', 'A. Không để sót tổn thương, các mảnh cắt phải đại diện cho tổn thương, phải lấy được rìa diện cắt, hạch (nếu có).', 1, 1, NULL, 1596183283, 1596183283, NULL),
(443, 'text', 'GPBL', 'Trong cắt lọc bệnh phẩm cắt bỏ tử cung do ung thư cổ tử cung, mỗi lát cắt cổ tử cung cần đảm bảo yêu cầu nào dưới đây? Chọn câu đúng nhất.', '', 'A. Mỗi lát cắt phải chứa biểu mô cổ ngoài và vùng chuyển tiếp gai trụ.;;;s_answer;;;B.  Mỗi lát cắt chỉ cần chứa biểu mô cổ ngoài.;;;s_answer;;;C. Mỗi lát cắt chỉ cần chứa biểu mô cổ ngoài;;;s_answer;;;D. Mỗi lát cắt phải có nang Naboth.', 'A. Mỗi lát cắt phải chứa biểu mô cổ ngoài và vùng chuyển tiếp gai trụ.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(444, 'text', 'GPBL', 'Mô tả đại thể hạch trong cắt lọc bệnh phẩm cắt bỏ tử cung do ung thư cổ tử cung? Chọn câu đúng nhất.', '', 'A. Số lượng;;;s_answer;;;B. Mật độ;;;s_answer;;;C. Kích thước;;;s_answer;;;D. Số lượng, hình thái bên ngoài, có nghi ngờ bị di căn không.', 'D. Số lượng, hình thái bên ngoài, có nghi ngờ bị di căn không.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(445, 'text', 'GPBL', 'Thành phần nào quan trọng trong mô tả đại thể u buồng trứng góp phần gợi ý chẩn đoán? Chọn câu đúng.', '', 'A. Hình dạng;;;s_answer;;;B. Mặt ngoài u nhẵn hay có nhú hoặc dính, u đặc hay dạng nang, có chồi hay vách.;;;s_answer;;;C. Cân nặng;;;s_answer;;;D.  Kích thước.', 'B. Mặt ngoài u nhẵn hay có nhú hoặc dính, u đặc hay dạng nang, có chồi hay vách.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(446, 'text', 'GPBL', 'Thành phần nào quan trọng trong mô tả đại thể u nang buồng trứng góp phần gợi ý chẩn đoán? Chọn câu đúng.', '', 'A. Thành u nang dày hay mỏng, chất chứa trong u nang, mặt trong vách u nang nhẵn hay sù sì, có chảy máu hay hoại tử hoặc calci hoá không.;;;s_answer;;;B. Kích thước;;;s_answer;;;C. Hình dạng;;;s_answer;;;D. Cân nặng', 'A. Thành u nang dày hay mỏng, chất chứa trong u nang, mặt trong vách u nang nhẵn hay sù sì, có chảy máu hay hoại tử hoặc calci hoá không.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(447, 'text', 'GPBL', 'Cách cắt lọc bệnh phẩm cắt bỏ buồng trứng do u buồng trứng như thế nào? Chọn câu đúng nhất.', '', 'A. Cắt 1 lát tiếp tuyến qua đường kính lớn nhất và cuống.;;;s_answer;;;B. Cắt 3 lát ở u nang, nhất là vùng có nhú.;;;s_answer;;;C. Cắt 3 lát hoặc mỗi lát cắt cho mỗi cm nếu khối u lớn, nếu thấy được mô buồng trúng không có u cắt thêm 1 lát.;;;s_answer;;;D. Cắt 1 lát theo đường kính nhỏ nhất.', 'C. Cắt 3 lát hoặc mỗi lát cắt cho mỗi cm nếu khối u lớn, nếu thấy được mô buồng trúng không có u cắt thêm 1 lát.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(448, 'text', 'GPBL', 'Cách cắt lọc bệnh phẩm cắt bỏ u nang buồng trứng như thế nào? Chọn câu đúng nhất.', '', 'A. Cắt 1 lát tiếp tuyến qua đường kính lớn nhất và cuống.;;;s_answer;;;B. Cắt 3 lát ở u nang, nhất là vùng có nhú hay vách dày.;;;s_answer;;;C. Cắt 3 lát hoặc mỗi lát cắt cho mỗi cm nếu khối u lớn, nếu thấy được mô buồng trúng không có u cắt thêm 1 lát.;;;s_answer;;;D. Cắt 1 lát theo đường kính nhỏ nhất.', 'B. Cắt 3 lát ở u nang, nhất là vùng có nhú hay vách dày.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(449, 'text', 'GPBL', 'Cách cắt lọc bệnh phẩm cắt bỏ buồng trứng ngẫu nhiên như thế nào? Chọn câu đúng nhất.', '', 'A. Cắt 1 lát tiếp tuyến qua đường kính lớn nhất và cuống của buồng trứng.;;;s_answer;;;B. Cắt 3 lát ở u nang;;;s_answer;;;C. Cắt 3 lát hoặc mỗi lát cắt cho mỗi cm nếu khối u lớn, nếu thấy được mô buồng trúng không có u cắt thêm 1 lát.;;;s_answer;;;D. Cắt 1 lát theo đường kính nhỏ nhất.', 'A. Cắt 1 lát tiếp tuyến qua đường kính lớn nhất và cuống của buồng trứng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(450, 'text', 'GPBL', 'Đối với bệnh phẩm buồng trứng các phương pháp mổ thường gặp là? Chọn câu đúng.', '', 'A. Cắt bỏ buồng trứng do u buồng trứng.;;;s_answer;;;B. Cắt bỏ u nang buồng trứng.;;;s_answer;;;C. Cắt bỏ buồng trứng ngẫu nhiên có thể kèm ống dẫn trứng.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(451, 'text', 'GPBL', 'Nguyên tắc cắt lọc bệnh phẩm để xét nghiệm thụ thể hormon là gì, chọn câu đúng?', '', 'A. Tốt nhất là lấy mẫu mô tươi, giữ lạnh sâu bệnh phẩm ở trạng thái đông cho tới khi mẫu được mang đến một phòng xét nghiệm phù hợp hoặc phải cố định ngay toàn bộ bệnh phẩm trong formol đệm trung tính 10% trước khi đưa về phòng xét nghiệm.;;;s_answer;;;B. Tốt nhất là mẫu vùi nến.;;;s_answer;;;C. Tốt nhất là lấy mẫu mô tươi, bảo quản trong dung dịch glutaraldehyde 2,5% với chất đệm Millonig''s photphat, 3 - 4ml một lọ, giữ lạnh ở 40C.;;;s_answer;;;D. Tất cả đều sai.', 'A. Tốt nhất là lấy mẫu mô tươi, giữ lạnh sâu bệnh phẩm ở trạng thái đông cho tới khi mẫu được mang đến một phòng xét nghiệm phù hợp hoặc phải cố định ngay toàn bộ bệnh phẩm trong formol đệm trung tính 10% trước khi đưa về phòng xét nghiệm.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(452, 'text', 'GPBL', 'Điểm đặc biệt trong cắt lọc bệnh phẩm để xét nghiệm thụ thể hormon là gì?', '', 'A. Băm nhỏ các lát cắt thành những khối 1mm3 bằng lưỡi dao sắc và nhúng bệnh phẩm vào dung dịch glutaraldehyde 2,5% lạnh 40C.;;;s_answer;;;B. Lấy một mẫu mô khác ở ngay cạnh chỗ cũ để làm xét nghiệm mô bệnh học (với mục đích kiểm chứng) và xem như là một mẫu tương đồng.;;;s_answer;;;C. Bệnh phẩm bảo quản trong môi trường nuôi cấy.;;;s_answer;;;D. Tất cả đều sai.', 'B. Lấy một mẫu mô khác ở ngay cạnh chỗ cũ để làm xét nghiệm mô bệnh học (với mục đích kiểm chứng) và xem như là một mẫu tương đồng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(453, 'text', 'GPBL', 'Nguyên tắc trong phẫu tích bệnh phẩm vú (sinh thiết và/hoặc cắt bỏ rộng đối với các u sờ được):', '', 'A. Không để sót tổn thương;;;s_answer;;;B. Các mảnh cắt phải đại diện cho tổn thương;;;s_answer;;;C. Phải lấy được rìa diện cắt, hạch (nếu có);;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(454, 'text', 'GPBL', 'Số lượng hạch trong phẫu tích bệnh phẩm vú toàn phần:', '', 'A. Tối thiểu 20 hạch;;;s_answer;;;B. Tối thiếu 15 hạch;;;s_answer;;;C. Tối thiểu 10 hạch;;;s_answer;;;D. Không bắt buộc số lượng', 'A. Tối thiểu 20 hạch', 1, 1, NULL, 1596183283, 1596183283, NULL),
(455, 'text', 'GPBL', 'Cắt lọc bệnh phẩm núm vú:', '', 'A. Cắt lát ngang núm vú;;;s_answer;;;B. Cắt lát dọc từ trên xuống;;;s_answer;;;C. Cắt toàn bộ núm vú;;;s_answer;;;D. Không cần khảo sát núm vú', 'B. Cắt lát dọc từ trên xuống', 1, 1, NULL, 1596183283, 1596183283, NULL),
(456, 'text', 'GPBL', 'Cắt lọc bệnh phẩm hạch:', '', 'A. Chỉ khảo sát những hạch lớn;;;s_answer;;;B. Không cần khảo sát hạch;;;s_answer;;;C. Lấy tất cả các hạch;;;s_answer;;;D. Chỉ cần khảo sát 1 hạch', 'C. Lấy tất cả các hạch', 1, 1, NULL, 1596183283, 1596183283, NULL),
(457, 'text', 'GPBL', 'Chọn câu đúng nhất. Cố định bệnh phẩm:', '', 'A. Không quá 30 phút kể từ khi lấy bệnh phẩm ra khỏi cơ thể;;;s_answer;;;B. Không quá 20 phút kể từ khi lấy bệnh phẩm ra khỏi cơ thể;;;s_answer;;;C. Không quá 10 phút kể từ khi lấy bệnh phẩm ra khỏi cơ thể;;;s_answer;;;D. Phải cố định ngay khi lấy bệnh phẩm ra khỏi cơ thể', 'A. Không quá 30 phút kể từ khi lấy bệnh phẩm ra khỏi cơ thể', 1, 1, NULL, 1596183283, 1596183283, NULL),
(458, 'text', 'GPBL', 'Cắt lọc mô vú:', '', 'A. Cắt 3 mẫu ở mô u;;;s_answer;;;B. Lấy tối thiểu mẫu ở mỗi 1/4 theo thứ tự: 1/4 trên ngoài, 1/4 dưới ngoài, 1/4 dưới trong, 1/4 trên trong;;;s_answer;;;C. Lấy mẫu tất cả các tổn thương thấy được trên đại thể hoặc X-quang;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(459, 'text', 'GPBL', 'Người thực hiện cắt lọc:', '', 'A. Bác sĩ giải phẫu bệnh - tế bào học;;;s_answer;;;B. Bác sĩ giải phẫu bệnh - tế bào học và kỹ thuật viên giải phẫu bệnh - tế bào học;;;s_answer;;;C. Kỹ thuật viên giải phẫu bệnh - tế bào học;;;s_answer;;;D. Sinh viên', 'B. Bác sĩ giải phẫu bệnh - tế bào học và kỹ thuật viên giải phẫu bệnh - tế bào học', 1, 1, NULL, 1596183283, 1596183283, NULL),
(460, 'text', 'GPBL', 'Các phần cần khảo sát khi phân tích bệnh phẩm bánh nhau (đơn thai):', '', 'A. Màng ối và bánh nhau;;;s_answer;;;B. Màng ối và dây rốn;;;s_answer;;;C. Dây rốn và bánh nhau;;;s_answer;;;D. Dây rốn, bánh nhau, màng nhau', 'D. Dây rốn, bánh nhau, màng nhau', 1, 1, NULL, 1596183283, 1596183283, NULL),
(461, 'text', 'GPBL', 'Vị trí cần khảo sát của dây rốn:', '', 'A. Cách vị trí bám vào bánh nhau 3cm;;;s_answer;;;B. Cách vị trí bám vào bánh nhau 1cm;;;s_answer;;;C. Cách vị trí bám vào bánh nhau 5cm;;;s_answer;;;D. Cách vị trí bám vào bánh nhau 7cm', 'A. Cách vị trí bám vào bánh nhau 3cm', 1, 1, NULL, 1596183283, 1596183283, NULL),
(462, 'text', 'GPBL', 'Số lượng mẫu bánh nhau (đơn thai) cần khảo sát:', '', 'A. 2 mẫu;;;s_answer;;;B. 3 mẫu;;;s_answer;;;C. 4 mẫu;;;s_answer;;;D. 5 mẫu', 'C. 4 mẫu', 1, 1, NULL, 1596183283, 1596183283, NULL),
