(481, 'text', 'GPBL', 'Để tránh nhầm lẫn mẫu Pap’s cần phải :', '', 'A. Có sổ giao - nhận  hằng ngày;;;s_answer;;;B. Kiểm tra sự đồng thuận trên lam phết và phiếu chỉ định xét nghiệm pap’s;;;s_answer;;;C. Báo cáo ngay các trường hợp không phù hợp;;;s_answer;;;D. Tất cả các câu trên', 'D. Tất cả các câu trên', 1, 1, NULL, 1596183283, 1596183283, NULL),
(482, 'text', 'GPBL', 'Kết quả Pap’s không tin cậy có thể là:', '', 'A. Nhân sự tầm soát  Pap’s  qua đào tạo không  bài bản;;;s_answer;;;B. Đọc quá nhiều tiêu bản trong ngày;;;s_answer;;;C. Đang bị stress, cảm cúm hoặc không tập trung ;;;s_answer;;;D. Tất cả các câu trên', 'D. Tất cả các câu trên', 1, 1, NULL, 1596183283, 1596183283, NULL),
(483, 'text', 'GPBL', 'Vi nấm gây huyết trắng bệnh lý thường gặp là:', '', 'A. Microsporum;;;s_answer;;;B. Trichopyton;;;s_answer;;;C. Penicilium;;;s_answer;;;D. Candida  albicans', 'D. Candida  albicans', 1, 1, NULL, 1596183283, 1596183283, NULL),
