(488, 'text', 'GPBL', 'Dung dịch nào sau đây thường được dùng để cố định bệnh phẩm ngay sau khi được lấy ra khỏi cơ thể:', '', 'A. Dung dịch formol đệm trung tính 10%;;;s_answer;;;B. Dung dịch hematoxylin;;;s_answer;;;C. Dung dịch HCl 30%;;;s_answer;;;D. Dung dịch formol nguyên chất', 'A. Dung dịch formol đệm trung tính 10%', 1, 1, NULL, 1596183283, 1596183283, NULL),
(489, 'text', 'GPBL', 'Chất nào sau đây KHÔNG phải là thành phần của dung dịch khử calci trong xương:', '', 'A. Acid formic nguyên chất;;;s_answer;;;B. Acid chlohydric;;;s_answer;;;C. Nước cất;;;s_answer;;;D. Ethanol nguyên chất', 'D. Ethanol nguyên chất', 1, 1, NULL, 1596183283, 1596183283, NULL),
(490, 'text', 'GPBL', 'Chọn câu đúng nhất:', '', 'A. Để khử calci, bệnh phẩm xương cần được cắt nhỏ thành các lát mỏng.;;;s_answer;;;B. Có thể phẫu tích bệnh phẩm tươi hoặc đã qua cố định.;;;s_answer;;;C. Thể tích dung dịch cố định cần dùng nhiều gấp 20-30 lần thể tích bệnh phẩm.;;;s_answer;;;D. Cả A, B, C đều đúng.', 'D. Cả A, B, C đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
