(492, 'text', 'GPBL', 'Chọn câu SAI:', '', 'A. Khối parafin sau khi đúc phải đạt độ cứng đồng đều, không có viền trắng quanh bệnh phẩm, không có khoảng trống giữa bệnh phẩm và parafin.;;;s_answer;;;B. Khi đúc khối cần đặt bệnh phẩm sát mặt đáy, định hướng đúng chiều bệnh phẩm.;;;s_answer;;;C. Khi đúc khối có thể đặt bệnh phẩm ở chiều bất kỳ vì mặt cắt cho ra là như nhau.;;;s_answer;;;D. Bệnh phẩm trước khi đúc khối cần trải qua quá trình vùi trong parafin đủ thời gian.', 'C. Khi đúc khối có thể đặt bệnh phẩm ở chiều bất kỳ vì mặt cắt cho ra là như nhau.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(493, 'text', 'GPBL', 'Khi cắt mỏng, cần cắt bệnh phẩm thành các lát cắt có độ dày:', '', 'A. 1-2 µm;;;s_answer;;;B. 3-4 µm;;;s_answer;;;C. 5-6 µm;;;s_answer;;;D. 7-8 µm', 'B. 3-4 µm', 1, 1, NULL, 1596183283, 1596183283, NULL),
(494, 'text', 'GPBL', 'Hóa chất nào sau đây không được dùng trong quy trình nhuộm HE:', '', 'A. Giemsa;;;s_answer;;;B. Ethanol;;;s_answer;;;C. Xylen hoặc toluen;;;s_answer;;;D. Hematoxylin', 'A. Giemsa', 1, 1, NULL, 1596183283, 1596183283, NULL),
(495, 'text', 'GPBL', 'Nguyên lý nhuộm PAS:', '', 'A. Dùng tác nhân oxy hóa là acid nitrite để phá vỡ mối liên kết của 2 nguyên tử carbon.;;;s_answer;;;B. Dùng tác nhân oxy hóa là acid formic để phá vỡ mối liên kết của 2 nguyên tử carbon.;;;s_answer;;;C. Dùng tác nhân oxy hóa là acid sulfuric để phá vỡ mối liên kết của 2 nguyên tử carbon.;;;s_answer;;;D. Dùng tác nhân oxy hóa là acid periodic để phá vỡ mối liên kết của 2 nguyên tử carbon.', 'D. Dùng tác nhân oxy hóa là acid periodic để phá vỡ mối liên kết của 2 nguyên tử carbon.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(496, 'text', 'GPBL', 'Các thành phần nào sau đây sẽ bắt màu hồng đậm hoặc đỏ khi nhuộm PAS:', '', 'A. Nhân tế bào;;;s_answer;;;B. Bào tương của hồng cầu;;;s_answer;;;C. Chất nhầy;;;s_answer;;;D. Ty thể', 'C. Chất nhầy', 1, 1, NULL, 1596183283, 1596183283, NULL),
(497, 'text', 'GPBL', 'Để phát hiện vi khuẩn Helicobacter pylori cần tiến hành nhuộm:', '', 'A. Giemsa;;;s_answer;;;B. PAS;;;s_answer;;;C. Meyer;;;s_answer;;;D. Trichrome', 'A. Giemsa', 1, 1, NULL, 1596183283, 1596183283, NULL),
(498, 'text', 'GPBL', 'Nồng độ dung dịch formol đệm trung tính dùng để cố định bệnh phẩm:', '', 'A. 10%;;;s_answer;;;B. 12%;;;s_answer;;;C. 15%;;;s_answer;;;D. 30%', 'A. 10%', 1, 1, NULL, 1596183283, 1596183283, NULL),
(499, 'text', 'GPBL', 'Tác dụng của dung dịch xylene trong xử lý bệnh phẩm, chọn câu SAI:', '', 'A. Làm “trong sạch” tế bào;;;s_answer;;;B. Giảm tác dụng độc hại của các hóa chất;;;s_answer;;;C. Hòa tan ethanol;;;s_answer;;;D. Hòa tan parafin', 'H.  Giảm tác dụng độc hại của các hóa chất', 1, 1, NULL, 1596183283, 1596183283, NULL),
(500, 'text', 'GPBL', 'Tác dụng của dung dịch hematoxylin:', '', 'A. Nhuộm các thành phần ái kiềm trong tế bào;;;s_answer;;;B. Nhuộm các thành phần ái toan trong tế bào;;;s_answer;;;C. Nhuộm các thành phần trung tính trong tế bào;;;s_answer;;;D. Nhuộm màng đáy của mô', 'A. Nhuộm các thành phần ái kiềm trong tế bào', 1, 1, NULL, 1596183283, 1596183283, NULL),
