(503, 'text', 'GPBL', 'Trong kỹ thuật nhuộm Papanicolaou, chất keratin bắt màu:', '', 'A. Xanh tím;;;s_answer;;;B. Xanh lá;;;s_answer;;;C. Cam;;;s_answer;;;D. Không bắt màu', 'C. Cam', 1, 1, NULL, 1596183283, 1596183283, NULL),
(504, 'text', 'GPBL', 'Cần chú ý điều gì khi dán lamen:', '', 'A. Cần dán lamen trước khi tiến hành nhuộm bệnh phẩm;;;s_answer;;;B. Lamen sử dụng không được to hơn kích thước bệnh phẩm;;;s_answer;;;C. Cần dán lamen ngay sau khi vớt mảnh mô từ bồn nước nóng lúc cắt mỏng;;;s_answer;;;D. Không được để xuất hiện bọt khí giữa lamen và mảnh mô', 'D. Không được để xuất hiện bọt khí giữa lamen và mảnh mô', 1, 1, NULL, 1596183283, 1596183283, NULL),
(505, 'text', 'GPBL', 'Với phương pháp nhuộm Papanicolaou, tiêu bản tế bào học cần được cố định ngay lập tức trong:', '', 'A. Methanol 10%;;;s_answer;;;B. Formol đệm trung tính 10%;;;s_answer;;;C. Formol đệm trung tính 95%;;;s_answer;;;D. Ethanol 95%', 'D. Ethanol 95%', 1, 1, NULL, 1596183283, 1596183283, NULL),
(506, 'text', 'GPBL', 'Trong kỹ thuật lấy bệnh phẩm làm phiến đồ cổ tử cung, âm đạo, khâu chuẩn bị người bệnh cần:', '', 'A. Không trong ngày có kinh nguyệt;;;s_answer;;;B. Kiêng giao hợp ít nhất 3 ngày;;;s_answer;;;C. Không rửa âm đạo trong 24h trước đó;;;s_answer;;;D. Cả A, B, C đều đúng', 'D. Cả A, B, C đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(507, 'text', 'GPBL', 'Kỹ thuật chọc hút tế bào bằng kim nhỏ (FNA) thường KHÔNG sử dụng cho cơ quan nào sau đây:', '', 'A. Vú;;;s_answer;;;B. Tim;;;s_answer;;;C. Hạch;;;s_answer;;;D. Tuyến giáp', 'B. Tim', 1, 1, NULL, 1596183283, 1596183283, NULL),
(508, 'text', 'GPBL', 'Kỹ thuật chọc hút tế bào bằng kim nhỏ (FNA) có những ưu điểm sau, NGOẠI TRỪ:', '', 'A. An toàn, rất ít biến chứng;;;s_answer;;;B. Ít tốn thời gian, tiền bạc;;;s_answer;;;C. Có độ chính xác cao;;;s_answer;;;D. Thay thế hoàn toàn kỹ thuật mô bệnh học thường quy', 'D. Thay thế hoàn toàn kỹ thuật mô bệnh học thường quy', 1, 1, NULL, 1596183283, 1596183283, NULL),
(509, 'text', 'GPBL', 'Khẩu kính kim nhỏ thường được sử dụng trong kỹ thuật chọc hút tế bào bằng kim nhỏ (FNA):', '', 'A. 18-20G;;;s_answer;;;B. 22-27G;;;s_answer;;;C. 16-18G;;;s_answer;;;D. >16G', 'B. 22-27G', 1, 1, NULL, 1596183283, 1596183283, NULL),
(510, 'text', 'GPBL', 'Chống chỉ định tuyệt đối của kỹ thuật chọc hút tế bào bằng kim nhỏ (FNA):', '', 'A. Hầu như không có chống chỉ định tuyệt đối;;;s_answer;;;B. U thể cảnh và u sắc bào;;;s_answer;;;C. U nang buồng trứng;;;s_answer;;;D. Cơ địa dễ chảy máu, u nằm sâu', 'A. Hầu như không có chống chỉ định tuyệt đối', 1, 1, NULL, 1596183283, 1596183283, NULL),
(511, 'text', 'GPBL', 'Tai biến thường gặp nhất khi thực hiện kỹ thuật chọc hút tế bào bằng kim nhỏ (FNA):', '', 'A. Nhiễm trùng tại vị trí chọc hút;;;s_answer;;;B. Tràn khí màng phổi, xuất huyết trong phổi;;;s_answer;;;C. Tụ máu tại vị trí chọc hút;;;s_answer;;;D. Viêm phúc mạc, thủng ruột', 'C. Tụ máu tại vị trí chọc hút', 1, 1, NULL, 1596183283, 1596183283, NULL),
(512, 'text', 'GPBL', 'Trong kỹ thuật chọc hút tế bào bằng kim nhỏ (FNA), đối với các u nằm sâu hoặc không sờ thấy được, cần thực hiện dưới hướng dẫn của:', '', 'A. MRI;;;s_answer;;;B. X-quang thẳng/nghiêng;;;s_answer;;;C. Siêu âm hoặc CT-scan;;;s_answer;;;D. Nội soi', 'C. Siêu âm hoặc CT-scan', 1, 1, NULL, 1596183283, 1596183283, NULL),
(513, 'text', 'GPBL', 'Với các bệnh phẩm sinh thiết lõi kim, để chẩn đoán mô bệnh học, cần sử dụng:', '', 'A. Toàn bộ      ;;;s_answer;;;B. Một mẫu bất kỳ     ;;;s_answer;;;C. Một mẫu nghi bệnh   ;;;s_answer;;;D. Hai mẫu bất kỳ', 'A. Toàn bộ', 1, 1, NULL, 1596183283, 1596183283, NULL),
(514, 'text', 'GPBL', 'Với các bệnh phẩm sinh thiết lõi kim, bệnh phẩm cần được :', '', 'A. Cố định sau 1 tiếng  ;;;s_answer;;;B. Cố định sau 30 phút  ;;;s_answer;;;C. Cố định ngay (không quá 30 phút kể từ khi bệnh phẩm được lấy ra khỏi cơ thể)   ;;;s_answer;;;D. Cố định sau 2 tiếng', 'C. Cố định ngay (không quá 30 phút kể từ khi bệnh phẩm được lấy ra khỏi cơ thể)', 1, 1, NULL, 1596183283, 1596183283, NULL),
(515, 'text', 'GPBL', 'Bệnh phẩm sinh thiết lõi kim do các khoa, phòng lâm sàng gửi tới không được:', '', 'A. Đựng trong dung dịch formol đệm trung tính 10% ;;;s_answer;;;B. Cố định tại chỗ ngay sau khi sinh thiết;;;s_answer;;;C. Gửi ngay tới khoa giải phẫu bệnh;;;s_answer;;;D. Gói bệnh phẩm trong gạc', 'D. Gói bệnh phẩm trong gạc', 1, 1, NULL, 1596183283, 1596183283, NULL),
(516, 'text', 'GPBL', 'Trong nguyên tắc phẫu tích bệnh phẩm âm hộ cần:', '', 'A. Không để sót tổn thương;;;s_answer;;;B. Các mảnh cắt phải đại diện cho tổn thương;;;s_answer;;;C. Phải lấy được rìa diện cắt, hạch (nếu  có);;;s_answer;;;D. Cả A,B,C đều đúng', 'D. Cả A,B,C đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(517, 'text', 'GPBL', 'Trong mô tả đại thể bệnh phẩm âm hộ cần:', '', 'A. Loại phẫu thuật: đơn giản, dưới da, tận gốc; các nhóm hạch..;;;s_answer;;;B. Hạch: kích thước hạch lớn nhất bị di căn.;;;s_answer;;;C. Hình thái vùng không u: teo, sừng hóa, loét.;;;s_answer;;;D. Cả A,B,C đều đúng', 'D. Cả A,B,C đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(518, 'text', 'GPBL', 'Đối với bệnh phẩm từ phẫu thuật cắt âm hộ tận gốc:', '', 'A. Cần phân biệt các nhóm hạch và cố định riêng rẽ.;;;s_answer;;;B. Không cần phân biệt các nhóm hạch và cố định riêng rẽ. ;;;s_answer;;;C. Không cần lấy hạch;;;s_answer;;;D. Chỉ cần lấy 1 hạch đại diện', 'A. Cần phân biệt các nhóm hạch và cố định riêng rẽ.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(519, 'text', 'GPBL', 'Bệnh phẩm âm hộ chứa:', '', 'A. Toàn bộ tổn thương;;;s_answer;;;B. Bờ diện cắt;;;s_answer;;;C. Hạch (nếu có);;;s_answer;;;D. Cả A,B,C đều đúng', 'D. Cả A,B,C đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(520, 'text', 'GPBL', 'Với bệnh phẩm tinh hoàn:', '', 'A. Vùng u lấy 1 lát cắt;;;s_answer;;;B. Vùng u lấy 2 lát cắt ;;;s_answer;;;C. Vùng u lấy nhiều nhất 3 lát cắt;;;s_answer;;;D. Vùng u lấy ít nhất 3 lát cắt hoặc 1 lát cắt cho mỗi cm u', 'D. Vùng u lấy ít nhất 3 lát cắt hoặc 1 lát cắt cho mỗi cm u', 1, 1, NULL, 1596183283, 1596183283, NULL),
(521, 'text', 'GPBL', 'Các lát cắt chứa u phải:', '', 'A. Không được chứa mô tinh hoàn không u;;;s_answer;;;B. Không được có vỏ bao tinh hoàn;;;s_answer;;;C. Có vùng chảy máu, hoại tử và đặc;;;s_answer;;;D. Tất cả A, B, C đều sai', 'C. Có vùng chảy máu, hoại tử và đặc', 1, 1, NULL, 1596183283, 1596183283, NULL),
(522, 'text', 'GPBL', 'Cắt lọc bệnh phẩm làm xét nghiệm mô bệnh học trong phẫu tích bệnh phẩm tinh hoàn, câu nào sau đây SAI:', '', 'A. Vùng tinh hoàn ngoài u: 2 lát cắt.;;;s_answer;;;B. Mào tinh hoàn không cần lấy mẫu.;;;s_answer;;;C. Thừng tinh và mô xung quanh cách tinh hoàn 1cm: 1 lát cắt ngang.;;;s_answer;;;D. Thừng tinh và mô xung quanh ở bờ phẫu thuật (diện cắt): 1 lát cắt ngang.', 'B. Mào tinh hoàn không cần lấy mẫu.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(523, 'text', 'GPBL', 'Với bệnh phẩm sinh thiết tinh hoàn:', '', 'A. Dùng kẹp có mấu kẹp chặt vì bệnh phẩm nhỏ dể trôi mất.;;;s_answer;;;B. Gói trong gạc trắng để dễ thấy vì mấu sinh thiết rất nhỏ.;;;s_answer;;;C. Không dùng kẹp có mấu kẹp chặt làm nát bệnh phẩm.;;;s_answer;;;D. Không được dùng kẹp lấy bệnh phẩm.', 'C. Không dùng kẹp có mấu kẹp chặt làm nát bệnh phẩm.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(524, 'text', 'GPBL', 'Trong nguyên tắc phẫu tích bệnh phẩm sinh thiết hạch :', '', 'A. Lấy vài hạch để đại diện;;;s_answer;;;B. Phải lấy cả mô kế cận (nếu có);;;s_answer;;;C. Bệnh phẩm sau khi pha không cần phải cố định ngay.;;;s_answer;;;D. Cả A, B, C đều sai.', 'B. Phải lấy cả mô kế cận (nếu có)', 1, 1, NULL, 1596183283, 1596183283, NULL),
(525, 'text', 'GPBL', 'Trong quy trình chuẩn bị phẫu tích bệnh phẩm sinh thiết hạch, đối với hạch còn tươi:', '', 'A. Cắt 2 -3 lát song song với trục dọc;;;s_answer;;;B. Không cần lấy mẫu để cấy vi khuẩn trong mọi trường hợp;;;s_answer;;;C. 4 phiến kính áp hạch, cố định trong formol đệm trung tính 10%.;;;s_answer;;;D. Trường hợp nghi bệnh lý u lymphô: để dành phiến đồ cho phương pháp đo tế bào dòng chảy, di truyền tế bào, di truyền học phân tử…', 'D. Trường hợp nghi bệnh lý u lymphô: để dành phiến đồ cho phương pháp đo tế bào dòng chảy, di truyền tế bào, di truyền học phân tử…', 1, 1, NULL, 1596183283, 1596183283, NULL),
(526, 'text', 'GPBL', 'Khi mô tả đại thể bệnh phẩm sinh thiết hạch CẦN ghi nhận:', '', 'A. Hạch tươi hay đã cố định.;;;s_answer;;;B. Kích thước hạch và tình trạng vỏ hạch.;;;s_answer;;;C. Mặt cắt, màu sắc, dạng nốt, chảy máu, hoại tử.;;;s_answer;;;D. Cả A,B,C đều đúng', 'D. Cả A,B,C đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(527, 'text', 'GPBL', 'Cắt lọc bệnh phẩm sinh thiết hạch làm xét nghiệm mô bệnh học CẦN:', '', 'A. Cắt dọc hạch;;;s_answer;;;B. Bóc tách vỏ bao;;;s_answer;;;C. Lấy 1 đến 3 lát tùy kích thước hạch;;;s_answer;;;D. Lấy 1 lát bất kể kích thước hạch', 'C. Lấy 1 đến 3 lát tùy kích thước hạch', 1, 1, NULL, 1596183283, 1596183283, NULL),
(528, 'text', 'GPBL', 'Mối chỉ đánh dấu được gửi kèm mẫu bệnh phẩm cắt chóp cổ tử cung nằm ở vị trí nào?', '', 'A. 12h;;;s_answer;;;B. 3h;;;s_answer;;;C. 6h;;;s_answer;;;D. 9h', 'A. 12h', 1, 1, NULL, 1596183283, 1596183283, NULL),
(529, 'text', 'GPBL', 'Bệnh phẩm cắt chóp cổ tử cung được chia thành mấy phần?', '', 'A. 1;;;s_answer;;;B. 2;;;s_answer;;;C. 3;;;s_answer;;;D. 4', 'D. 4', 1, 1, NULL, 1596183283, 1596183283, NULL),
(530, 'text', 'GPBL', 'Cắt lọc khảo sát vi thể bệnh phẩm cắt chóp cổ tử cung cần lấy các lát cắt nào?', '', 'A. Chỉ cần lấy các lát chứa tổn thương cổ tử cung.;;;s_answer;;;B. Vùi nến toàn bộ các mẫu mô.;;;s_answer;;;C. A và B đều sai.;;;s_answer;;;D. A và B đều đúng.', 'B. Vùi nến toàn bộ các mẫu mô.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(531, 'text', 'GPBL', 'Bệnh phẩm cắt chóp cổ tử cung được đánh dấu mực tàu ở vị trí nào?', '', 'A. Cổ trong cổ tử cung.;;;s_answer;;;B. Cổ ngoài cổ tử cung.;;;s_answer;;;C. Đánh dấu toàn bộ diện cắt bằng mực tàu.;;;s_answer;;;D. Không cần đánh dấu mực tàu ở bệnh phẩm cắt chóp cổ tử cung.', 'C. Đánh dấu toàn bộ diện cắt bằng mực tàu.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(532, 'text', 'GPBL', 'Yêu cầu của các mẫu cắt khảo sát vi thể bệnh phẩm cắt chóp cổ tử cung?', '', 'A. Mỗi lát cắt phải chứa biểu mô và vùng chuyển tiếp cổ trong và cổ ngoài.;;;s_answer;;;B. Ở mỗi phần chỉ cần lấy 1 mẫu chứa biểu mô và vùng chuyển tiếp.;;;s_answer;;;C. Cắt theo thứ tự từng phần: 12h-3h, 3h-6h, 6h-9h, 9h-12h.;;;s_answer;;;D. Cả A và C.', 'D. Cả A và C.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(533, 'text', 'GPBL', 'Số lượng lát cắt phải lấy ít nhất bao nhiêu ở bệnh phẩm cắt tử cung do quá sản hoặc ung thư nội mạc tử cung?', '', 'A. Ít nhất 3 lát cắt.;;;s_answer;;;B. Lấy mẫu toàn bộ nội mạc tử cung.;;;s_answer;;;C. Tùy vào có thấy hay không thấy u rõ rệt.;;;s_answer;;;D. Chỉ cần lấy 1-2 lát đại diện u.', 'C. Tùy vào có thấy hay không thấy u rõ rệt.', 1, 1, NULL, 1596183283, 1596183283, NULL);
INSERT INTO `qandas` (`id`, `type_question`, `knowledge`, `question`, `image`, `answer`, `right_answer`, `created_by`, `updated_by`, `deleted_by`, `created_at`, `updated_at`, `deleted_at`) VALUES
(534, 'text', 'GPBL', 'Ở bệnh phẩm cắt tử cung do quá sản hoặc ung thư nội mạc tử cung, khi nhìn rõ u thì lấy những lát nào?', '', 'A. Ít nhất 3 lát.;;;s_answer;;;B. Có 1 lát ở phần xâm lấn sâu nhất; mỗi lát phải chứa toàn bộ thành tử cung, từ nội mạc đến thanh mạc; nếu quá dày thì có thể phân mỗi lát thành 2 nửa có đánh số để có thể đặt vừa vào trong khuôn nhựa đựng mô.;;;s_answer;;;C. Có 2 lát cắt ở vùng nội mạc tử cung ngoài u, không cần phải chứa toàn bộ thành tử cung.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(535, 'text', 'GPBL', 'Ở bệnh phẩm cắt tử cung do quá sản hoặc ung thư nội mạc tử cung, khi không thể nhìn rõ u thì lấy những lát nào?', '', 'A. Lấy mẫu toàn bộ nội mạc tử cung bằng những lát cắt ngang song song, dày 2- 3mm của 2 nửa tử cung.;;;s_answer;;;B. Có 1 lát cắt chứa toàn bộ thành tử cung, từ nội mạc tử cung tới thanh mạc, những lát khác thì gọt bỏ 2/3 ngoài lớp cơ tử cung.;;;s_answer;;;C. Đánh số thứ tự và phân biệt nửa trước hoặc nửa sau tử cung.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(536, 'text', 'GPBL', 'Mô tả đại thể của u ở bệnh phẩm cắt tử cung do quá sản hoặc ung thư nội mạc tử cung:', '', 'A. Vị trí, kích thước, hình thái (đặc, nhú, hoại tử,chảy máu), màu sắc.;;;s_answer;;;B. Độ lan rộng trong nội mạc.;;;s_answer;;;C. Mức độ xâm lấn vào thanh mạc, dây chằng rộng, mạch máu, cổ tử cung hoặc vòi trứng.;;;s_answer;;;D. Tất cả đều đúng.', 'D. Tất cả đều đúng.', 1, 1, NULL, 1596183283, 1596183283, NULL),
(537, 'text', 'GPBL', 'Số lượng mẫu ít nhất cần lấy từ mô nhau của bệnh phẩm sảy thai?', '', 'A. 4;;;s_answer;;;B. 3;;;s_answer;;;C. 2;;;s_answer;;;D. 1', 'A. 4', 1, 1, NULL, 1596183283, 1596183283, NULL),
(538, 'text', 'GPBL', 'Phết Atrophy thường có đặc điểm:', '', 'A. Hiện diện đầy đủ các loại tế bào;;;s_answer;;;B. Chỉ gồm tế bào cận đáy và đáy;;;s_answer;;;C. Nhiều nhân trần;;;s_answer;;;D. Các câu trên', 'D. Các câu trên', 1, 1, NULL, 1596183283, 1596183283, NULL),
(539, 'text', 'GPBL', '« Clue- cell » là thuật ngữ  diễn đạt nhiễm trùng:', '', 'A. Lậu cầu;;;s_answer;;;B. Trực trùng gram âm;;;s_answer;;;C. Cocci gram dương;;;s_answer;;;D. Garnerella', 'D. Garnerella', 1, 1, NULL, 1596183283, 1596183283, NULL),
(540, 'text', 'GPBL', 'Polyp tăng sản nội mạc tử cung có đặc điểm', '', 'A. Không chịu ảnh hưởng của estrogen;;;s_answer;;;B. Mô đệm tương tự như giai đoạn phát triển;;;s_answer;;;C. Cuống polyp có ít mạch máu;;;s_answer;;;D. Chuyển sản gai là dấu hiệu ác tính', 'B. Mô đệm tương tự như giai đoạn phát triển', 1, 1, NULL, 1596183283, 1596183283, NULL),
(541, 'text', 'GPBL', 'ASC-US theo Bethesda:', '', 'A. Tế bào biến đổi ác tính;;;s_answer;;;B. Tế bào biền đổi lành tính;;;s_answer;;;C. Tế bào bình thường;;;s_answer;;;D. Tế bào gai không điển hình có ý nghĩa không xác định', 'D. Tế bào gai không điển hình có ý nghĩa không xác định', 1, 1, NULL, 1596183283, 1596183283, NULL),
(542, 'text', 'GPBL', 'Loạn sừng có thể gặp trong các trường hợp:', '', 'A. Nhiễm Trichomonas;;;s_answer;;;B. Nhiễm HPV;;;s_answer;;;C. Nhiễm HIV;;;s_answer;;;D. A và B', 'D. A và B', 1, 1, NULL, 1596183283, 1596183283, NULL),
