(548, 'text', 'GPBL', 'Bất thường tế bào biểu mô theo phân loại Bethesda 2014 ?', '', 'A. Tế bào gai;;;s_answer;;;B. Tế bào tuyến;;;s_answer;;;C. Tế bào gai và tế bào tuyến;;;s_answer;;;D. Không câu nào đúng', 'C. Tế bào gai và tế bào tuyến', 1, 1, NULL, 1596183283, 1596183283, NULL),
(549, 'text', 'GPBL', 'Theo ACOG 2016, tầm soát ung thư ở phụ nữ đã chích ngừa HPV:', '', 'A. Không cần tầm soát;;;s_answer;;;B. Tầm soát giống như bình thường;;;s_answer;;;C. Tầm soát 10 năm/lần;;;s_answer;;;D. Không câu nào đúng', 'B. Tầm soát giống như bình thường', 1, 1, NULL, 1596183283, 1596183283, NULL),
(550, 'text', 'GPBL', 'Theo ACOG 2016, tầm soát ung thư cổ tử cung ở phụ nữ từ 21-29 tuổi:', '', 'A. 1 năm/lần;;;s_answer;;;B. 2 năm/lần;;;s_answer;;;C. 3 năm/lần;;;s_answer;;;D. 4 năm /lần', 'C. 3 năm/lần', 1, 1, NULL, 1596183283, 1596183283, NULL),
(551, 'text', 'GPBL', 'Tầm soát ung thư CTC bằng phết Pap được áp dụng cho phụ nữ trong độ tuổi', '', 'A. Dưới 20 ;;;s_answer;;;B. 21-29 ;;;s_answer;;;C. Trên 30 ;;;s_answer;;;D. 21-65', 'D. 21-65', 1, 1, NULL, 1596183283, 1596183283, NULL),
(552, 'text', 'GPBL', 'Loại tế bào có nhân được dùng chuẩn tham chiếu về kích thước và đặc điểm chất nhiễm sắc cho các loại tế bào khác trong phết Pap', '', 'A. Tế bào đáy ;;;s_answer;;;B. Tế bào trung gian;;;s_answer;;;C. Tế bào cận đáy;;;s_answer;;;D. Tế bào bề mặt', 'B. Tế bào trung gian', 1, 1, NULL, 1596183283, 1596183283, NULL),
(553, 'text', 'GPBL', 'Sự hiện diện của tế bào nội mạc trên phết Pap là bất thường khi xảy ra trong', '', 'A. Ngày 6 đến 10 của chu kỳ kinh ;;;s_answer;;;B. Giai đoạn sau mãn kinh ;;;s_answer;;;C. Nữa sau chu kỳ kinh ;;;s_answer;;;D. Tất cả đều đúng', 'B. Giai đoạn sau mãn kinh', 1, 1, NULL, 1596183283, 1596183283, NULL),
(554, 'text', 'GPBL', 'KHÔNG PHẢI đặc điểm của tế bào chuyển sản gai', '', 'A.  Hình dạng kích thước giống tế bào cận đáy hoặc tế bào trung gian;;;s_answer;;;B. Xếp thành đám giống viên đá cuội;;;s_answer;;;C. Nhân có chất nhiễm sắc nhịn;;;s_answer;;;D. Hạch nhân to, chất nhiễm sắc thô', 'D. Hạch nhân to, chất nhiễm sắc thô', 1, 1, NULL, 1596183283, 1596183283, NULL),
(555, 'text', 'GPBL', 'Tế bào giống tế bào bề mặt thu nhỏ, nhân vón, đặc, đậm màu ở chính giữa, bào tương bắt màu cam, là đặc điểm của biến đổi', '', 'A. Phản ứng – sửa chữa;;;s_answer;;;B. Tăng sừng;;;s_answer;;;C. Cận sừng;;;s_answer;;;D. Teo', 'C. Cận sừng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(556, 'text', 'GPBL', 'KHÔNG PHẢI đặc điểm của biến đổi phản ứng – sửa chữa', '', 'A. Tế bào gai có nhân lớn gấp 3 lần tế bào trung gian;;;s_answer;;;B. Nhân tăng sắc, hạch nhân to;;;s_answer;;;C. Xếp thành đám giống đàn cá;;;s_answer;;;D.  A, B  đúng', 'D.  A, B  đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(557, 'text', 'GPBL', 'Đặc điểm vi thể của carcinoma nội mạc tử cung', '', 'A. Tuyến dị dạng, không có mô đệm giữa các ống tuyến;;;s_answer;;;B. Mô đệm thoái hoá nhày hoặc nhiều vùng hoại tử;;;s_answer;;;C. Tế bào tuyến biệt hoá rõ, ít dị dạng;;;s_answer;;;D. Mô đệm thấm nhập nhiều lympho bào', 'A. Tuyến dị dạng, không có mô đệm giữa các ống tuyến', 1, 1, NULL, 1596183283, 1596183283, NULL),
(558, 'text', 'GPBL', 'Hình ảnh “tế bào chỉ điểm” được thấy trong', '', 'A. Nhiễm Trichomonas ;;;s_answer;;;B. Chuyển đổi hệ vi khuẩn âm đạo ;;;s_answer;;;C. Nhiễm Candida ;;;s_answer;;;D. Nhiễm Actinomyces', 'B. Chuyển đổi hệ vi khuẩn âm đạo', 1, 1, NULL, 1596183283, 1596183283, NULL),
(559, 'text', 'GPBL', 'Phết pap có đám rối nùi tựa “ cục bông gòn (cotton balls)” trên nền tiêu bản chứa nhiều bạch cầu đa nhân là đặc điểm của', '', 'A. Nhiễm Trichomonas;;;s_answer;;;B. Nhiễm Actinomyces;;;s_answer;;;C. Nhiễm virus Herpes ;;;s_answer;;;D. Nhiễm Cytomeglovirus', 'B. Nhiễm Actinomyces', 1, 1, NULL, 1596183283, 1596183283, NULL),
(560, 'text', 'GPBL', 'Phết Pap có các tế bào hình trái lê dài từ 15 đến 30 micromet với bào tương chứa các hạt màu đỏ, nền tiêu bản có chứa mảnh vụn tế bào và các sợi mảnh màu xanh, là đặc điểm của', '', 'A. Nhiễm Trichomonas;;;s_answer;;;B. Nhiễm Actinomyces ;;;s_answer;;;C. Nhiễm virus Herpes ;;;s_answer;;;D. Nhiễm Cytomeglovirus', 'A. Nhiễm Trichomonas', 1, 1, NULL, 1596183283, 1596183283, NULL),
(561, 'text', 'GPBL', 'Tế bào gai không điển hình – không rõ ý nghĩa xác định có thể gặp ở dạng sau', '', 'A. Tế bào không điển hình trong biến đổi sửa chữa, trong biến đổi do teo;;;s_answer;;;B. Tế bào không điển hình có khoảng sáng quanh nhân giống tế bào rỗng, nhân biến đổi không rõ ràng;;;s_answer;;;C. Tế bào không điển hình trong biến đổi cận sừng;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(562, 'text', 'GPBL', 'KHÔNG PHẢI đặc điểm tế bào gai không điển hình – không loại trừ HSIL', '', 'A. Bào tương kiểu tế bào gai trưởng thành;;;s_answer;;;B. Bào tương kiểu tế bào chuyển sản gai chưa trưởng thành;;;s_answer;;;C. Nhân có kích thước 3-4 lần nhân tế bào trung gian;;;s_answer;;;D.  Nhân to, tăng sắc', 'A. Bào tương kiểu tế bào gai trưởng thành', 1, 1, NULL, 1596183283, 1596183283, NULL),
(563, 'text', 'GPBL', 'Tế bào LSIL có đặc điểm nhân như sau:', '', 'A. Nhân to hơn nhân tế bào trung gian ≥ 3 lần;;;s_answer;;;B. Có khi có 2 – 3 nhân, màng nhân đều hoặc có khía lõm;;;s_answer;;;C. Nhân tăng sắc, chất nhiễm sắc phân bố tương đối đều;;;s_answer;;;D. Tất cả đúng', 'D. Tất cả đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(564, 'text', 'GPBL', 'Thuốc ngừa thai có thể làm nội mạc tử cung', '', 'A. Phì đại;;;s_answer;;;B. Tăng sản;;;s_answer;;;C. Teo đét;;;s_answer;;;D. Loạn sản', 'C. Teo đét', 1, 1, NULL, 1596183283, 1596183283, NULL),
(565, 'text', 'GPBL', 'Trong phết tế bào, HSIL khác LSIL ở đặc điểm', '', 'A. Các đám tế bào kết thành đám 3 chiều kiểu hợp bào;;;s_answer;;;B. Tỷ lệ nhân/ bào tương cao (>50%);;;s_answer;;;C. Màng nhân đều;;;s_answer;;;D. Chỉ có A và B đúng', 'D. Chỉ có A và B đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(566, 'text', 'GPBL', 'KHÔNG PHẢI đặc điểm tế bào học của HSIL', '', 'A. Tế bào đứng riêng lẽ hoặc thành nhóm chen chúc tăng sắc;;;s_answer;;;B. Nền lam có nhiều mảnh vụn tế bào hoại tử và hồng cầu (nền u);;;s_answer;;;C. Tế bào có tỉ lệ nhân trên bào tương cao;;;s_answer;;;D. Bào tương có khi sừng hòa bắt màu cam', 'B. Nền lam có nhiều mảnh vụn tế bào hoại tử và hồng cầu (nền u)', 1, 1, NULL, 1596183283, 1596183283, NULL),
(567, 'text', 'GPBL', 'Tuyến nội mạc tử cung vừa giai đoạn phát triển vừa giai đoạn chế tiết gặp trong', '', 'A. Tăng sản nội mạc tử cung;;;s_answer;;;B. Lạc nội mạc tử cung trong cơ;;;s_answer;;;C. Bong tróc nội mạc tử cung không đều;;;s_answer;;;D. U lành cơ trơn', 'C. Bong tróc nội mạc tử cung không đều', 1, 1, NULL, 1596183283, 1596183283, NULL),
(568, 'text', 'GPBL', 'Trong 1 phết tế bào, carcinom tế bào gai và HSIL giống nhau ở các đặc điểm sau, NGOẠI TRỪ:', '', 'A. Có nền hoại tử u;;;s_answer;;;B. Tỷ lệ nhân / bào tương cao;;;s_answer;;;C. Nhân tăng sắc;;;s_answer;;;D. Màng nhân không đều', 'A. Có nền hoại tử u', 1, 1, NULL, 1596183283, 1596183283, NULL),
(569, 'text', 'GPBL', 'Carcinom tế bào gai sừng hóa có đặc điểm:', '', 'A. Tế bào u đứng rời, riêng rẽ, ít kết đám, bào tương ái toan ;;;s_answer;;;B. Tế bào có hình đa giác, hình thoi, hình đuôi nòng nọc;;;s_answer;;;C. Bào tương có khi có dạng nhầy;;;s_answer;;;D. Chỉ có A, B đúng', 'D. Chỉ có A, B đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(570, 'text', 'GPBL', 'Kết quả sinh thiết cổ tử cung là CIN 2, nghĩa là', '', 'A. Biểu mô gai dị dạng ở 2/3 dưới bề dày lớp biểu mô;;;s_answer;;;B. Carcinoma tế bào gai giai đoạn II (FIGO);;;s_answer;;;C. Carcinoma tuyến cổ tử cung giai đoạn II (FIGO);;;s_answer;;;D. Biểu mô gai dị dạng chiếm toàn bộ bề dày lớp biểu mô', 'A. Biểu mô gai dị dạng ở 2/3 dưới bề dày lớp biểu mô', 1, 1, NULL, 1596183283, 1596183283, NULL),
(571, 'text', 'GPBL', 'Carcinom tuyến tại chỗ cổ trong CTC trên Pap có hình ảnh:', '', 'A. Tế bào tuyến kết thanh đám ba chiều gọi là “nhóm chen chúc”;;;s_answer;;;B. Tế bào tuyến ở ngoại vi của đám có hình ảnh “lông chim”;;;s_answer;;;C. Đám tế bào tuyến có hình “hoa hồng”, tuyến giả tầng;;;s_answer;;;D. Tất cả đúng', 'D. Tất cả đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(572, 'text', 'GPBL', 'Carcinom tuyến tại chỗ cổ trong CTC cần phải chẩn đoán phân biệt với, NGOẠI TRỪ:', '', 'A. HSIL;;;s_answer;;;B. Tế bào rổng;;;s_answer;;;C. Tế bào nội mạc tử cung;;;s_answer;;;D. Carcinom tuyến cổ trong CTC', 'B. Tế bào rổng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(573, 'text', 'GPBL', 'Trên phết tế bào CTC có thể gặp carcinom tuyến của:', '', 'A. Carcinom tuyến tại chỗ cổ trong CTC;;;s_answer;;;B. Carcinom tuyến cổ trong CTC;;;s_answer;;;C. Carcinom tuyến nội mạc tử cung;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(574, 'text', 'GPBL', 'Tân sinh trong biểu mô cổ tử cung thường phát triển ở vùng', '', 'A. Biểu mô tuyến cổ trong;;;s_answer;;;B. Vùng tiếp giáp gai trụ;;;s_answer;;;C. Biểu mô gai cổ ngoài;;;s_answer;;;D. Vùng kênh cổ tử cung', 'B. Vùng tiếp giáp gai trụ', 1, 1, NULL, 1596183283, 1596183283, NULL),
(575, 'text', 'GPBL', 'Tế bào tuyến cổ trong không điển hình – NOS cần chẩn đoán phân biệt với:', '', 'A. HSIL;;;s_answer;;;B. Carcinom tuyến tại chỗ cổ trong CTC;;;s_answer;;;C. Carcinom tuyến nội mạc;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(576, 'text', 'GPBL', 'Phương pháp ly tâm thường được áp dụng:', '', 'A. Đối với bệnh phẩm nghèo tế bào;;;s_answer;;;B. Bệnh phẩm thường là dịch cơ thể;;;s_answer;;;C. Để tạo block tế bào;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(577, 'text', 'GPBL', 'Viêm tuyến vú cấp tính', '', 'A. Có thể gây ung thư vú;;;s_answer;;;B. Thường chỉ bị 1 bên vú;;;s_answer;;;C. Hiếm khi hình thành ổ áp xe ;;;s_answer;;;D. Do tụ cầu xâm nhập qua đường máu vào mô vú', 'B. Thường chỉ bị 1 bên vú', 1, 1, NULL, 1596183283, 1596183283, NULL),
(578, 'text', 'GPBL', 'Phẩm nhuộm KHÔNG CÓ trong quy trình nhuộm Papanicolaou:', '', 'A. Hematoxylin ;;;s_answer;;;B. Light Green ;;;s_answer;;;C. Orange G ;;;s_answer;;;D. Eosin', 'B. Light Green', 1, 1, NULL, 1596183283, 1596183283, NULL),
(579, 'text', 'GPBL', 'Phương pháp nhuộm Diff Quik:', '', 'A. Thuốc cùng nhóm với phương pháp nhuộm May-Grunwald-Giemsa;;;s_answer;;;B. Cho thấy rõ chi tiết của nhân;;;s_answer;;;C. Cho phép đánh giá nhanh chất lượng tiêu bản;;;s_answer;;;D. Chỉ A và B đúng', 'D. Chỉ A và B đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(580, 'text', 'GPBL', 'So với phết Papanicolaou thường quy, phết tế bào trong chất lỏng có ưu điểm:', '', 'A. Tế bào được trãi thành 1 lớp mỏng;;;s_answer;;;B. Loại bỏ được hồng cầu và chất nhầy;;;s_answer;;;C. Rẻ tiền hơn;;;s_answer;;;D. Chỉ A và B đúng', 'D. Chỉ A và B đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(581, 'text', 'GPBL', 'U dịch nhày buồng trứng có đặc điểm', '', 'A. Là u biểu mô;;;s_answer;;;B. Thường gặp ở trẻ em;;;s_answer;;;C. Kích thước rất nhỏ;;;s_answer;;;D. Thường là u ác', 'A. Là u biểu mô', 1, 1, NULL, 1596183283, 1596183283, NULL),
(582, 'text', 'GPBL', 'Phết Pap thường quy KHÔNG đủ tiêu chuẩn về mặt chất lượng để chẩn đoán tế bào học', '', 'A. Tiêu bản chứa 5000 tế bào biểu mô gai;;;s_answer;;;B. Trên 75% tế bào biểu mô bị che lấp;;;s_answer;;;C. Không có tế bào cổ trong;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183283, 1596183283, NULL),
(583, 'text', 'GPBL', 'Loại tế bào có nhân được dùng chuẩn tham chiếu về kích thước và đặc điểm chất nhiễm sắc cho các loại tế bào khác trong phết Pap:', '', 'A. Tế bào đáy;;;s_answer;;;B. Tế bào trung gian;;;s_answer;;;C. Tế bào cận đáy;;;s_answer;;;D. Tế bào bề mặt', 'B. Tế bào trung gian', 1, 1, NULL, 1596183283, 1596183283, NULL),
(584, 'text', 'GPBL', 'Sự hiện diện của tế bào nội mạc trên phết Pap nghi ngờ bất thường khi xảy ra trong:', '', 'A. Ngày 6 -10 của chu kỳ kinh;;;s_answer;;;B. Nữa sau chu kỳ kinh;;;s_answer;;;C. Giai đoạn sau mãn kinh;;;s_answer;;;D. Chỉ A và B đúng', 'C. Giai đoạn sau mãn kinh', 1, 1, NULL, 1596183283, 1596183283, NULL),
