(1155, 'text', 'Sơ sinh', 'Thủ thuật ấn ngực có thể gây ra các nguy cơ gì', '', 'A. Bụng chướng;;;s_answer;;;B. Chấn thương cột sống;;;s_answer;;;C. Gãy xương sườn;;;s_answer;;;D. Thương tổn tim', 'C. Gãy xương sườn', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1156, 'text', 'Sơ sinh', 'Kỹ thuật ấn ngực được ưa chuộng', '', 'A. Kỹ thuật ngón cái;;;s_answer;;;B. Kỹ thuật 2 ngón tay;;;s_answer;;;C. Kỹ thuật gốc bàn tay ;;;s_answer;;;D.  Kỹ thuật bàn tay nắm chặt', 'B. Kỹ thuật 2 ngón tay', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1157, 'text', 'Sơ sinh', 'Phản xạ tự nhiên của trẻ không cần phải học là', '', 'A. Phản xạ tìm vú;;;s_answer;;;B. Phản xạ nuốt;;;s_answer;;;C. Phản xạ mút;;;s_answer;;;D. Tất cả đúng', 'D. Tất cả đúng', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1158, 'text', 'Sơ sinh', 'Vị trí tốt nhất để tiêm bắp cho trẻ là', '', 'A. Cơ bả vai;;;s_answer;;;B. Cơ đùi;;;s_answer;;;C. Cơ mông;;;s_answer;;;D. Cơ Delta', 'B. Cơ đùi', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1159, 'text', 'Sơ sinh', 'Thứ tự hút nhớt cho trẻ là', '', 'A. Miệng trước mũi sau;;;s_answer;;;B. Mũi trước mũi sau;;;s_answer;;;C. Cả hai đều đúng;;;s_answer;;;D. Cả hai đều sai', 'A. Miệng trước mũi sau', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1160, 'text', 'Sơ sinh', 'Cần tái khám ngay khi trẻ có dấu hiệu sau đây', '', 'A. Bỏ bú ;;;s_answer;;;B. Ngủ li bì, khó đánh thức;;;s_answer;;;C. Co giật;;;s_answer;;;D. Tất cả các dấu hiệu trên', 'D. Tất cả các dấu hiệu trên', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1161, 'text', 'Sơ sinh', 'Những trẻ cần thông khí áp lực dương hoặc những mức độ hồi sức tích cực, sau hồi sức sẽ được chăm sóc', '', 'A. Chăm sóc thường quy;;;s_answer;;;B. Chăm sóc theo dõi;;;s_answer;;;C. Chăm sóc sau hồi sức;;;s_answer;;;D. Tất cả đều sai', 'C. Chăm sóc sau hồi sức', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1162, 'text', 'Sơ sinh', 'Trẻ sơ sinh đủ tháng có chiều cao trung bình là', '', 'A. 40 – 45 cm;;;s_answer;;;B. 45 – 50 cm;;;s_answer;;;C. 48 – 50 cm;;;s_answer;;;D. 50 – 55 cm', 'C. 48 – 50 cm', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1163, 'text', 'Sơ sinh', 'Trẻ sơ sinh đủ tháng có vòng đầu', '', 'A. 35 cm;;;s_answer;;;B. 37 cm;;;s_answer;;;C. 40 cm;;;s_answer;;;D. 45 cm', 'A. 35 cm', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1164, 'text', 'Sơ sinh', 'Trong thời gian dùng thuốc corticoid, trẻ được chủng ngừa sau khi ngưng thuốc bao lâu', '', 'A. 3 tuần;;;s_answer;;;B. 6 tuần;;;s_answer;;;C. 1 tháng;;;s_answer;;;D. 2 tháng', 'C. 1 tháng', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1165, 'text', 'Sơ sinh', 'Nguyên tắc chăm sóc trẻ sinh thiếu tháng là', '', 'A. Đàm bảo cho sự thở;;;s_answer;;;B. Đảm bảo đủ ấm;;;s_answer;;;C. Trẻ được nuôi dưỡng bằng sữa mẹ;;;s_answer;;;D. Tất cả đúng', 'D. Tất cả đúng', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1166, 'text', 'Sơ sinh', 'Hiện tượng vàng da sinh lý thường mất đi ở thời điểm nào sau sinh', '', 'A. 5 – 6 ngày sau sinh;;;s_answer;;;B. 7 – 8 ngày sau sinh;;;s_answer;;;C. 10 – 12 ngày sau sinh;;;s_answer;;;D. Sau 15 ngày', 'B. 7 – 8 ngày sau sinh', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1167, 'text', 'Sơ sinh', 'Bóng có mấy thành phần cơ bản', '', 'A. 4;;;s_answer;;;B. 5;;;s_answer;;;C.   6 ;;;s_answer;;;D. 7', 'D. 7', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1168, 'text', 'Sơ sinh', 'Ấn ngực (hay xoa tim ngoài lồng ngực) là', '', 'A. Ấn tim vào cột sống;;;s_answer;;;B. Tăng áp lực trong lồng ngực;;;s_answer;;;C. Đưa máu đến các cơ quan quan trọng trong cơ thể;;;s_answer;;;D. Tất cả đúng', 'D. Tất cả đúng', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1169, 'text', 'Sơ sinh', 'Phòng ngừa viêm kết mạc mắt trẻ sơ sinh', '', 'A. Tiêm ngừa VAT cho phụ nữ mang thai;;;s_answer;;;B. Điều trị các bệnh viêm nhiễm đường sinh dục, nhỏ mắt cho trẻ ngay sau sanh;;;s_answer;;;C. Tiêm ngừa VAT và điều rị các bệnh lây truyền qua đường tình dục;;;s_answer;;;D. Tất cả đều sai', 'B. Điều trị các bệnh viêm nhiễm đường sinh dục, nhỏ mắt cho trẻ ngay sau sanh', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1170, 'text', 'Sơ sinh', 'Co giật sau hồi sức sơ sinh có thể do', '', 'A. Bệnh lý não do thiếu oxy;;;s_answer;;;B. Rối loạn chuyển hóa, hạ đường huyết;;;s_answer;;;C. Bất thường điện giải như hạ Natri máu hay hạ Calci máu;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1171, 'text', 'Sơ sinh', 'Để giảm nguy cơ xuất huyết não khi hồi sức cho trẻ thì tư thế tốt nhất là', '', 'A. Nằm đầu cao 15°;;;s_answer;;;B. Nằm đầu cao 30° ;;;s_answer;;;C. Nằm đầu thấp (bàn hồi sức nằm ngang);;;s_answer;;;D. Tất cả đều sai', 'C. Nằm đầu thấp (bàn hồi sức nằm ngang)', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1172, 'text', 'Sơ sinh', 'Dấu hiệu nào sau đây thường gặp ở trẻ non tháng', '', 'A. Trẻ cử động nhiều;;;s_answer;;;B. Da mỏng, ửng đỏ, nhiều chất gây;;;s_answer;;;C. Da tím;;;s_answer;;;D. Bong da', 'B. Da mỏng, ửng đỏ, nhiều chất gây', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1173, 'text', 'Sơ sinh', 'Khoảng bao nhiêu % trẻ sơ sinh cần hồi sức tích cực để sống sót', '', 'A. 1%;;;s_answer;;;B. 5%;;;s_answer;;;C. 10%;;;s_answer;;;D. 30%', 'A. 1%', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1174, 'text', 'Sơ sinh', 'Nguyên tắc chăm sóc trẻ sinh thiếu tháng:', '', 'A. Đảm bảo cho sự thở ;;;s_answer;;;B. Đảm bảo đủ ấm ;;;s_answer;;;C. Trẻ được nuôi dưỡng bằng sữa mẹ ;;;s_answer;;;D. Tất cả đúng', 'D. Tất cả đúng', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1175, 'text', 'Sơ sinh', 'Người xác định tình trạng cấp cứu cho trẻ bỏ rơi là:', '', 'A. Người phát hiện;;;s_answer;;;B. Nhân viên phục vụ y tế;;;s_answer;;;C. Nhân viên y tế phát hiện hay được báo;;;s_answer;;;D. Bác sĩ khoa sơ sinh', 'C. Nhân viên y tế phát hiện hay được báo', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1176, 'text', 'Dược', 'Thuốc nhìn gần giống nhau:', '', 'A. Thuốc tương tự nhau về hình dạng.;;;s_answer;;;B. Thuốc có kích thước và thiết kế trên bao bì tương tự nhau.;;;s_answer;;;C. Thuốc tương tự nhau về hình dạng, màu sắc, kích thước và thiết kế trên bao bì.;;;s_answer;;;D. Thuốc có màu sắc gần giống nhau', 'C. Thuốc tương tự nhau về hình dạng, màu sắc, kích thước và thiết kế trên bao bì.', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1177, 'text', 'Dược', 'LASA là chữ viết tắt', '', 'A. Lookalike;;;s_answer;;;B. Soundalike;;;s_answer;;;C. Look alike sound alike;;;s_answer;;;D. Look a like sound a like', 'C. Look alike sound alike', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1178, 'text', 'Dược', 'Thuốc trong danh mục LASA qui định sắp xếp như thế nào', '', 'A. Xếp xa nhau.;;;s_answer;;;B. Không cùng trên 1 kệ;;;s_answer;;;C. Cách nhau ít nhất 2 thuốc;;;s_answer;;;D. Xếp xa nhau, không để trên cùng 1 kệ (Cách ít nhất 4 thuốc trên cùng kệ)', 'D. Xếp xa nhau, không để trên cùng 1 kệ (Cách ít nhất 4 thuốc trên cùng kệ)', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1179, 'text', 'Dược', 'Mục đích của qui trình biệt trữ thuốc', '', 'A. Đảm bảo những thuốc chưa kết luận về mặt chất lượng được bảo quản riêng biệt và không đưa vào cấp phát nhằm đảm bảo an toàn cho người bệnh.;;;s_answer;;;B. Đảm bảo những thuốc chưa kết luận về mặt chất lượng cũng như những thuốc không đạt chất lượng được bảo quản riêng biệt và không đưa vào cấp phát nhằm đảm bảo an toàn cho người bệnh.;;;s_answer;;;C. Đảm bảo thuốc đạt chất lượng khi cấp phát cho người sử dụng;;;s_answer;;;D. Đảm bảo cho người bệnh sử dụng thuốc an toàn hợp lý.', 'B. Đảm bảo những thuốc chưa kết luận về mặt chất lượng cũng như những thuốc không đạt chất lượng được bảo quản riêng biệt và không đưa vào cấp phát nhằm đảm bảo an toàn cho người bệnh.', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1180, 'text', 'Dược', 'Biệt trữ là gì', '', 'A. Là tình trạng thuốc, nguyên liệu làm thuốc được bảo quản cách ly một cách cơ học.;;;s_answer;;;B. Là tình trạng thuốc, nguyên liệu làm thuốc được bảo quản cách ly bằng biện pháp hiệu quả khác trong khi chờ quyết định cho phép nhập kho, xuất kho để bào chế, sản xuất, đóng gói, phân phối hoặc tiêu.;;;s_answer;;;C. Thuốc không đạt chất lượng chưa được phân phối;;;s_answer;;;D. Là tình trạng thuốc, nguyên liệu làm thuốc được bảo quản cách ly một cách cơ học hoặc bằng biện pháp hiệu quả khác trong khi chờ quyết định cho phép nhập kho, xuất kho để bào chế, sản xuất, đóng gói, phân phối hoặc tiêu hủy.', 'D. Là tình trạng thuốc, nguyên liệu làm thuốc được bảo quản cách ly một cách cơ học hoặc bằng biện pháp hiệu quả khác trong khi chờ quyết định cho phép nhập kho, xuất kho để bào chế, sản xuất, đóng gói, phân phối hoặc tiêu hủy.', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1181, 'text', 'Dược', 'Các bước cần thực hiện trong qui trình biệt trữ', '', 'A. Nhận thuốc biệt trữ - phân loại – xử lý thuốc biệt trữ;;;s_answer;;;B. Phân loại thuốc biệt trữ - Sắp xếp thuốc biệt trữ - xử lý thuốc biệt trữ.;;;s_answer;;;C. Phát hiện thuốc không đạt chất lượng – sắp xếp – xử lý;;;s_answer;;;D. Phát hiện thuốc không đạt chất lượng - Nhận thuốc biệt trữ - xử lý thuốc biệt trữ.', 'B. Phân loại thuốc biệt trữ - Sắp xếp thuốc biệt trữ - xử lý thuốc biệt trữ.', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1182, 'text', 'Dược', 'Mục tiêu cụ thể của qui trình theo dõi ghi chép điều kiện nhiệt độ bảo quản', '', 'A. Đảm bảo thuốc, hóa chất và vaccin luôn được bảo quản đúng yêu cầu của nhà sản xuất.;;;s_answer;;;B. Đảm bảo cung cấp thuốc, hóa chất và vaccin đạt chất lượng đến người bệnh.;;;s_answer;;;C. Phát hiện kịp thời những sự cố xảy ra đối với các tủ lạnh bảo quản thuốc, hóa chất xét nghiệm và vaccin.;;;s_answer;;;D. A,B đúng', 'D. A,B đúng', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1183, 'text', 'Dược', 'Nhiệt độ tủ lạnh bảo quản thuốc, vaccin, hóa chất được ghi nhận vào “biểu đồ theo dõi nhiệt độ”', '', 'A. 2 lần/ ngày;;;s_answer;;;B. 3 lần/ngày;;;s_answer;;;C. 2 – 4 lần/ngày;;;s_answer;;;D. 4 lần/ ngày', 'D. 4 lần/ ngày', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1184, 'text', 'Dược', 'Khi nào tủ lạnh không đạt yêu cầu bảo quản', '', 'A. Nhiệt độ hiển thị trên nhiệt kế điện tử  của tủ lạnh >8°C hoặc < 2°C trong 10 phút không trở về nhiệt độ qui định (2 – 8°C);;;s_answer;;;B. Chỉ thị đông băng hiển thị dấu X;;;s_answer;;;C. Tmin < 1,5°C nhiều hơn 2 lần trong 6h;;;s_answer;;;D. Tất cả đúng.', 'D. Tất cả đúng.', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1185, 'text', 'Dược', 'Mục tiêu cụ thể trong qui trình định kỳ đối chiếu thuốc trong kho:', '', 'A. Quy định cách thức kiểm kê thuốc trong kho về mặt số lượng, về mặt cảm quan của thuốc, hạn dùng của thuốc;;;s_answer;;;B. Không để thất thoát tài sản của Bệnh viện, phát hiện kịp thời các thuốc không đạt chất lượng đang lưu kho ;;;s_answer;;;C. Quy định cách thức kiểm kê thuốc trong kho về mặt số lượng;;;s_answer;;;D. Tất cả đúng.', 'B. Không để thất thoát tài sản của Bệnh viện, phát hiện kịp thời các thuốc không đạt chất lượng đang lưu kho', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1186, 'text', 'Dược', 'Qui định thời điểm kiểm kê thuốc trong kho', '', 'A. Hàng ngày – hàng tuần – hàng tháng – hàng quí;;;s_answer;;;B. Hàng tuần – hàng tháng – hàng quí – hàng năm;;;s_answer;;;C. Hàng ngày – hàng tháng – hàng quí – hàng năm;;;s_answer;;;D. Hàng ngày – hàng tuần – hàng tháng', 'B. Hàng tuần – hàng tháng – hàng quí – hàng năm', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1187, 'text', 'Dược', 'Các bộ phận tham gia kiểm kê định kỳ năm:', '', 'A. Nhân viên kho – Nhân viên phòng TCKT- Nhân viên Công ty Kiểm toán;;;s_answer;;;B. Nhân viên kho – Nhân viên Phòng thống kê Dược – Nhân viên phòng TCKT- Nhân viên Công ty Kiểm toán;;;s_answer;;;C. Nhân viên nhà thuốc – Nhân viên Phòng thống kê Dược – Nhân viên phòng TCKT- Nhân viên Công ty Kiểm toán;;;s_answer;;;D. Nhân viên kho – Nhân viên Phòng thống kê Dược – Nhân viên phòng TCKT', 'B. Nhân viên kho – Nhân viên Phòng thống kê Dược – Nhân viên phòng TCKT- Nhân viên Công ty Kiểm toán', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1188, 'text', 'Dược', 'Bình tích lạnh được sắp xếp như thế nào trong phích vaccin', '', 'A. Xếp bình tích lạnh dưới đáy phích vaccin;;;s_answer;;;B. Xếp bình tích lạnh dưới đáy phích vaccin có nhiệt kế theo dõi;;;s_answer;;;C. Xếp bình tích lạnh vào 4 thành xung quanh và dưới đáy của phích vaccin.;;;s_answer;;;D. Tất cả đúng.', 'D. Tất cả đúng.', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1189, 'text', 'Dược', 'Cách đóng gói vaccin vào phích vaccin', '', 'A. Xếp bình tích lạnh vào 4 thành xung quanh và dưới đáy của phích vaccin;;;s_answer;;;B. Xếp bình tích lạnh vào 4 thành xung quanh và dưới đáy của phích vaccin, Xếp các hộp vaccin để đầu lọ vaccin quay lên trên;;;s_answer;;;C. Xếp bình tích lạnh vào 4 thành xung quanh và dưới đáy của phích vaccin -  Xếp các hộp vaccin để đầu lọ vaccin quay lên trên - Gói vaccin, dung môi vào túi nilong và xếp vào giữa phích vaccin - Để nhiệt kế cùng với vaccin, để miếng xốp ở trên cùng trong phích vaccin.;;;s_answer;;;D. Gói vaccin, dung môi vào túi nilong và xếp vào giữa phích vaccin.', 'C. Xếp bình tích lạnh vào 4 thành xung quanh và dưới đáy của phích vaccin -  Xếp các hộp vaccin để đầu lọ vaccin quay lên trên - Gói vaccin, dung môi vào túi nilong và xếp vào giữa phích vaccin - Để nhiệt kế cùng với vaccin, để miếng xốp ở trên cùng trong', 1, 1, NULL, 1596183381, 1596183381, NULL),
(1190, 'text', 'Dược', 'Mục đích của qui trình đóng gói vaccin vào phích vaccin', '', 'A. Đảm bảo vaccin không bị hư hỏng trong quá trình bảo quản và vận chuyển;;;s_answer;;;B. Đảm bảo vaccin luôn được bảo quản đúng nhiệt độ qui định.;;;s_answer;;;C. Đảm bảo vaccin được sử dụng an toàn, hiệu quả;;;s_answer;;;D. Tất cả đúng.', 'A. Đảm bảo vaccin không bị hư hỏng trong quá trình bảo quản và vận chuyển', 1, 1, NULL, 1596183381, 1596183381, NULL);
