(1376, 'text', 'AT.TTBYT', 'Các nguyên nhân gây thất thoát khí trên bệnh nhân thở máy, NGOẠI TRỪ', '', 'A. Ống nội khí quản không bóng chèn hoặc chưa bơm bóng chèn;;;s_answer;;;B. Bóng chèn ống nội khí quản bị vỡ;;;s_answer;;;C. Ống nội khí quản vào sâu phế quản gốc ;;;s_answer;;;D. Hở các chỗ nối trên bộ dây máy thở', 'C. Ống nội khí quản vào sâu phế quản gốc', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1377, 'text', 'AT.TTBYT', 'Các cách xác định đầu ống nội khí quản đúng vị trí, cách nào chắc chắn đúng nhất:', '', 'A. Bệnh nhân hồng, SpO2 > 95% sau khi đặt nội khí quản bóp bóng giúp thở;;;s_answer;;;B. Nghe phế âm phổi đều 2 bên khi bóp bóng;;;s_answer;;;C. Thấy lồng ngực nhô đều 2 bên khi bóp bóng;;;s_answer;;;D. Đầu ống nội khí quản nằm ngang đầu nối 2 xương đòn khí chụp X quang', 'D. Đầu ống nội khí quản nằm ngang đầu nối 2 xương đòn khí chụp X quang', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1378, 'text', 'AT.TTBYT', 'Yếu tố nào quan trọng nhất để chẩn đoán tụt nội khí quản', '', 'A. Bệnh nhân tím tái;;;s_answer;;;B. Ống nội khí quản có vẻ hơi dài;;;s_answer;;;C. Máy báo động áp lực đường thở thấp;;;s_answer;;;D. Bóp bóng thử thấy nhẹ tay, bong bóng trào ra mũi miệng', 'D. Bóp bóng thử thấy nhẹ tay, bong bóng trào ra mũi miệng', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1379, 'text', 'AT.TTBYT', 'Cách xử trí ống nội khí quản tắc nghẽn hoàn toàn do đàm đặc:', '', 'A. Bơm Natri clorua 0,9% vô trùng, mỗi lần 1 – 3 ml, bóp bóng giúp thở 4 – 6 nhịp sau đó hút đàm, lặp lại nhiều lần nếu cần.;;;s_answer;;;B. Thay nội khí quản khác;;;s_answer;;;C. A và B đúng;;;s_answer;;;D. Còn cách xử trí khác', 'C. A và B đúng', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1380, 'text', 'AT.TTBYT', 'Khử khuẩn mức độ trung bình là:', '', 'A. Tiêu diệt các vi khuẩn thông thường và ;;;s_answer;;;B. Tiêu diệt một vài vi rút và nấm, ;;;s_answer;;;C. Tiêu diệt được bào tử vi khuẩn,;;;s_answer;;;D. Không tiêu diệt được vi khuẩn lao', 'D. Không tiêu diệt được vi khuẩn lao', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1381, 'text', 'AT.TTBYT', 'Biến chứng của thở CPAP áp lực quá cao là', '', 'A. Tràn khí màng phổi;;;s_answer;;;B. Giảm cung lượng tim;;;s_answer;;;C. Viêm phổi do hít sặc;;;s_answer;;;D. A và B đúng', 'D. A và B đúng', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1382, 'text', 'AT.TTBYT', 'Các nguyên nhân có thể gây báo động áp lực đường thở cao, NGOẠI TRỪ:', '', 'A. Tắc nghẽn ống nội khí quản;;;s_answer;;;B. Tần số thở tăng;;;s_answer;;;C. Ống nội khí quản vào sâu;;;s_answer;;;D. Xẹp phổi', 'B. Tần số thở tăng', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1383, 'text', 'AT.TTBYT', 'Trong các dụng cụ sau đây, dụng cụ nào không hấp ẩm được:', '', 'A. Bình làm ẩm máy thở;;;s_answer;;;B. Bẫy nước;;;s_answer;;;C. Dây máy thở silicon;;;s_answer;;;D. Dây điện trở luồn vào trong bộ dây máy thở', 'D. Dây điện trở luồn vào trong bộ dây máy thở', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1384, 'text', 'AT.TTBYT', 'Câu nào KHÔNG đúng khi chọn áp lực hút đàm:', '', 'A. Càng cao càng tốt để hút sạch hết đàm;;;s_answer;;;B. Trẻ em áp lực hút từ – 100 đến – 120 mmHg;;;s_answer;;;C. Trẻ sơ sinh áp lực hút từ – 80 đến – 100 mmHg;;;s_answer;;;D. B và C đúng', 'A. Càng cao càng tốt để hút sạch hết đàm', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1385, 'text', 'AT.TTBYT', 'Ống hút đàm qua nội khí quản nên được đưa vào:', '', 'A. Ngắn hơn đầu ống NKQ 1 cm;;;s_answer;;;B. Ngang đầu ống NKQ;;;s_answer;;;C. Dài hơn đầu ống nội khí quản 1 cm;;;s_answer;;;D. Đến chạc ba khí phế quản (carina)', 'C. Dài hơn đầu ống nội khí quản 1 cm', 1, 1, NULL, 1596183394, 1596183394, NULL),
(1386, 'text', 'AT.TTBYT', 'Dòng điện với cường độ bao nhiêu sẽ gây nguy hiểm, ảnh hưởng hệ thống thần kinh, co giật cơ sau đó tê liệt và ngưng tim ngưng thở?', '', 'A. 0,6 – 1,5 mA.;;;s_answer;;;B. Khoảng 15mA.;;;s_answer;;;C. 15 – 20 mA.;;;s_answer;;;D. Không có đáp án nào phù hợp.', 'C. 15 – 20 mA.', 1, 1, NULL, 1596183394, 1596183394, NULL),
