(1464, 'text', 'VLTL', 'Đề phòng tai biến khi tập cơ ức đòn chủm', '', 'A. Không được xoa lên khối u;;;s_answer;;;B. Tránh kéo giãn cột sống thoái quá;;;s_answer;;;C. Nếu khối u quá to thì phẫu thuật;;;s_answer;;;D. Các câu trên đều sai', 'B. Tránh kéo giãn cột sống thoái quá', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1465, 'text', 'VLTL', 'Chống chỉ định vật lí trị liệu hô hấp khi', '', 'A. Trẻ bỏ bú ;;;s_answer;;;B. Trẻ sốt cao trên 39 độ;;;s_answer;;;C. Trẻ ho;;;s_answer;;;D. Tất cả các câu trên', 'D. Tất cả các câu trên', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1466, 'text', 'VLTL', 'Điều cần lưu ý khi thực hiện VLTL hô hấp', '', 'A. Thực hiện tốt nhất trước ăn 1-2h;;;s_answer;;;B. Luôn giữ SpO2 trên 90%;;;s_answer;;;C. Dung thuốc long đờm trong khi tập;;;s_answer;;;D. Không tăng lưu lượng oxy trong khi tập', 'A. Thực hiện tốt nhất trước ăn 1-2h', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1467, 'text', 'VLTL', 'Chậm nói được xác định khi', '', 'A. Không bập bẹ baba má má lúc 6 tháng tuổi;;;s_answer;;;B. Không nói từ đơn lúc 18 tháng tuổi;;;s_answer;;;C. Không nói được câu 2 từ lúc 24 tháng ;;;s_answer;;;D. Các câu trên đều đúng', 'D. Các câu trên đều đúng', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1468, 'text', 'VLTL', 'Triệu chứng nào cho thấy được trong liệt thần kinh cánh tay', '', 'A. Tay liệt cử động kém tay khép sát người;;;s_answer;;;B. Nhìn thấy cánh tay bé xoay trong và cổ tay gập;;;s_answer;;;C. Câu A và B đúng;;;s_answer;;;D. Câu A và B sai', 'C. Câu A và B đúng', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1469, 'text', 'VLTL', 'Thời điểm điều trị tốt nhất trong liệt thần kinh cánh tay', '', 'A. Sau 48h bất động tay yếu;;;s_answer;;;B. Nếu có gãy xương đòn đi kèm chờ 7-10 ngày mới bắt đầu điều trị khớp vai;;;s_answer;;;C. Nếu có phù nề quanh khớp vai chỉ bắt đầu tập Vận động khớp vai khi hết phù nề;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1470, 'text', 'VLTL', 'Điều trị phẫu thuật trong liệt đám rôi thần kinh cánh tay khi', '', 'A. Sau quá trình tập 1 tháng không cải thiện;;;s_answer;;;B. Sau 1 tuần nếu không cải thiện;;;s_answer;;;C. Sau 3 tháng tập VLTL không cải thiện;;;s_answer;;;D. Các câu trên đều sai', 'C. Sau 3 tháng tập VLTL không cải thiện', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1471, 'text', 'VLTL', 'Theo dõi trong liệt đám rối thần kinh cánh tay', '', 'A. Hẹn tập ở khoa từ 2-3 lần/tuần;;;s_answer;;;B. Nếu có dấu hiệu phục hồi hướng dẫn bài tập về nhà và hẹn tái khám mỗi tuần 1 lần.;;;s_answer;;;C. Sau đó hẹn tái khám mỗi tháng 1 lần.;;;s_answer;;;D. Tất cả câu trên đều đúng.', 'D. Tất cả câu trên đều đúng.', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1472, 'text', 'VLTL', 'Sơ đồ quá trình tàn tật', '', 'A. Người khỏe ->bệnh ->giảm khả năng-> khiếm khuyết->tàn tật;;;s_answer;;;B. Người khỏe->bệnh->khiếm khuyết->giảm khả năng ->tàn tật;;;s_answer;;;C. Người khỏe->bệnh->tàn tật->giảm khả năng->khiếm khuyết;;;s_answer;;;D. Người khỏe->tàn tật->bệnh->giảm khả năng->khiếm khuyết', 'B. Người khỏe->bệnh->khiếm khuyết->giảm khả năng ->tàn tật', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1473, 'text', 'VLTL', 'Chức năng của người điều dưỡng trong phục hồi chức năng', '', 'A. Phát hiện, phối hợp thực hiện;;;s_answer;;;B. Điều trị, chuyển đi;;;s_answer;;;C. Phát hiện, chăm sóc, điều trị;;;s_answer;;;D. Tất cả đều đúng', 'A. Phát hiện, phối hợp thực hiện', 1, 1, NULL, 1596183408, 1596183408, NULL),
(1474, 'text', 'VLTL', 'Mục đích của đèn hồng ngoại', '', 'A. Gia tăng tuần hoàn, giảm đau tăng lực cơ;;;s_answer;;;B. Làm ấm mô cơ, tăng trương lực cơ;;;s_answer;;;C. Tăng tuần hoàn, giảm đau, tăng tiết mồ hôi;;;s_answer;;;D. Tất cả đều sai', 'C. Tăng tuần hoàn, giảm đau, tăng tiết mồ hôi', 1, 1, NULL, 1596183408, 1596183408, NULL),
