(1608, 'text', 'Sản khoa', 'Với những tiến bộ trong chẩn đoán, theo dõi và xử trí ĐTĐ ngày nay hoàn toàn không còn là mối nguy hiểm cho mẹ và thai nhi nữa', '', 'A. Đúng;;;s_answer;;;B. Sai', 'B. Sai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1609, 'text', 'Sản khoa', 'Trong thời kỳ mang thai, người mẹ bị thiếu máu sẽ có nguy cơ:', '', 'A. Dễ sẩy thai, sanh non;;;s_answer;;;B. Dễ bị nôn ói;;;s_answer;;;C. Dễ bị nhau tiền đạo;;;s_answer;;;D. Dễ bị ra huyết bất thường', 'A. Dễ sẩy thai, sanh non', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1610, 'text', 'Sản khoa', 'Mẹ bị thiếu máu thì thai nhi sẽ có nguy cơ:', '', 'A. Thai chậm phát triển trong tử cung;;;s_answer;;;B. Bé sinh ra sẽ chậm phát triển về tâm thần;;;s_answer;;;C. Bé sinh ra sẽ chậm phát triển về thể chất;;;s_answer;;;D. Bé không thông minh', 'A. Thai chậm phát triển trong tử cung', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1611, 'text', 'Sản khoa', 'Một phụ nữ trước khi mang thai, qua xét nghiệm cho thấy bị bệnh thalassemia, bước tiếp theo cần thực hiện là:', '', 'A. Tư vấn không nên mang thai;;;s_answer;;;B. Tư vấn điều trị trước khi mang thai;;;s_answer;;;C. Tư vấn nên truyền máu trước khi mang thai;;;s_answer;;;D. Xét nghiệm huyết đồ cho chồng', 'D. Xét nghiệm huyết đồ cho chồng', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1612, 'text', 'Sản khoa', 'Khoảng cách ngắn nhất từ mỏm nhô xương cùng đến khớp mu được gọi là đường kính:', '', 'A. Đường kính lưỡng mỏm gai ;;;s_answer;;;B. Đường kính nhô hậu vệ;;;s_answer;;;C. Đường kính nhô hạ vệ;;;s_answer;;;D. Đường kính nhô thượng vệ', 'B. Đường kính nhô hậu vệ', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1613, 'text', 'Sản khoa', 'Những yếu tố nào sau đây có thể ảnh hưởng đến kết quả của Non – stress test', '', 'A. Cơn co tử cung;;;s_answer;;;B. Tuổi thai;;;s_answer;;;C. Tư thế nằm ngửa;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1614, 'text', 'Sản khoa', 'Khi đánh giá (đọc) một biểu đồ đo Tim Thai cơn gò tử cung bằng monitor sản khoa cần phải xem:', '', 'A. Nhịp tim thai cơ bản;;;s_answer;;;B. Dao động nội tại - Nhịp tăng , nhịp giảm;;;s_answer;;;C. Cơn co tử cung – Trương lực cơ bản;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1615, 'text', 'Sản khoa', 'Một sản phụ tiền sản giật nặng được cho truyền tĩnh mạch Nicardipine liều  tấn công. Bạn cần phải', '', 'A. Đo HA, M SpO2, đo  ECG trước khi dùng thuốc;;;s_answer;;;B. Đo HA, M , SpO2 mỗi 3 giờ /lần ;;;s_answer;;;C. Đo HA, M , SpO2 liên tục trên monitor mỗi 15 phút và đánh giá tim thai, cơn gò tử cung với monitor sản khoa đến khi HA ≤ 140/90;;;s_answer;;;D. A và C đúng', 'D. A và C đúng', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1616, 'text', 'Sản khoa', 'Một sản phụ nhập viện vì thai quá ngày được làm Non- stress test không đáp ứng, theo bạn hướng xử trí tiếp theo là:', '', 'A. Làm lại Non – stress test sau 3 ngày;;;s_answer;;;B. Làm stress test ;;;s_answer;;;C. Giục sanh chấm dứt thai kỳ;;;s_answer;;;D. Mổ lấy thai', 'B. Làm stress test', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1617, 'text', 'Sản khoa', 'So với sổ nhau tự nhiên, sổ nhau tích cực có ưu điểm:', '', 'A. Giảm nguy cơ băng huyết sau sanh;;;s_answer;;;B. Rút ngắn thời gian sổ nhau;;;s_answer;;;C. Giảm nguy cơ soát lòng tử cung;;;s_answer;;;D. A và B đúng', 'D. A và B đúng', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1618, 'text', 'Sản khoa', 'Siêu âm trong thời điểm nào có khả năng phát hiện được hầu hết các dị tật thai:', '', 'A. 12 - 15 tuần ;;;s_answer;;;B. 16 - 19 tuần;;;s_answer;;;C. 20 - 24 tuần ;;;s_answer;;;D. 25 - 28 tuần', 'C. 20 - 24 tuần', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1619, 'text', 'Sản khoa', 'Thai chậm tăng trưởng trong tử cung, thường kết hợp các biến chứng sớm sau đây:', '', 'A. Thai chết lưu ;;;s_answer;;;B. Rối loạn điện giải bé sau sanh;;;s_answer;;;C. Hạ đường huyết ở bé sau sanh;;;s_answer;;;D. Bé chậm phát triển tâm thần sau sanh', 'A. Thai chết lưu', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1620, 'text', 'Sản khoa', 'Xử trí tích cực giai đoạn 3, Oxytocin được tiêm bắp 10 đv khi', '', 'A. Vừa sổ đầu;;;s_answer;;;B. Vừa sổ thai;;;s_answer;;;C. Trước khi kẹp cắt rốn;;;s_answer;;;D. Sau khi chắc chắn không còn thai nào trong tử cung', 'D. Sau khi chắc chắn không còn thai nào trong tử cung', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1621, 'text', 'Sản khoa', 'Trong chăm sóc thiết yếu cho bà mẹ và trẻ sơ sinh trong và ngay sau đẻ, việc tiêm vitamin K cho bé nên được thực hiện tốt nhất khi:', '', 'A. Ngay sau sanh ;;;s_answer;;;B. Sau sanh 1 giờ;;;s_answer;;;C. Sau sanh 2 giờ ;;;s_answer;;;D. Sau sanh 6 giờ', 'C. Sau sanh 2 giờ', 1, 1, NULL, 1596183428, 1596183428, NULL),
