(1623, 'text', 'Sản khoa', 'Điểm mốc của ngôi ngang là', '', 'A. Mỏm vai ;;;s_answer;;;B. Khuỷu tay;;;s_answer;;;C. Bụng;;;s_answer;;;D. Lưng', 'A. Mỏm vai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1624, 'text', 'Sản khoa', '  Trong chăm sóc thiết yếu cho bà mẹ và trẻ sơ sinh trong và ngay sau đẻ, mục đích của việc da kề da là, ngoại trừ:', '', 'A. Ủ ấm bé;;;s_answer;;;B. Cho bú sớm;;;s_answer;;;C. Tập cho mẹ biết chăm sóc bé ;;;s_answer;;;D. Giảm stress cho mẹ và bé', 'C. Tập cho mẹ biết chăm sóc bé', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1625, 'text', 'Sản khoa', '"Sắp xếp các bước sau cho đúng thứ tự trong  nguyên tắc xử trí kẹt vai”\n1. Gọi người hỗ trợ: sản khoa, hồi sức sơ sinh, gây mê.\n2. Thủ thuật Mc Roberts, \n3. Cắt rộng TSM\n4. Ấn trên xương mu', '', 'A. 1,2,3,4;;;s_answer;;;B. 2,1,3,4;;;s_answer;;;C. 3,1,2,4;;;s_answer;;;D. 1,3,4,2', 'D. 1,3,4,2', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1626, 'text', 'Sản khoa', 'Trong chuyển dạ, yếu tố nào sau đây có ảnh hưởng trực tiếp đến thai nhi nhiều nhất?', '', 'A. Cơn gò tử cung;;;s_answer;;;B. Tình trạng ối;;;s_answer;;;C. Tư thế của mẹ;;;s_answer;;;D. Tình trạng nhau thai', 'A. Cơn gò tử cung', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1627, 'text', 'Sản khoa', 'Mục đích của việc kẹp cắt dây rốn muộn là', '', 'A. Chỉ kẹp và cắt sau khi sổ thai 30 giây;;;s_answer;;;B. Giúp cho trẻ nhận được lượng sắt dự trữ trong 6 tháng đầu sau đẻ;;;s_answer;;;C. Rất có nguy cơ là cho trẻ bị vàng da tan máu;;;s_answer;;;D. Áp dụng cho tất cả các trường hợp đẻ đường âm đạo', 'B. Giúp cho trẻ nhận được lượng sắt dự trữ trong 6 tháng đầu sau đẻ', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1628, 'text', 'Sản khoa', 'Đối tượng thai phụ nào sau đây không có chỉ định thực hiện test sàng lọc để tầm soát dị tật thai:', '', 'A. Cha bệnh viêm gan siêu vi B ;;;s_answer;;;B. Tiền sử sanh con có hội chứng Down;;;s_answer;;;C. Mẹ nhiễm Rubella trong thai kỳ;;;s_answer;;;D. Mẹ tuổi > 35', 'A. Cha bệnh viêm gan siêu vi B', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1629, 'text', 'Sản khoa', '  Bánh nhau như một cái đĩa úp vào mặt trong tử cung. Bánh nhau có:', '', 'A. Đường kính từ 16 – 20cm, dày nhất ở trung tâm từ 2 – 3cm.;;;s_answer;;;B. Đường kính từ 10 – 20cm, dày nhất ở trung tâm từ 2 – 3cm.;;;s_answer;;;C. Đường kính từ 10 – 20cm, dày nhất ở trung tâm từ 1 – 2cm.;;;s_answer;;;D. Đường kính từ 16 – 20cm, dày nhất ở trung tâm từ 1 – 2cm.', 'A. Đường kính từ 16 – 20cm, dày nhất ở trung tâm từ 2 – 3cm.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1630, 'text', 'Sản khoa', 'Trong quá trình theo dõi chuyển dạ ở pha tiềm tàng, các cơn co tử cung được theo dõi mỗi:', '', 'A. 30 phút một lần ;;;s_answer;;;B. 1 giờ một lần ;;;s_answer;;;C. 2 giờ một lần;;;s_answer;;;D. 3 giờ một lần', 'B. 1 giờ một lần', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1631, 'text', 'Sản khoa', 'Trong quá trình theo dõi chuyển dạ ở pha tích cực, nhịp tim thai được theo dõi mỗi:', '', 'A. 30 phút một lần ;;;s_answer;;;B. 1 giờ một lần;;;s_answer;;;C. 2 giờ một lần;;;s_answer;;;D. 3 giờ một lần', 'A. 30 phút một lần', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1632, 'text', 'Sản khoa', 'Tiêm Vitamine K 0,1 mg, tiêm bắp cho bé sau sanh, có tác dụng ngăn ngừa:', '', 'A. Xuất huyết não cho bé ;;;s_answer;;;B. Xuất huyết rốn cho bé;;;s_answer;;;C. Xuất huyết dưới da cho bé;;;s_answer;;;D. Tất cả các câu trên đều sai', 'A. Xuất huyết não cho bé', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1633, 'text', 'Sản khoa', 'Theo ACOG 2009, biểu đồ tim thai – cơn gò thuộc nhóm III bao gồm', '', 'A. Mất dao động nội tại và 1 trong những dấu hiệu sau: xuất hiện nhịp giảm sớm, nhịp chậm;;;s_answer;;;B. Mất dao động nội tại và 1 trong những dấu hiệu sau: xuất hiện nhịp hình sin, nhịp giảm sớm;;;s_answer;;;C. Mất dao động nội tại và 1 trong những dấu hiệu sau: xuất hiện nhip giảm muộn, nhịp giảm bất định, nhịp hinh sin ;;;s_answer;;;D. "Dao động nội tại bình thường và 1 trong những dấu hiệu sau: nhịp giảm sớm, nhịp giảm muộn, nhịp chậm"', 'C. Mất dao động nội tại và 1 trong những dấu hiệu sau: xuất hiện nhip giảm muộn, nhịp giảm bất định, nhịp hinh sin', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1634, 'text', 'Sản khoa', 'Tim thai có thể nghe được qua Doppler khi thai được', '', 'A. 10 tuần tuổi ;;;s_answer;;;B. 12 tuần tuổi ;;;s_answer;;;C. 14 tuần tuổi;;;s_answer;;;D. Khi thai máy', 'B. 12 tuần tuổi', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1635, 'text', 'Sản khoa', 'Trong thai kỳ, nên siêu âm ít nhất:', '', 'A. Mỗi lần khám thai;;;s_answer;;;B. Mỗi tam cá nguyệt 1 lần;;;s_answer;;;C. Chưa có sự đồng thuận thống nhất về số lần ;;;s_answer;;;D. Mỗi khi có vấn đề', 'C. Chưa có sự đồng thuận thống nhất về số lần', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1636, 'text', 'Sản khoa', 'Trong thai kỳ, các xét nghiệm máu là cần thiết, ngoại trừ:', '', 'A. HIV;;;s_answer;;;B. Viêm gan B;;;s_answer;;;C. Thương hàn ;;;s_answer;;;D. Công thức máu', 'C. Thương hàn', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1637, 'text', 'Sản khoa', 'Khám và tư vấn trước khi mang thai nhằm:', '', 'A. Đánh giá nguy cơ có thể mắc phải ở thai phụ lần này;;;s_answer;;;B. Tiên đoán bệnh lý mạn tính có thể nặng lên khi mang thai  ;;;s_answer;;;C. Phân lập nhóm đối tượng nguy cơ trước khi mang thai ;;;s_answer;;;D. Tư vấn chế độ dinh dưỡng trước khi mang thai', 'C. Phân lập nhóm đối tượng nguy cơ trước khi mang thai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1638, 'text', 'Sản khoa', 'Cách nào thường được dùng để chẩn đoán sớm có thai', '', 'A. Xác định hCG trong nước tiểu ;;;s_answer;;;B. Xác định hCG trong  máu;;;s_answer;;;C. Siêu âm ngả bụng;;;s_answer;;;D. Siêu âm ngả âm đạo', 'A. Xác định hCG trong nước tiểu', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1639, 'text', 'Sản khoa', 'Tính ngày dự sanh ở thai phụ có chu kỳ kinh nguyệt không đều dựa vào', '', 'A. Công thức Nagaelee;;;s_answer;;;B. Công thức tính theo ngày rụng trứng;;;s_answer;;;C. Vòng xoay tính tuổi thai;;;s_answer;;;D. Siêu âm vào 7 tuần vô kinh', 'D. Siêu âm vào 7 tuần vô kinh', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1640, 'text', 'Sản khoa', 'Nhiễm khuẩn hậu sản', '', 'A. Nhiễm khuẩn từ bộ phận sinh dục;;;s_answer;;;B. Đường vào của vi khuẩn từ bộ phận sinh dục theo đường máu;;;s_answer;;;C. Các nhiễm khuẩn xuất phát từ bộ phận sinh dục trong thời kỳ hậu sản ;;;s_answer;;;D. Nhiễm khuẩn ngược dòng từ âm đạo, CTC qua đường sinh dục', 'C. Các nhiễm khuẩn xuất phát từ bộ phận sinh dục trong thời kỳ hậu sản', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1641, 'text', 'Sản khoa', '  Nguyên nhân viêm nội mạc tử cung', '', 'A. Sót nhau sót màng;;;s_answer;;;B. Nhiễm khuẩn ối, chuyển dạ kéo dài;;;s_answer;;;C. Thủ thuật bóc nhau, kiểm soát tử cung không đảm bảo vô khuẩn;;;s_answer;;;D. Tất cả các câu trên', 'D. Tất cả các câu trên', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1642, 'text', 'Sản khoa', '  Triệu chứng nhiễm trùng hậu sản viêm nội mạc tử cung', '', 'A. Sốt 38- 38°5C;;;s_answer;;;B. Sản dịch hôi, tử cung co hồi chậm;;;s_answer;;;C. A & B đúng ;;;s_answer;;;D. Tầng sinh môn sưng tấy, đau', 'C. A & B đúng', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1643, 'text', 'Sản khoa', 'Sự co hồi tử cung sau sanh câu nào sai', '', 'A. Sau sanh tử cung có cầu an toàn;;;s_answer;;;B. Cho con bú sữa mẹ giúp tử cung co hồi tốt hơn;;;s_answer;;;C. Trung bình sau sanh tử cung co hồi 1cm mỗi ngày;;;s_answer;;;D. Co hồi tử cung ở con rạ thường nhanh hơn con so', 'D. Co hồi tử cung ở con rạ thường nhanh hơn con so', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1644, 'text', 'Sản khoa', '  Đối với thai chậm tăng trưởng trong tử cung ở 3 tháng giữa thai kỳ, xử trí ưu tiên hàng đầu là:', '', 'A. Cho nhập viện để theo dõi tình trạng thai;;;s_answer;;;B. Tìm nguyên nhân gây ra thai chậm tăng trưởng ;;;s_answer;;;C. Đánh giá độ trưởng thành phổi của thai nhi;;;s_answer;;;D. Thực hiện NST, siêu âm mỗi tuần', 'B. Tìm nguyên nhân gây ra thai chậm tăng trưởng', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1645, 'text', 'Sản khoa', 'Biến chứng chọc ối (chọn câu sai):', '', 'A. Sa dây rốn ;;;s_answer;;;B. Sanh non;;;s_answer;;;C. Nhiễm trùng ;;;s_answer;;;D. Ối vỡ non', 'A. Sa dây rốn', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1646, 'text', 'Sản khoa', 'Stress test (ST) được chỉ định trong trường hợp', '', 'A. Không có tăng nhịp tim thai tiếp theo sau cử động thai ;;;s_answer;;;B. Hở eo tử cung;;;s_answer;;;C. Định độ trưởng thành của thai;;;s_answer;;;D. Thai suy dinh dưỡng', 'A. Không có tăng nhịp tim thai tiếp theo sau cử động thai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1647, 'text', 'Sản khoa', 'TSG được xác định là nặng khi', '', 'A. Chỉ cần một trong các triệu chứng sau: HA ≥ 160/110 mmHg, Protein niệu ≥ 5g;;;s_answer;;;B. HA 140/90 mmHg, phù (+), Protein niệu (+);;;s_answer;;;C. HA ≥ 160/110 mmHg, Protein niệu > 5g/ 24 giờ, thiểu niệu ≤ 400 mml/ 24 giờ ;;;s_answer;;;D. B và C sai', 'C. HA ≥ 160/110 mmHg, Protein niệu > 5g/ 24 giờ, thiểu niệu ≤ 400 mml/ 24 giờ', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1648, 'text', 'Sản khoa', 'Đối với thai phụ có ĐTĐ cần lưu ý như thế nào khi tập thể dục?', '', 'A. Mặc quần áo kín, giữ ấm ;;;s_answer;;;B. Uống nhiều nước trước, trong và sau khi tập luyện ;;;s_answer;;;C. Tập ít nhất 3 lần/ tuần, mỗi lần ít nhất 30 phút;;;s_answer;;;D. Tất cả đều đúng', 'B. Uống nhiều nước trước, trong và sau khi tập luyện', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1649, 'text', 'Sản khoa', 'Khám thấy đỉnh xương cùng ở vị trí 4 giờ, kiểu thế là:', '', 'A. Cùng chậu trái trước.;;;s_answer;;;B. Cùng chậu trái sau. ;;;s_answer;;;C. Cùng chậu phải trước.;;;s_answer;;;D. Cùng chậu phải sau.', 'B. Cùng chậu trái sau.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1650, 'text', 'Sản khoa', 'Loại forceps được dùng trong đỡ đầu hậu khi sanh ngôi mông:', '', 'A. Simpson.;;;s_answer;;;B. KeillanD.;;;s_answer;;;C. Piper. ;;;s_answer;;;D. Babcook.', 'C. Piper.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1651, 'text', 'Sản khoa', 'Điểm mốc của ngôi mặt là:', '', 'A. Đỉnh mũi.;;;s_answer;;;B. Miệng.;;;s_answer;;;C. Cằm. ;;;s_answer;;;D. Gò má.', 'C. Cằm.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1652, 'text', 'Sản khoa', 'Điểm mốc của ngôi trán là:', '', 'A. Đỉnh mũi.;;;s_answer;;;B. Mắt.;;;s_answer;;;C. Gốc mũi.;;;s_answer;;;D. Gò má.', 'C. Gốc mũi.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1653, 'text', 'Sản khoa', 'PARA thể hiện qua . . . . . số:', '', 'A. 3;;;s_answer;;;B. 4;;;s_answer;;;C. 5;;;s_answer;;;D. 6', 'B. 4', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1654, 'text', 'Sản khoa', 'Trong PARA con số thứ 4 thể hiện:', '', 'A. Số con bà ta đã sinh ra;;;s_answer;;;B. Số con bà ta đã sanh thường;;;s_answer;;;C. Số con hiện còn sống ;;;s_answer;;;D. Số con sanh quá ngày', 'C. Số con hiện còn sống', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1655, 'text', 'Sản khoa', 'Một sản phụ đến khám thai định kỳ. Tiền căn của bà ta: Cách đây 5 năm bà ta sanh thường 1 lần, thai 38 tuần, bé sống. Cách đây 3 năm bà ta mổ lấy thai 1 lần với chẩn đoán là “Thai 32 tuần nhau tiền đạo trung tâm ra huyết nhiều”, bé chết do suy hô hấp. Bà ta không có sẩy thai lần nào. PARA của bà ta vào thời điểm đến khám là:', '', 'A. 2.0.1.1.;;;s_answer;;;B. 1.1.1.1.;;;s_answer;;;C. 1.1.0.1. ;;;s_answer;;;D. 1.2.0.1.', 'C. 1.1.0.1.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1656, 'text', 'Sản khoa', '  Tiền căn của sản phụ: Cách đây 8 năm bà ta mổ lấy thai 1 lần với chẩn đoán “Thai 42 tuần, thiểu ối”, bé sống. Cách đây 5 năm bà ta mổ lấy thai 1 lần với chẩn đoán là “Thai 38 tuần, đau vết mổ cũ”, bé chết lúc 2 tuổi do sốt xuất huyết. Cách đây 2 năm bà ta bị hư thai 1 lần lúc thai 8 tuần. PARA của bà ta vào thời điểm đến khám là :', '', 'A. 2.1.1.1.;;;s_answer;;;B. 1.1.1.1.;;;s_answer;;;C. 2.0.1.1. ;;;s_answer;;;D. 1.0.1.1.', 'C. 2.0.1.1.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1657, 'text', 'Sản khoa', 'Tiền căn của sản phụ: Cách đây 5 năm bà ta sanh thường 1 lần, thai 42 tuần, bé sống. Cách đây 3 năm bà ta mổ 1 lần với chẩn đoán là “Thai ngoài tử cung bên trái vỡ”. Cách đây 2 năm bà ta bị hư thai 1 lần lúc thai được 10 tuần. PARA của bà ta vào thời điểm đến khám là:', '', 'A. 1.0.2.1. ;;;s_answer;;;B. 0.0.1.1.;;;s_answer;;;C. 1.0.1.1. ;;;s_answer;;;D. 1.0.0.1.', 'A. 1.0.2.1.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1658, 'text', 'Sản khoa', 'Mục đích của thủ thuật Leopold là:', '', 'A. Giúp đầu thai nhi cúi tốt trong khi đỡ sanh ngôi mông.;;;s_answer;;;B. Làm giảm sự chèn ép của dây rốn khi có sa dây rốn. ;;;s_answer;;;C. Xác định ngôi và thế của thai. ;;;s_answer;;;D. Xác định xem nhau có bong hay chưa.', 'C. Xác định ngôi và thế của thai.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1659, 'text', 'Sản khoa', 'Thủ thuật Leopold có . . . thì :', '', 'A. 2;;;s_answer;;;B. 3;;;s_answer;;;C. 4;;;s_answer;;;D. 5', 'C. 4', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1660, 'text', 'Sản khoa', 'Trong thì thứ 4 của thủ thuật Leopold khi hai bàn tay của thầy thuốc không hội tụ được thì ta kết luận:', '', 'A. Độ lọt của thai là 5/5. ;;;s_answer;;;B. Thai đã lọt. ;;;s_answer;;;C. Độ lọt của thai là 0. ;;;s_answer;;;D. Độ lọt thấp.', 'B. Thai đã lọt.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1661, 'text', 'Sản khoa', 'Hãy chọn cách báo cáo kết quả đúng sau khi thực hiện thủ thuật Leopold:', '', 'A. Ngôi chẩm - Thế trái - Chưa lọt.;;;s_answer;;;B. Ngôi đầu - Thế phải - Lọt +1. ;;;s_answer;;;C. Ngôi đầu - Thế trái - Đã lọt.;;;s_answer;;;D. Ngôi chẩm - Thế phải - Lọt 0.', 'C. Ngôi đầu - Thế trái - Đã lọt.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1662, 'text', 'Sản khoa', '  Ống nghe tim thai có tên là:', '', 'A. Beaudeloque.;;;s_answer;;;B. Pinard. ;;;s_answer;;;C. Duncan.;;;s_answer;;;D. Jacquemier.', 'B. Pinard.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1663, 'text', 'Sản khoa', 'Vị trí nghe tim thai (trên thành bụng) thường gặp nhất là:', '', 'A. 1/4 trên bên trái.;;;s_answer;;;B. 1/4 trên bên phải. ;;;s_answer;;;C. 1/4 dưới bên trái. ;;;s_answer;;;D. 1/4 dưới bên phải', 'C. 1/4 dưới bên trái.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1664, 'text', 'Sản khoa', 'Thủ thuật Leopold là ngôi mông, thế trái. Vị trí nghe tim thai (trên thành bụng) là:', '', 'A. Dưới rốn, bên trái của sản phụ. ;;;s_answer;;;B. Dưới rốn, bên phải của sản phụ. ;;;s_answer;;;C. Trên rốn, bên trái của sản phụ. ;;;s_answer;;;D. Trên rốn, bên phải của sản phụ.', 'C. Trên rốn, bên trái của sản phụ.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1665, 'text', 'Sản khoa', 'Chuyển dạ được chia làm . . . . giai đoạn:', '', 'A. 2;;;s_answer;;;B. 3;;;s_answer;;;C. 4;;;s_answer;;;D. 5', 'B. 3', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1666, 'text', 'Sản khoa', 'Thời gian chuyển dạ ở người con so thường . . . . . . . . so với người con rạ:', '', 'A. Ngắn hơn.;;;s_answer;;;B. Dài hơn ;;;s_answer;;;C. Tương đương', 'B. Dài hơn', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1667, 'text', 'Sản khoa', 'Thời gian sổ thai trung bình của người con so là:', '', 'A. 20 phút.;;;s_answer;;;B. 30 phút.;;;s_answer;;;C. 40 phút. ;;;s_answer;;;D. 50 phút', 'C. 40 phút.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1668, 'text', 'Sản khoa', 'Khi khám khung chậu, “góc vòm vệ” dùng để đánh giá:', '', 'A. Đường kính ngang của eo trên.;;;s_answer;;;B. Đường kính chéo của eo trên. ;;;s_answer;;;C. Đường kính ngang của eo giữa. ;;;s_answer;;;D. Đường kính ngang của eo dưới.', 'D. Đường kính ngang của eo dưới.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1669, 'text', 'Sản khoa', 'Đường kính mỏm nhô - hậu vệ bằng:', '', 'A. Đường kính mỏm nhô - hạ vệ + 1,5 cm. ;;;s_answer;;;B. Đường kính mỏm nhô - hạ vệ - 1,5 cm. ;;;s_answer;;;C. Đường kính mỏm nhô - hạ vệ + 0,5 cm. ;;;s_answer;;;D. Đường kính mỏm nhô - hạ vệ + 0,5 cm.', 'B. Đường kính mỏm nhô - hạ vệ - 1,5 cm.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1670, 'text', 'Sản khoa', 'Ký hiệu nào sau đây chứng tỏ chỉ khâu này là loại chỉ tan:', '', 'A. Có chữ Polyglactin.;;;s_answer;;;B. Có chữ Absorbable. ;;;s_answer;;;C. Có chữ Vicryl.;;;s_answer;;;D. Có chữ Ethicon.', 'B. Có chữ Absorbable.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1671, 'text', 'Sản khoa', 'Trong mổ lấy thai có thể khâu phúc mạc bằng chỉ', '', 'A. Silk 1.;;;s_answer;;;B. Silk 2/0.;;;s_answer;;;C. Chromic 2/0. ;;;s_answer;;;D. Chromic 1.', 'C. Chromic 2/0.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1672, 'text', 'Sản khoa', 'Loại chỉ nào sau đây có kích thước lớn nhất:', '', 'A. Silk 1. ;;;s_answer;;;B. Silk 0.;;;s_answer;;;C. Silk 2/0.;;;s_answer;;;D. Silk 3/0.', 'A. Silk 1.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1673, 'text', 'Sản khoa', 'Gọi là cơn co tử cung tốt khi', '', 'A. Có 3 cơn co/ 10 phút. ;;;s_answer;;;B. Gây đau.;;;s_answer;;;C. Trương lực cơ bản tăng. ;;;s_answer;;;D. Tất cả đều sai.', 'D. Tất cả đều sai.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1674, 'text', 'Sản khoa', 'Cơn co tử cung thưa có thể gây:', '', 'A. Chuyển dạ kéo dài. ;;;s_answer;;;B. Suy thai.;;;s_answer;;;C. Vỡ tử cung.;;;s_answer;;;D. Nhau bong non.', 'A. Chuyển dạ kéo dài.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1675, 'text', 'Sản khoa', 'Khi cơn co tử cung thưa, có thể dùng . . . . . để tăng cơn co tử cung:', '', 'A. Methergin.;;;s_answer;;;B. Oxytocin. ;;;s_answer;;;C. Cytotec.;;;s_answer;;;D. Laminare.', 'B. Oxytocin.', 1, 1, NULL, 1596183428, 1596183428, NULL);
INSERT INTO `qandas` (`id`, `type_question`, `knowledge`, `question`, `image`, `answer`, `right_answer`, `created_by`, `updated_by`, `deleted_by`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1676, 'text', 'Sản khoa', ' Khi sờ thấy thóp sau ở vị trí 2 giờ, kiểu thế là:', '', 'A. Chẩm chậu trái trước. ;;;s_answer;;;B. Chẩm chậu trái sau.;;;s_answer;;;C. Chẩm chậu phải trước. ;;;s_answer;;;D. Chẩm chậu phải sau.', 'A. Chẩm chậu trái trước.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1677, 'text', 'Sản khoa', 'Trong ngôi chẩm thai thường sổ theo kiểu thế:', '', 'A. Chẩm chậu trái trước.;;;s_answer;;;B. Chẩm cùng.;;;s_answer;;;C. Chẩm chậu phải trước. ;;;s_answer;;;D. Chẩm vệ.', 'D. Chẩm vệ.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1678, 'text', 'Sản khoa', 'Khi ối vỡ, sờ thấy đỉnh xương cùng ở vị trí 7 giờ, kiểu thế trong trường hợp này là:', '', 'A. Cùng chậu trái trước.;;;s_answer;;;B. Cùng chậu phải trước. ;;;s_answer;;;C. Cùng chậu trái sau.;;;s_answer;;;D. Cùng chậu phải sau.', 'D. Cùng chậu phải sau.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1679, 'text', 'Sản khoa', 'Trên lâm sàng để phân biệt “thóp trước”và “thóp sau” thường dựa vào yếu tố:', '', 'A. Thóp trước to hơn thóp sau.;;;s_answer;;;B. Thóp trước gần gốc mũi hơn thóp sau.;;;s_answer;;;C. Thóp trước được tạo bởi 4 xương, thóp sau được tạo bởi 3 xương. ;;;s_answer;;;D. Thóp trước hình thoi (◊), thóp sau hình lamda (λ).', 'C. Thóp trước được tạo bởi 4 xương, thóp sau được tạo bởi 3 xương.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1680, 'text', 'Sản khoa', 'Thời điểm tầm soát hội chứng Down (trên SA) tốt nhất bắt đầu vào tuần thứ . . . của thai kỳ:', '', 'A. 11 - 14. ;;;s_answer;;;B. 15 - 18.;;;s_answer;;;C. 19 - 22.;;;s_answer;;;D. 23 - 26.', 'A. 11 - 14.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1681, 'text', 'Sản khoa', 'Để tầm soát hội chứng Down của thai nhi trên SA, dựa vào . . . của thai nhi:', '', 'A. Bề dầy độ mờ da gáy. ;;;s_answer;;;B. Đường kính ngang bụng. ;;;s_answer;;;C. Chiều dài cột sống.;;;s_answer;;;D. Chu vi vòng đầu.', 'A. Bề dầy độ mờ da gáy.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1682, 'text', 'Sản khoa', 'Thời điểm tiêm ngừa uốn ván tốt nhất là:', '', 'A. Ba tháng đầu thai kỳ. ;;;s_answer;;;B. Ba tháng giữa thai kỳ. ;;;s_answer;;;C. Ba tháng cuối thai kỳ. ;;;s_answer;;;D. Ngay sau sanh.', 'B. Ba tháng giữa thai kỳ.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1683, 'text', 'Sản khoa', '  Thời điểm tầm soát dị dạng của thai nhi bằng siêu âm tốt nhất là:', '', 'A. Ba tháng đầu thai kỳ.;;;s_answer;;;B. Ba tháng giữa thai kỳ. ;;;s_answer;;;C. Thời điểm nào cũng thích hợp.;;;s_answer;;;D. Ba tháng cuối thai kỳ.', 'B. Ba tháng giữa thai kỳ.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1684, 'text', 'Sản khoa', 'Được gọi là thai đủ tháng khi:', '', 'A. Thai 37 tuần theo SA 3 tháng giữa thai kỳ.;;;s_answer;;;B. Thai 37 tuần theo kinh cuối, chu kỳ 28 - 35 ngày.;;;s_answer;;;C. Thai 37 tuần theo SA 3 tháng đầu thai kỳ. ;;;s_answer;;;D. Thai 37 tuần theo SA 3 tháng cuối thai kỳ.', 'C. Thai 37 tuần theo SA 3 tháng đầu thai kỳ.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1685, 'text', 'Sản khoa', 'Định nghĩa “thai kỳ nguy cơ cao” là:', '', 'A. Thai kỳ có nguy cơ sinh non.;;;s_answer;;;B. Thai kỳ có nguy cơ mổ lấy thai.;;;s_answer;;;C. Thai kỳ có nguy cơ băng huyết sau sanh.;;;s_answer;;;D. Thai kỳ có ảnh hưởng đến sức khỏe (có thể gây tử vong) cho mẹ và con.', 'D. Thai kỳ có ảnh hưởng đến sức khỏe (có thể gây tử vong) cho mẹ và con.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1686, 'text', 'Sản khoa', 'Nói về xử trí “thai kỳ nguy cơ cao”:', '', 'A. Nên được theo dõi chuyển dạ ở nơi có điều kiện phẫu thuật. ;;;s_answer;;;B. Nên được mổ lấy thai chủ động.;;;s_answer;;;C. Nên được bóc nhau nhân tạo và kiểm soát tử cung sau sanh. ;;;s_answer;;;D. Nên được vận động triệt sản.', 'A. Nên được theo dõi chuyển dạ ở nơi có điều kiện phẫu thuật.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1687, 'text', 'Sản khoa', 'Đối với những “thai kỳ nguy cơ cao” thì anh (chị) NÊN:', '', 'A. Khuyên sản phụ chấm dứt thai kỳ ngay vì nếu không sẽ gây nguy hiểm cho mẹ và con. ;;;s_answer;;;B. Nên được theo dõi chuyển dạ ở nơi có điều kiện phẫu thuật. ;;;s_answer;;;C. Khuyên sản phụ đi khám thai mỗi tuần 1 lần từ 3 tháng giữa thai kỳ. ;;;s_answer;;;D. Khuyên sản phụ nhập viện khi thai được 36 tuần.', 'B. Nên được theo dõi chuyển dạ ở nơi có điều kiện phẫu thuật.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1688, 'text', 'Sản khoa', 'Trong thai kỳ sản phụ NÊN được tiêm ngừa', '', 'A. Viêm gan siêu vi B. ;;;s_answer;;;B. HIV.;;;s_answer;;;C. Uốn ván. ;;;s_answer;;;D. Rubella.', 'C. Uốn ván.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1689, 'text', 'Sản khoa', 'Thời điểm làm công thức máu để phát hiện thiếu máu trong thai kỳ tốt nhất là:', '', 'A. Ba tháng đầu thai kỳ. ;;;s_answer;;;B. Ba tháng giữa thai kỳ. ;;;s_answer;;;C. Ba tháng cuối thai kỳ.;;;s_answer;;;D. Bất kỳ thời điểm nào trong thai kỳ.', 'A. Ba tháng đầu thai kỳ.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1690, 'text', 'Sản khoa', 'Sản phụ nào sau đây có nguy cơ bị nhau tiền đạo nhất:', '', 'A. PARA: 0.0.0.0;;;s_answer;;;B. PARA: 1.0.0.1;;;s_answer;;;C. PARA: 1.0.1.0;;;s_answer;;;D. PARA: 2.1.3.2', 'D. PARA: 2.1.3.2', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1691, 'text', 'Sản khoa', 'Thể lâm sàng thường gặp nhất trong cao huyết áp và thai là:', '', 'A. Cao huyết áp đơn thuần.;;;s_answer;;;B. Cao huyết áp do thai (tiền sản giật). ;;;s_answer;;;C. Cao huyết áp mãn và thai.;;;s_answer;;;D. Cao huyết áp mãn nặng lên do thai.', 'B. Cao huyết áp do thai (tiền sản giật).', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1692, 'text', 'Sản khoa', 'Trong tiền sản giật khi sản phụ than đau hạ sườn phải, nguyên nhân NÊN được nghĩ đến đầu tiên là:', '', 'A. Sản phụ bị đau dạ dày. ;;;s_answer;;;B. Sản phụ bị sỏi túi mật.;;;s_answer;;;C. Sản phụ bị xuất huyết trong bao gan. ;;;s_answer;;;D. Sản phụ bị viêm tụy cấp.', 'C. Sản phụ bị xuất huyết trong bao gan.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1693, 'text', 'Sản khoa', 'Thuốc dùng để ngừa sản giật là:', '', 'A. Hydralazine.;;;s_answer;;;B. Sulfate Magnesium ;;;s_answer;;;C. Furosemid.;;;s_answer;;;D. Seduxen', 'B. Sulfate Magnesium', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1694, 'text', 'Sản khoa', 'Khi được tiêm Sulfate Magnesium sản phụ sẽ cảm thấy . . . nơi tiêm:', '', 'A. Lạnh.;;;s_answer;;;B. Nóng. ;;;s_answer;;;C. Tê.;;;s_answer;;;D. Ngứa.', 'B. Nóng.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1695, 'text', 'Sản khoa', 'Liều tấn công của Sulfate Magnesium là:', '', 'A. 1 - 2 g.;;;s_answer;;;B. 4 - 6 g. ;;;s_answer;;;C. 8 - 10 g.;;;s_answer;;;D. 12 - 14 g.', 'B. 4 - 6 g.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1696, 'text', 'Sản khoa', 'Liều duy trì của Sulfate Magnesium là:', '', 'A. Mỗi 0,5g/ giờ.;;;s_answer;;;B. Mỗi 1g/ giờ. ;;;s_answer;;;C. Mỗi 1,5g/ giờ.;;;s_answer;;;D. Mỗi 2g/ giờ.', 'B. Mỗi 1g/ giờ.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1697, 'text', 'Sản khoa', 'Dùng thuốc đối kháng với Sulfate Magnesium khi:', '', 'A. Huyết áp tâm trương < 70 mmHg.;;;s_answer;;;B. Mất phản xạ gân xương. ;;;s_answer;;;C. Có biểu hiện suy thận.;;;s_answer;;;D. Khi có biểu hiện suy hô hấp.', 'D. Khi có biểu hiện suy hô hấp.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1698, 'text', 'Sản khoa', 'Trong cơ chế đẻ ngôi chỏm, thì xuống là giai đoạn di chuyển của ngôi từ:', '', 'A. Mặt phẳng eo trên đến mặt phẳng eo giữa;;;s_answer;;;B. Mặt phẳng eo trên đến mặt phẳng eo dưới;;;s_answer;;;C. Mặt phẳng eo giữa đến mặt phẳng eo dưới;;;s_answer;;;D. Mặt phẳng eo dưới đến khi thai sổ ra ngoài', 'B. Mặt phẳng eo trên đến mặt phẳng eo dưới', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1699, 'text', 'Sản khoa', 'Trong giai đoạn sổ thai, nếu cơn co tử cung thưa, thuốc được dùng để tăng co là:', '', 'A. Cytotec.;;;s_answer;;;B. Methergin.;;;s_answer;;;C. Hydralazin.;;;s_answer;;;D. Oxytocin.', 'D. Oxytocin.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1700, 'text', 'Sản khoa', 'Cổ tử cung xóa là quá trình cổ tử cung ngắn lại.', '', 'A. Đúng;;;s_answer;;;B. Sai', 'A. Đúng', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1701, 'text', 'Sản khoa', 'Biến chứng đáng sợ nhất trong đỡ sanh ngôi mông là?', '', 'A. Rách cổ tử cung.;;;s_answer;;;B. Vỡ tạng trong ổ bụng của thai nhi. ;;;s_answer;;;C. Kẹt đầu hậu. ;;;s_answer;;;D. Gãy tay do hạ tay không đúng kỹ thuật.', 'C. Kẹt đầu hậu.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1702, 'text', 'Sản khoa', 'Điểm mốc của ngôi mông là:', '', 'A. Ụ ngồi.;;;s_answer;;;B. Hậu môn.;;;s_answer;;;C. Đỉnh xương cùng. ;;;s_answer;;;D. Cơ quan sinh dục.', 'C. Đỉnh xương cùng.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1703, 'text', 'Sản khoa', 'Ngôi mông có thể lầm với:', '', 'A. Ngôi mặt. ;;;s_answer;;;B. Ngôi trán.;;;s_answer;;;C. Ngôi thóp trước.;;;s_answer;;;D. Ngôi ngang.', 'A. Ngôi mặt.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1704, 'text', 'Sản khoa', 'Dấu hiệu lâm sàng thường gặp nhất trong thai trứng là:', '', 'A. Ra huyết kéo dài ;;;s_answer;;;B. Tử cung to hơn tuổi thai;;;s_answer;;;C. Dấu hiệu tiền sản giật;;;s_answer;;;D. Dấu hiệu cường giáp', 'A. Ra huyết kéo dài', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1705, 'text', 'Sản khoa', 'Ngôi ngang là ngôi có tỉ lệ chiếm khoảng ……. trong đó trục dọc của thai nhi cắt trục dọc của mẹ bởi một góc vuông', '', 'A. 0,01 % ;;;s_answer;;;B. 0,05 % ;;;s_answer;;;C. 0,1 % ;;;s_answer;;;D. 0,5 %', 'D. 0,5 %', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1706, 'text', 'Sản khoa', 'Trong trường hợp sanh rớt ngoại viện, những dấu hiệu của bé cần trình bác sĩ nhi, chọn câu đúng nhất:', '', 'A. Tím tái, trắng bệch, khó thở co kéo, không phản xạ ;;;s_answer;;;B. Tím tái, trắng bệch, khó thở co kéo, có phản xạ;;;s_answer;;;C. Tím tái, trắng bệch, không khó thở co kéo, có phản xạ;;;s_answer;;;D. Tím tái, khó thở co kéo, không phản xạ', 'A. Tím tái, trắng bệch, khó thở co kéo, không phản xạ', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1707, 'text', 'Sản khoa', 'Biểu hiện cơ bản nhất của giai đoạn xâm nhiễm khi sản giật là:', '', 'A. Co giật các cơ vùng đầu mặt cổ ;;;s_answer;;;B. Co giật cơ toàn thân ;;;s_answer;;;C. Co cứng cơ toàn thân ;;;s_answer;;;D. Co thắt cơ hô hấp', 'A. Co giật các cơ vùng đầu mặt cổ', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1708, 'text', 'Sản khoa', 'Được gọi là “thai quá ngày” khi:', '', 'A. Tuổi thai > 41 tuần theo siêu âm 3 tháng đầu thai kỳ. ;;;s_answer;;;B. Tuổi thai > 42 tuần theo siêu âm 3 tháng đầu thai kỳ. ;;;s_answer;;;C. Trọng lượng thai > 4000g theo siêu âm 3 tháng cuối thai kỳ.;;;s_answer;;;D. Chiều dài xương đùi > 70 mm theo siêu âm 3 tháng cuối thai kỳ.', 'B. Tuổi thai > 42 tuần theo siêu âm 3 tháng đầu thai kỳ.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1709, 'text', 'Sản khoa', 'Sản phụ A, con so. Nhập viện vào ngày 15/10/2020, trường hợp nào sau đây chẩn đoán là “thai quá ngày”:', '', 'A. Sản phụ có cảm giác thai máy lần đầu lúc 15/05/2008.;;;s_answer;;;B. Siêu âm lúc 15/02/2020, một phôi thai sống khoảng 8 tuần. ;;;s_answer;;;C. Ước lượng trọng lượng thai > 4000g.;;;s_answer;;;D. Bề cao tử cung 40 cm.', 'B. Siêu âm lúc 15/02/2020, một phôi thai sống khoảng 8 tuần.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1710, 'text', 'Sản khoa', 'Dấu hiệu nào trên siêu âm giúp chẩn đoán thai quá ngày:', '', 'A. Nhau trưởng thành độ 3.;;;s_answer;;;B. Nước ối kém thuần trạng.;;;s_answer;;;C. Đường kính ngang bụng > 100 mm. ;;;s_answer;;;D. Tất cả đều sai.', 'D. Tất cả đều sai.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1711, 'text', 'Sản khoa', 'Gọi là ối vỡ sớm:', '', 'A. Ối vỡ khi thai < 37 tuần.;;;s_answer;;;B. Ối vỡ trước khi vào chuyển dạ giai đoạn hoạt động.;;;s_answer;;;C. Ối vỡ khi đã vào chuyển dạ trước khi cổ tử cung mở trọn. ;;;s_answer;;;D. Ối vỡ khi thai chưa lọt.', 'C. Ối vỡ khi đã vào chuyển dạ trước khi cổ tử cung mở trọn.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1712, 'text', 'Sản khoa', 'Nhau bong non là nhau bong:', '', 'A. Trước tuần thứ 37.;;;s_answer;;;B. Trước khi vào chuyển dạ giai đoạn hoạt động. ;;;s_answer;;;C. Trước khi sổ thai ;;;s_answer;;;D. Trước khi có dấu hiệu bong nhau.', 'C. Trước khi sổ thai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1713, 'text', 'Sản khoa', 'Trong nhau bong non chỉ theo dõi sanh ngả âm đạo khi:', '', 'A. Thai non tháng.;;;s_answer;;;B. Thai đã chết.;;;s_answer;;;C. Máu chảy ra khỏi âm đạo ít.;;;s_answer;;;D. Mẹ chưa bị ảnh hưởng sức khỏe', 'D. Mẹ chưa bị ảnh hưởng sức khỏe', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1714, 'text', 'Sản khoa', 'Tỷ lệ vỡ tử cung trong chuyển dạ . . . . . vỡ tử cung trong thai kỳ:', '', 'A. Ít hơn. ;;;s_answer;;;B. Bằng nhau.;;;s_answer;;;C. Nhiều hơn.', 'C. Nhiều hơn.', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1715, 'text', 'Sản khoa', 'Trong dọa vỡ tử cung, biểu hiện lâm sàng nào xuất hiện trước khi có dấu hiệu vòng Banld:', '', 'A. Ra huyết âm đạo lượng nhiều. ;;;s_answer;;;B. Dấu hiện Frommel.;;;s_answer;;;C. Cơn co tử cung dồn dập ;;;s_answer;;;D. Nước tiểu có lẫn máu.', 'C. Cơn co tử cung dồn dập', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1716, 'text', 'Sản khoa', 'Gọi là thai to khi', '', 'A. Thai trên sản phụ bị tiểu đường.;;;s_answer;;;B. Thai có đường kính lưỡng đỉnh > 95 mm. ;;;s_answer;;;C. Thai có trọng lượng > 4000g ;;;s_answer;;;D. Thai có chiều dài xương đùi > 71 mm.', 'C. Thai có trọng lượng > 4000g', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1717, 'text', 'Sản khoa', 'Biến chứng nguy hiểm nhất khi cho sanh 1 trường hợp thai to là', '', 'A. Đầu cúi kém.;;;s_answer;;;B. Kẹt vai ;;;s_answer;;;C. Vỡ tạng trong ổ bụng. ;;;s_answer;;;D. Gãy chân.', 'B. Kẹt vai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1718, 'text', 'Sản khoa', 'Trong song thai, xét nghiệm nào sau đây có giá trị nhất:', '', 'A. Định lượng hCG.;;;s_answer;;;B. Siêu âm thai ;;;s_answer;;;C. X quang vùng chậu. ;;;s_answer;;;D. Soi ối.', 'B. Siêu âm thai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1719, 'text', 'Sản khoa', 'Dấu hiệu nào sau đây giúp chẩn đoán xác định song thai:', '', 'A. Bề cao tử cung to hơn tuổi thai.;;;s_answer;;;B. Sản phụ nghén nhiều. ;;;s_answer;;;C. Siêu âm thấy 2 thai ;;;s_answer;;;D. Sản phụ tăng cân nhiều.', 'C. Siêu âm thấy 2 thai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1720, 'text', 'Sản khoa', 'Thời điểm quan sát số lượng thai, bánh nhau và buồng ối tốt nhất là:', '', 'A. Ba tháng đầu thai kỳ ;;;s_answer;;;B. Ba tháng giữa thai kỳ. ;;;s_answer;;;C. Ba tháng cuối thai kỳ.;;;s_answer;;;D. Ở bất kỳ thời điểm nào trong thai kỳ.', 'A. Ba tháng đầu thai kỳ', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1721, 'text', 'Sản khoa', 'Song thai khóa có nghĩa là', '', 'A. Hai chân thai nhi khóa với nhau. ;;;s_answer;;;B. Hai tay thai nhi khóa với nhau. ;;;s_answer;;;C. Hai đầu thai nhi khóa với nhau ;;;s_answer;;;D. Chân của 1 thai khóa với đầu của thai còn lại.', 'C. Hai đầu thai nhi khóa với nhau', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1722, 'text', 'Sản khoa', 'Song thai khóa có thể xảy ra khi:', '', 'A. Thai 1 là ngôi đầu; thai 2 là ngôi mông. ;;;s_answer;;;B. Thai 1 là ngôi mông; thai 2 là ngôi mông. ;;;s_answer;;;C. Thai 1 là ngôi mông; thai 2 là ngôi ngang. ;;;s_answer;;;D. Thai 1 là ngôi mông; thai 2 là ngôi đầu', 'D. Thai 1 là ngôi mông; thai 2 là ngôi đầu', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1723, 'text', 'Sản khoa', 'Trong song thai, thai thứ 2 là ngôi ngang. Sau khi sanh thai thứ 1, thái độ xử trí đối với thai 2 là:', '', 'A. Ngoại xoay thai thành ngôi đầu và để chuyển dạ tự nhiên.;;;s_answer;;;B. Nội xoay thai thành ngôi mông và để chuyển dạ sanh tự nhiên. ;;;s_answer;;;C. Mổ lấy thai thứ 2.;;;s_answer;;;D. Nội xoay đại kéo thai', 'D. Nội xoay đại kéo thai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1724, 'text', 'Sản khoa', 'Khi nội xoay đại kéo thai cần phải biết chính xác vị trí . . . của thai nhi:', '', 'A. Đầu.;;;s_answer;;;B. Tay.;;;s_answer;;;C. Chân ;;;s_answer;;;D. Ụ ngồi.', 'C. Chân', 1, 1, NULL, 1596183428, 1596183428, NULL),
