(1732, 'text', 'Sản khoa', 'Gọi là “đau vết mổ cũ” khi', '', 'A. Sản phụ cảm thấy đau vết mổ khi cho nằm nghiêng sang 1 bên. ;;;s_answer;;;B. Sản phụ cảm thấy đau vết mổ khi ngồi.;;;s_answer;;;C. Sản phụ cảm thấy đau vết mổ khi thầy thuốc ấn ngang trên vệ (ngoài cơn co tử cung) ;;;s_answer;;;D. Sản phụ cảm thấy đau vết mổ khi có cơn co tử cung.', 'C. Sản phụ cảm thấy đau vết mổ khi thầy thuốc ấn ngang trên vệ (ngoài cơn co tử cung)', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1733, 'text', 'Sản khoa', 'Để biết vết mổ trong tử cung là dọc thân hay ngang đoạn dưới thì:', '', 'A. Xem vết mổ ngoài thành bụng.;;;s_answer;;;B. Xem giấy phẫu thuật lần trước ;;;s_answer;;;C. Siêu âm bụng.;;;s_answer;;;D. Xem lần trước mổ ở đâu.', 'B. Xem giấy phẫu thuật lần trước', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1734, 'text', 'Sản khoa', 'Dấu hiệu nghi ngờ nứt vết mổ trong tử cung là', '', 'A. Siêu âm thấy khối máu tụ ở đoạn dưới tử cung.;;;s_answer;;;B. Sản phụ than đau bụng nhiều khi có cơn co tử cung. ;;;s_answer;;;C. Ấn trên vệ ngoài cơn co tử cung sản phụ than đau ;;;s_answer;;;D. Siêu âm thấy đoạn dưới tử cung dầy < 5 mm.', 'C. Ấn trên vệ ngoài cơn co tử cung sản phụ than đau', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1735, 'text', 'Sản khoa', 'Để kiểm tra xem có nứt vết mổ sau khi sanh, NÊN :', '', 'A. Siêu âm tử cung sau sanh 2 giờ.;;;s_answer;;;B. Kiểm soát tử cung ;;;s_answer;;;C. Khám bụng tìm xem có phản ứng phúc mạc hay không? ;;;s_answer;;;D. Đặt thông tiểu và lưu 24 giờ.', 'B. Kiểm soát tử cung', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1736, 'text', 'Sản khoa', 'Sau mổ lấy thai bệnh nhân than đau lúc đi, anh (chị) sẽ:', '', 'A. Cho thuốc giảm đau và khuyên bệnh nhân tiếp tục đi.;;;s_answer;;;B. Yêu cầu bệnh nhân nằm yên, chỉ được đi khi đã hết đau.;;;s_answer;;;C. Khuyên bệnh nhân tiếp tục đi (có thể giảm cường độ), cơn đau sẽ giảm dần ;;;s_answer;;;D. Chỉ cho bệnh nhân đi lại sau khi đã trung tiện được.', 'C. Khuyên bệnh nhân tiếp tục đi (có thể giảm cường độ), cơn đau sẽ giảm dần', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1737, 'text', 'Sản khoa', 'Biến chứng thường gặp khi gây tê tủy sống là :', '', 'A. Nhiễm trùng nơi chích.;;;s_answer;;;B. Tụ máu nơi chích. ;;;s_answer;;;C. Nhức đầu ;;;s_answer;;;D. Mờ mắt.', 'C. Nhức đầu', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1738, 'text', 'Sản khoa', 'Biểu hiện lâm sàng của nhức đầu do gây tê tủy sống là:', '', 'A. Nhức vùng trán, xảy ra khi nằm ngữa.;;;s_answer;;;B. Nhức vùng chẩm và sau gáy, xảy ra khi nằm ngữa. ;;;s_answer;;;C. Nhức vùng trán, xảy ra khi ngồi.;;;s_answer;;;D. Nhức vùng chẩm và sau gáy, xảy ra khi ngồi', 'D. Nhức vùng chẩm và sau gáy, xảy ra khi ngồi', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1739, 'text', 'Sản khoa', 'Hậu phẫu ngày thứ 2, tử cung co hồi trên vệ khoảng. . . . cm:', '', 'A. 19;;;s_answer;;;B. 16;;;s_answer;;;C. 13;;;s_answer;;;D. 10', 'C. 13', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1740, 'text', 'Sản khoa', 'Sau khi mổ lấy thai, khuyên sản phụ tập đi sớm nhất vào ngày:', '', 'A. Hậu phẫu 1.;;;s_answer;;;B. Hậu phẫu 2 ;;;s_answer;;;C. Hậu phẫu 3.;;;s_answer;;;D. Hậu phẫu 4.', 'B. Hậu phẫu 2', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1741, 'text', 'Sản khoa', 'Khuyên bà mẹ nên cho con bú ở tư thế:', '', 'A. Nằm.;;;s_answer;;;B. Ngồi dựa lưng vào tường ;;;s_answer;;;C. Ngồi chồm người ra trước. ;;;s_answer;;;D. Đứng.', 'B. Ngồi dựa lưng vào tường', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1742, 'text', 'Sản khoa', 'Sa dây rốn trong trường hợp nào sau đây là nguy hiểm nhất:', '', 'A. Ngôi mông đủ.;;;s_answer;;;B. Ngôi mông thiếu kiểu mông. ;;;s_answer;;;C. Ngôi ngang kèm sa tay.;;;s_answer;;;D. Ngôi chẩm kiểu thế sau', 'D. Ngôi chẩm kiểu thế sau', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1743, 'text', 'Sản khoa', 'Để phát hiện sớm sa dây rốn, NÊN:', '', 'A. Siêu âm Doppler những trường hợp có nguy cơ cao sa dây rốn. ;;;s_answer;;;B. Khám âm đạo ngay sau khi ối vỡ ;;;s_answer;;;C. Khám âm đạo mỗi 2 giờ.;;;s_answer;;;D. Nghe tim thai sau khi ối vỡ.', 'B. Khám âm đạo ngay sau khi ối vỡ', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1744, 'text', 'Sản khoa', 'Nguyên nhân thường gặp nhất trong băng huyết sau sanh là:', '', 'A. Đờ tử cung ;;;s_answer;;;B. Tổn thương đường sinh dục. ;;;s_answer;;;C. Rối loạn đông máu.;;;s_answer;;;D. Sót nhau.', 'A. Đờ tử cung', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1745, 'text', 'Sản khoa', 'Trong băng huyết sau sanh do đờ tử cung, cách dùng thuốc nào sau đây là phù hợp:', '', 'A. Lactate Ringer 500 ml + Methergin 0,2 mg 3ống ® TTM nhanh. ;;;s_answer;;;B. Methergin 0,2 mg 2ống TMC.;;;s_answer;;;C. Methergin 0,2 mg 1ống TB ;;;s_answer;;;D. Methergin 0,2 mg 1ống tiêm vào cơ tử cung.', 'C. Methergin 0,2 mg 1ống TB', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1746, 'text', 'Sản khoa', 'Methergin® (Ergometrine®) được dùng trong trường hợp nào:', '', 'A. Tránh bế sản dịch khi mổ lấy thai chủ động.;;;s_answer;;;B. Điều trị băng huyết sau sanh do đờ tử cung ;;;s_answer;;;C. Rút ngắn thời gian sổ thai.;;;s_answer;;;D. Làm mềm cổ tử cung thuận lợi cho việc khởi phát chuyển dạ.', 'B. Điều trị băng huyết sau sanh do đờ tử cung', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1747, 'text', 'Sản khoa', 'Trong “sanh chỉ huy” liều oxytocin lúc bắt đầu thường là:', '', 'A. 5 giọt/ 1 phút. ;;;s_answer;;;B. 10 giọt/ 1 phút ;;;s_answer;;;C. 15 giọt/ 1 phút. ;;;s_answer;;;D. 20 giọt/ 1 phút.', 'B. 10 giọt/ 1 phút', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1748, 'text', 'Sản khoa', 'Trong “sanh chỉ huy” thường pha:', '', 'A. Glucose 5% 500 ml + 5UI oxytocin ;;;s_answer;;;B. Glucose 5% 300 ml + 5UI oxytocin.;;;s_answer;;;C. Lactate Ringer 500 ml + 5UI oxytocin. ;;;s_answer;;;D. Lactate Ringer 300 ml + 5UI oxytocin', 'A. Glucose 5% 500 ml + 5UI oxytocin', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1749, 'text', 'Sản khoa', 'Trong quá trình sanh chỉ huy, phương tiện theo dõi tốt nhất là:', '', 'A. Ống nghe Pinard.;;;s_answer;;;B. Máy Doppler nghe tim thai. ;;;s_answer;;;C. Monitoring sản khoa ;;;s_answer;;;D. Siêu âm thai.', 'C. Monitoring sản khoa', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1750, 'text', 'Sản khoa', 'Trong sanh chỉ huy cơn co tử cung tốt là khi:', '', 'A. Ít nhất có 2 cơn/ 10 phút, mỗi cơn kéo dài 40 giây. ;;;s_answer;;;B. Ít nhất có 3 cơn/ 10 phút, mỗi cơn kéo dài 40 giây ;;;s_answer;;;C. Ít nhất có 4 cơn/ 10 phút, mỗi cơn kéo dài 40 giây. ;;;s_answer;;;D. Ít nhất có 5 cơn/ 10 phút, mỗi cơn kéo dài 40 giây.', 'B. Ít nhất có 3 cơn/ 10 phút, mỗi cơn kéo dài 40 giây', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1751, 'text', 'Sản khoa', 'Khi đạt cơn co tử cung tốt thì:', '', 'A. Ngưng truyền dịch.;;;s_answer;;;B. Giảm một nữa số giọt đang truyền. ;;;s_answer;;;C. Giữ nguyên số giọt đang truyền ;;;s_answer;;;D. Tăng gấp đôi số giọt đang truyền.', 'C. Giữ nguyên số giọt đang truyền', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1752, 'text', 'Sản khoa', 'Khi khởi phát chuyển dạ cần chú ý đến:', '', 'A. Chỉ số Apgar.;;;s_answer;;;B. Chỉ số Manning. ;;;s_answer;;;C. Chỉ số Bishop ;;;s_answer;;;D. Chỉ số Friedman.', 'C. Chỉ số Bishop', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1753, 'text', 'Sản khoa', 'Mục đích của non stress test (NST) là:', '', 'A. Đánh giá tình trạng dinh dưỡng của thai nhi. ;;;s_answer;;;B. Đánh giá tình trạng sức khỏe của thai nhi ;;;s_answer;;;C. Đánh giá mức độ trưởng thành của thai nhi. ;;;s_answer;;;D. Đánh giá mức độ suy hô hấp của thai nhi.', 'B. Đánh giá tình trạng sức khỏe của thai nhi', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1754, 'text', 'Sản khoa', 'Non stress test dựa trên nguyên tắc:', '', 'A. Nhịp tim thai tăng sau mỗi cử động thai ;;;s_answer;;;B. Nhịp tim thai tăng sau mỗi cơn co tử cung.;;;s_answer;;;C. Nhịp tim thai tăng sau mỗi nhịp tim thai giảm. ;;;s_answer;;;D. Nhịp tim thai tăng sau khi mẹ nghiêng trái.', 'A. Nhịp tim thai tăng sau mỗi cử động thai', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1755, 'text', 'Sản khoa', ' Nói về nhịp giảm trên monitoring sản khoa:', '', 'A. Phân loại nhịp giảm dựa theo mối tương quan giữa nhịp tim thai và cơn co tử cung ;;;s_answer;;;B. Nhịp giảm sớm là nhịp giảm xuất hiện trước mỗi cơn co tử cung.;;;s_answer;;;C. Nhịp giảm sớm biểu hiện suy thai.;;;s_answer;;;D. Nhịp giảm sớm chỉ xuất hiện ở giai đoạn 2 của chuyển dạ.', 'A. Phân loại nhịp giảm dựa theo mối tương quan giữa nhịp tim thai và cơn co tử cung', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1756, 'text', 'Sản khoa', 'Khi nhịp giảm bất định xuất hiện có nghĩa là:', '', 'A. Dây rốn bị chèn ép ;;;s_answer;;;B. Dây rốn quấn cổ;;;s_answer;;;C. Dây rốn ngắn;;;s_answer;;;D. Sa dây rốn', 'A. Dây rốn bị chèn ép', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1757, 'text', 'Sản khoa', 'Gọi là nghiệm pháp bong nhau dương tính khi:', '', 'A. Dùng tay đẩy đáy tử cung xuống thì dây rốn dài ra.;;;s_answer;;;B. Dùng tay đẩy đáy tử cung xuống thì nhau sổ ra.;;;s_answer;;;C. Dùng tay đẩy tử cung ngược lên thì thấy dây rốn đi lên.;;;s_answer;;;D. Dùng tay đẩy tử cung ngược lên thì thấy dây rốn không đi lên', 'D. Dùng tay đẩy tử cung ngược lên thì thấy dây rốn không đi lên', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1758, 'text', 'Sản khoa', 'Sau khi thai đã sổ, bước đầu tiên của “sổ nhau chủ động (xử trí tích cực giai đoạn 3 của chuyển dạ)” là:', '', 'A. Tiêm bắp ngay 10 đơn vị oxytocin ;;;s_answer;;;B. Làm nghiệm pháp bong nhau ngay. ;;;s_answer;;;C. Nắm dây rốn kéo ra.;;;s_answer;;;D. Tiêm bắp 1 ống Methergin.', 'A. Tiêm bắp ngay 10 đơn vị oxytocin', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1759, 'text', 'Sản khoa', 'Bước 2 của “sổ nhau chủ động (xử trí tích cực giai đoạn 3 của chuyển dạ)” là', '', 'A. Ấn đáy tử cung liên tục để nhau sổ ra.;;;s_answer;;;B. Nắm dây rốn kéo bánh nhau ra ;;;s_answer;;;C. Đưa tay vào buồng tử cung để kéo bánh nhau ra. ;;;s_answer;;;D. Xoa đáy tử cung liên tục cho đến khi nhau sổ ra.', 'B. Nắm dây rốn kéo bánh nhau ra', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1760, 'text', 'Sản khoa', 'Vị trí bánh nhau được tách đầu tiên là:', '', 'A. Trung tâm bánh nhau. ;;;s_answer;;;B. Mép bánh nhau ;;;s_answer;;;C. Tách toàn bộ bánh nhau cùng 1 lúc;;;s_answer;;;D. Tất cả đều sai', 'B. Mép bánh nhau', 1, 1, NULL, 1596183428, 1596183428, NULL),
(1761, 'text', 'Sản khoa', 'Thuốc chống shock vagal là:', '', 'A. Seduxen.;;;s_answer;;;B. Atropin ;;;s_answer;;;C. Morphin. ;;;s_answer;;;D. Dolargan.', 'B. Atropin', 1, 1, NULL, 1596183428, 1596183428, NULL),
