(1918, 'text', 'XN', 'Trong trường hợp cần truyền nhanh đơn vị chế phẩm máu và truyền với khối lượng lớn thì thể tích chế phẩm máu cần truyền ở người lớn trên:', '', 'A. 30 ml/kg/giờ;;;s_answer;;;B. 40 ml/kg/giờ;;;s_answer;;;C. 50 ml/kg/giờ;;;s_answer;;;D. 60 ml/kg/giờ', 'C. 50 ml/kg/giờ', 1, 1, NULL, 1596183429, 1596183429, NULL),
(1919, 'text', 'XN', 'Trong trường hợp cần truyền máu nhanh và khối lượng lớn nhiệt độ ủ ấm đoạn dây truyền máu không được vượt quá:', '', 'A. 370C;;;s_answer;;;B. 400C;;;s_answer;;;C. 500C;;;s_answer;;;D. 560C', 'A. 370C', 1, 1, NULL, 1596183429, 1596183429, NULL),
(1920, 'text', 'XN', 'Thời gian từ khi kết thúc việc làm tan đông chế phẩm máu đến kết thúc việc truyền máu cho người bệnh không được quá:', '', 'A. 3.5 giờ;;;s_answer;;;B. 4 giờ;;;s_answer;;;C. 5.5 giờ;;;s_answer;;;D. 6 giờ', 'D. 6 giờ', 1, 1, NULL, 1596183429, 1596183429, NULL),
(1921, 'text', 'XN', 'Chỉ truyền máu nhóm Rh(D) dương cho người nhận mang nhóm Rh(D) âm trong trường hợp đe dọa đến tính mạng người bệnh và có đủ các điều kiện sau:', '', 'A. Người bệnh là nam giới, cân nhắc lợi ích nếu người bệnh là phụ nữ trong độ tuổi sinh đẻ;;;s_answer;;;B. Xét nghiệm hòa hợp miễn dịch sử dụng huyết thanh chống globulin ở nhiệt độ 370C cho kết quả âm tính;;;s_answer;;;C. Có sự đồng ý bằng văn bản trong kết quả hội chẩn giữa người phụ trách hoặc người được ủy quyền của đơn vị phát máu, bác sỹ điều trị và được sự đồng ý của người bệnh hoặc người nhà của người bệnh.;;;s_answer;;;D. Tất cả đều đúng', 'D. Tất cả đều đúng', 1, 1, NULL, 1596183429, 1596183429, NULL),
(1922, 'text', 'XN', '[Chọn câu sai] Thực hiện xét nghiệm hòa hợp miễm dịch khi truyền huyết tươngvà tiểu cầu:', '', 'A. Định nhóm máu bằng phương pháp hồng cầu mẫu cho chế phẩm huyết tương, tiểu cầu;;;s_answer;;;B. Xét nghiệm hòa hợp giữa: Huyết tương của đơn vị chế phẩm máu + Hồng cầu người nhận;;;s_answer;;;C. Môi trường nước muối 0.9%, nhiệt độ phòng;;;s_answer;;;D. Nghiệm pháp coombs gián tiếp', 'D. Nghiệm pháp coombs gián tiếp', 1, 1, NULL, 1596183429, 1596183429, NULL),
(1923, 'text', 'XN', '[Chọn câu sai] Thực hiện xét nghiệm hòa hợp miễm dịch khi truyền khối hồng cầu:', '', 'A. Định nhóm máu bằng phương pháp huyết thanh mẫu cho khối hồng cầu.;;;s_answer;;;B. Môi trường nước muối 0.9%, nhiệt độ phòng: Hồng cầu đơn vị máu + Huyết thanh người nhận;;;s_answer;;;C. Nghiệm pháp coombs trực tiếp;;;s_answer;;;D. Nghiệm pháp coombs gián tiếp', 'C. Nghiệm pháp coombs trực tiếp', 1, 1, NULL, 1596183429, 1596183429, NULL),
(1924, 'text', 'XN', 'Đơn vị Khối hồng cầu đậm đặc (hồng cầu lắng) thể tích…… so với thể tích máu toàn phần ban đầu:', '', 'A. 40% ± 15% ;;;s_answer;;;B. 50% ± 15%;;;s_answer;;;C. 60% ± 15%;;;s_answer;;;D. 70% ± 15%', 'C. 60% ± 15%', 1, 1, NULL, 1596183429, 1596183429, NULL);
